151/**
152 * Run Maven analysis using Java plugin
153 */
154async function runMavenAnalysis(options: MavenPluginOptions): Promise<any> {
155  const outputFile = join(workspaceDataDirectory, 'maven-analysis.json');
156
157  // Check if verbose mode is enabled
158  const isVerbose = options.verbose || process.env.NX_VERBOSE_LOGGING === 'true' || process.argv.includes('--verbose');
159
160  // Skip Java analyzer check during testing to avoid git HEAD issues
161  if (process.env.NODE_ENV !== 'test') {
162    // Check if Java analyzer is available
163    if (!findJavaAnalyzer()) {
164      throw new Error('Maven analyzer not found. Please ensure maven-plugin is compiled.');
165    }
166  }
167
168  if (isVerbose) {
169    console.log(`Running Maven analysis with verbose logging enabled...`);
170    console.log(`Maven executable: ${options.mavenExecutable}`);
171    console.log(`Output file: ${outputFile}`);
172  }
173
174  // Build Maven command arguments
175  // Use the custom nx:analyze goal from our Java Maven plugin
176  const mavenArgs = [
177    'io.quarkus:maven-plugin:999-SNAPSHOT:analyze',
178    `-Dnx.outputFile=${outputFile}`,
179    `-Dnx.verbose=${isVerbose}`
180  ];
181
182  // Always use quiet mode to suppress expected reactor dependency warnings
183  // These warnings are normal in large multi-module projects and don't affect functionality
184  mavenArgs.push('-q');
185
186  if (isVerbose) {
187    console.log(`Executing Maven command: ${options.mavenExecutable} ${mavenArgs.join(' ')}`);
188    console.log(`Working directory: ${workspaceRoot}`);
189  }
190
191  // Run Maven plugin
192  await new Promise<void>((resolve, reject) => {
193    const child = spawn(options.mavenExecutable, mavenArgs, {
194      cwd: workspaceRoot,
195      stdio: 'inherit'
196    });
197
198    // No timeout for very large codebases like Quarkus
199    child.on('close', (code) => {
200      if (isVerbose) {
201        console.log(`Maven process completed with exit code: ${code}`);
202      }
203      if (code === 0) {
204        if (isVerbose) {
205          console.log(`Maven analysis completed successfully`);
206        }
207        resolve();
208      } else {
209        reject(new Error(`Maven process exited with code ${code}`));
210      }
211    });
212
213    child.on('error', (error) => {
214      reject(new Error(`Failed to spawn Maven process: ${error.message}`));
215    });
216  });
217
218  // Read and return JSON result
219  if (!existsSync(outputFile)) {
220    throw new Error(`Output file not found: ${outputFile}`);
221  }
222
223  const jsonContent = readFileSync(outputFile, 'utf8');
224  return JSON.parse(jsonContent);
225}
226
227/**
228 * Find the compiled Java Maven analyzer
229 */
230function findJavaAnalyzer(): string | null {
231  const possiblePaths = [
232    join(__dirname, 'maven-plugin/target/classes'),
233    join(__dirname, 'maven-plugin/target/maven-plugin-999-SNAPSHOT.jar'),
234  ];
235
236  console.log(possiblePaths);
237
238  for (const path of possiblePaths) {
239    if (existsSync(path)) {
240      return path;
241    }
242  }
243
244  return null;
245}
