  1import org.apache.maven.execution.MavenSession;
  2import org.apache.maven.plugin.AbstractMojo;
  3import org.apache.maven.plugin.MojoExecutionException;
  4import org.apache.maven.plugin.MojoFailureException;
  5import org.apache.maven.plugins.annotations.Mojo;
  6import org.apache.maven.plugins.annotations.Parameter;
  7import org.apache.maven.plugins.annotations.ResolutionScope;
  8import org.apache.maven.project.MavenProject;
  9
 10import com.google.gson.Gson;
 11import com.google.gson.GsonBuilder;
 12
 13import model.CreateNodesV2Entry;
 14import model.RawProjectGraphDependency;
 15import model.TargetConfiguration;
 16import model.TargetGroup;
 17import java.io.File;
 18import java.io.FileWriter;
 19import java.io.IOException;
 20import java.util.ArrayList;
 21import java.util.LinkedHashMap;
 22import java.util.LinkedHashSet;
 23import java.util.List;
 24import java.util.Map;
 25import java.util.Set;
 26
 27/**
 28 * Refactored Maven plugin that analyzes Maven projects for Nx integration.
 29 * This version delegates complex logic to specialized service classes.
 30 */
 31@Mojo(name = "analyze", aggregator = true, requiresDependencyResolution = ResolutionScope.NONE)
 32public class NxAnalyzerMojo extends AbstractMojo {
 33    
 34    @Parameter(defaultValue = "${session}", readonly = true, required = true)
 35    private MavenSession session;
 36    
 37    @Parameter(defaultValue = "${reactorProjects}", readonly = true, required = true)
 38    private List<MavenProject> reactorProjects;
 39    
 40    @Parameter(property = "nx.outputFile")
 41    private String outputFile;
 42    
 43    @Parameter(property = "nx.verbose", defaultValue = "false")
 44    private String verboseStr;
 45    
 46    // Services for delegating complex operations
 47    private TargetGenerationService targetGenerationService;
 48    private TargetGroupService targetGroupService;
 49    private TargetDependencyService targetDependencyService;
 50    
 51    @Override
 52    public void execute() throws MojoExecutionException, MojoFailureException {
 53        long startTime = System.currentTimeMillis();
 54        
 55        
 56        try {
 57            initializeServices();
 58            
 59            getLog().info("Starting Nx analysis for " + reactorProjects.size() + " projects");
 60            logBasicInfo();
 61            
 62            // Generate analysis results
 63            Map<String, Object> result = performAnalysis();
 64            
 65            // Write output
 66            String outputPath = determineOutputPath();
 67            writeResult(result, outputPath);
 68            
 69            logCompletion(startTime, outputPath);
 70            
 71        } catch (Exception e) {
 72            throw new MojoExecutionException("Analysis failed: " + e.getMessage(), e);
 73        }
 74    }
 75    
 76    private void initializeServices() {
 77        this.targetGenerationService = new TargetGenerationService(getLog(), isVerbose());
 78        this.targetGroupService = new TargetGroupService();
 79        this.targetDependencyService = new TargetDependencyService(getLog(), isVerbose(), session);
 80    }
 81    
 82    private void logBasicInfo() {
 83        if (isVerbose()) {
 84            getLog().info("Verbose mode enabled");
 85        }
 86        
 87        getLog().info("Root directory: " + session.getExecutionRootDirectory());
 88        if (!reactorProjects.isEmpty()) {
 89            MavenProject first = reactorProjects.get(0);
 90            MavenProject last = reactorProjects.get(reactorProjects.size() - 1);
 91            getLog().info("First project: " + first.getGroupId() + ":" + first.getArtifactId());
 92            getLog().info("Last project: " + last.getGroupId() + ":" + last.getArtifactId());
 93        }
 94    }
 95    
 96    private Map<String, Object> performAnalysis() {
 97        File workspaceRoot = new File(session.getExecutionRootDirectory());
 98        
 99        
100        // Generate targets and groups for each project
101        Map<MavenProject, Map<String, TargetConfiguration>> projectTargets = new LinkedHashMap<>();
102        Map<MavenProject, Map<String, TargetGroup>> projectTargetGroups = new LinkedHashMap<>();
103        
104        for (MavenProject project : reactorProjects) {
105            try {
106                // Calculate goal dependencies first
107                Map<String, List<String>> goalDependencies = calculateGoalDependencies(project, workspaceRoot);
108                
109                // Generate targets using pre-calculated goal dependencies (phase dependencies calculated later)
110                Map<String, TargetConfiguration> targets = targetGenerationService.generateTargets(
111                    project, workspaceRoot, goalDependencies, new LinkedHashMap<>());
112                
113                // Now calculate phase dependencies using the generated targets
114                Map<String, List<String>> phaseDependencies = calculatePhaseDependencies(project, targets);
115                
116                // Update phase targets with calculated dependencies
117                updatePhaseTargetsWithDependencies(targets, phaseDependencies);
118                projectTargets.put(project, targets);
119                
120                // Generate target groups
121                Map<String, TargetGroup> targetGroups = targetGroupService.generateTargetGroups(project, targets, session);
122                projectTargetGroups.put(project, targetGroups);
123                
124                if (isVerbose()) {
125                    getLog().info("Processed " + project.getArtifactId() + 
126                                 ": " + targets.size() + " targets, " + targetGroups.size() + " groups");
127                }
128                
129            } catch (Exception e) {
130                getLog().error("Error processing project " + project.getArtifactId() + ": " + e.getMessage(), e);
131                // Continue with empty targets and groups
132                projectTargets.put(project, new LinkedHashMap<>());
133                projectTargetGroups.put(project, new LinkedHashMap<>());
134            }
135        }
136        
137        // Generate Nx-compatible outputs
138        List<CreateNodesV2Entry> createNodesEntries = CreateNodesResultGenerator.generateCreateNodesV2Results(
139            reactorProjects, workspaceRoot, projectTargets, projectTargetGroups);
140        
141        List<Object[]> createNodesResults = new ArrayList<>();
142        for (CreateNodesV2Entry entry : createNodesEntries) {
143            createNodesResults.add(entry.toArray());
144        }
145        
146        List<RawProjectGraphDependency> createDependencies = CreateDependenciesGenerator.generateCreateDependencies(
147            reactorProjects, workspaceRoot, getLog(), isVerbose());
148        
149        if (isVerbose()) {
150            getLog().info("Generated " + createDependencies.size() + " workspace dependencies");
151        }
152        
153        Map<String, Object> result = new LinkedHashMap<>();
154        result.put("createNodesResults", createNodesResults);
155        result.put("createDependencies", createDependencies);
156        
157        return result;
158    }
159    
160    /**
161     * Calculate goal dependencies for a project
162     */
163    private Map<String, List<String>> calculateGoalDependencies(MavenProject project, File workspaceRoot) {
164        Map<String, List<String>> goalDependencies = new LinkedHashMap<>();
165        
166        if (isVerbose()) {
167            getLog().debug("Calculating goal dependencies for " + project.getArtifactId());
168        }
169        
170        // First pass: collect all potential goal targets
171        Set<String> goalTargets = collectGoalTargets(project);
172        
173        // Calculate dependencies for each goal target
174        for (String targetName : goalTargets) {
175            String goal = MavenUtils.extractGoalFromTargetName(targetName);
176            
177            // Try to find execution phase from plugin configuration
178            String executionPhase = findExecutionPhase(project, targetName);
179            
180            List<String> dependencies = targetDependencyService.calculateGoalDependencies(
181                project, executionPhase, targetName, reactorProjects);
182            goalDependencies.put(targetName, dependencies);
183        }
184        
185        return goalDependencies;
186    }
187    
188    /**
189     * Calculate phase dependencies for a project using generated targets
190     */
191    private Map<String, List<String>> calculatePhaseDependencies(MavenProject project, Map<String, TargetConfiguration> allTargets) {
192        Map<String, List<String>> phaseDependencies = new LinkedHashMap<>();
193        
194        String[] phases = {
195            "clean", "validate", "compile", "test", "package", 
196            "verify", "install", "deploy", "site"
197        };
198        
199        for (String phase : phases) {
200            List<String> dependencies = targetDependencyService.calculatePhaseDependencies(
201                phase, allTargets, project, reactorProjects);
202            phaseDependencies.put(phase, dependencies);
203        }
204        
205        return phaseDependencies;
206    }
207    
208    /**
209     * Update phase targets with calculated dependencies
210     */
211    private void updatePhaseTargetsWithDependencies(Map<String, TargetConfiguration> targets, 
212                                                   Map<String, List<String>> phaseDependencies) {
213        for (Map.Entry<String, List<String>> entry : phaseDependencies.entrySet()) {
214            String phase = entry.getKey();
215            List<String> dependencies = entry.getValue();
216            
217            TargetConfiguration phaseTarget = targets.get(phase);
218            if (phaseTarget != null) {
219                phaseTarget.setDependsOn(dependencies);
220            }
221        }
222    }
223    
224    /**
225     * Collect all potential goal targets from project plugins
226     */
227    private Set<String> collectGoalTargets(MavenProject project) {
228        Set<String> goalTargets = new LinkedHashSet<>();
229        
230        if (isVerbose()) {
231            getLog().debug("Collecting goals for " + project.getArtifactId() + " (" + 
232                          (project.getBuildPlugins() != null ? project.getBuildPlugins().size() : 0) + " plugins)");
233        }
234        
235        if (project.getBuildPlugins() != null) {
236            project.getBuildPlugins().forEach(plugin -> {
237                String artifactId = plugin.getArtifactId();
238                
239                // Add goals from executions
240                if (plugin.getExecutions() != null) {
241                    plugin.getExecutions().forEach(execution -> {
242                        if (execution.getGoals() != null) {
243                            execution.getGoals().forEach(goal -> {
244                                String targetName = MavenUtils.getTargetName(artifactId, goal);
245                                goalTargets.add(targetName);
246                            });
247                        }
248                    });
249                }
250                
251                // Add common goals for well-known plugins
252                addCommonGoals(artifactId, goalTargets);
253            });
254        }
255        
256        if (isVerbose()) {
257            getLog().debug("Found " + goalTargets.size() + " goals for " + project.getArtifactId());
258        }
259        return goalTargets;
260    }
261    
262    /**
263     * Find execution phase for a goal target
264     */
265    private String findExecutionPhase(MavenProject project, String targetName) {
266        String goal = MavenUtils.extractGoalFromTargetName(targetName);
267        
268        if (project.getBuildPlugins() != null) {
269            for (org.apache.maven.model.Plugin plugin : project.getBuildPlugins()) {
270                if (plugin.getExecutions() != null) {
271                    for (org.apache.maven.model.PluginExecution execution : plugin.getExecutions()) {
272                        if (execution.getGoals() != null && execution.getGoals().contains(goal)) {
273                            return execution.getPhase();
274                        }
275                    }
276                }
277            }
278        }
279        
280        return null; // Will trigger phase inference in dependency service
281    }
282    
283    /**
284     * Add common goals for well-known plugins
285     */
286    private void addCommonGoals(String artifactId, Set<String> goalTargets) {
287        if (artifactId.contains("compiler")) {
288            goalTargets.add(MavenUtils.getTargetName(artifactId, "compile"));
289            goalTargets.add(MavenUtils.getTargetName(artifactId, "testCompile"));
290        } else if (artifactId.contains("surefire")) {
291            goalTargets.add(MavenUtils.getTargetName(artifactId, "test"));
292        } else if (artifactId.contains("quarkus")) {
293            goalTargets.add(MavenUtils.getTargetName(artifactId, "dev"));
294            goalTargets.add(MavenUtils.getTargetName(artifactId, "build"));
295        } else if (artifactId.contains("spring-boot")) {
296            goalTargets.add(MavenUtils.getTargetName(artifactId, "run"));
297            goalTargets.add(MavenUtils.getTargetName(artifactId, "repackage"));
298        }
299    }
300    
301    
302    private boolean isVerbose() {
303        String systemProp = System.getProperty("nx.verbose");
304        boolean fromParam = "true".equalsIgnoreCase(verboseStr);
305        boolean fromSystem = "true".equalsIgnoreCase(systemProp);
306        return fromParam || fromSystem;
307    }
308    
309    private String determineOutputPath() {
310        if (outputFile != null && !outputFile.isEmpty()) {
311            return outputFile;
312        }
313        return new File(session.getExecutionRootDirectory(), "maven-analysis.json").getAbsolutePath();
314    }
315    
316    private void writeResult(Map<String, Object> result, String outputPath) throws IOException {
317        Gson gson = new GsonBuilder()
318            .setPrettyPrinting()
319            .serializeNulls()
320            .create();
321            
322        try (FileWriter writer = new FileWriter(outputPath)) {
323            gson.toJson(result, writer);
324        }
325        
326        if (isVerbose()) {
327            long fileSize = new File(outputPath).length();
328            getLog().info("Wrote " + (fileSize / 1024) + "KB analysis results");
329        }
330    }
331    
332    private void logCompletion(long startTime, String outputPath) {
333        long totalTime = System.currentTimeMillis() - startTime;
334        
335        getLog().info("Analysis complete. Results written to: " + outputPath);
336        if (isVerbose()) {
337            double projectsPerSecond = reactorProjects.size() / (totalTime / 1000.0);
338            getLog().info("Performance: Processed " + reactorProjects.size() + " projects in " + 
339                         (totalTime / 1000.0) + "s (" + String.format("%.1f", projectsPerSecond) + " projects/sec)");
340        }
341        getLog().info("SUCCESS: Maven analysis completed successfully");
342    }
343}

