  1import model.TargetConfiguration;
  2import model.TargetMetadata;
  3import org.apache.maven.model.Plugin;
  4import org.apache.maven.model.PluginExecution;
  5import org.apache.maven.plugin.logging.Log;
  6import org.apache.maven.project.MavenProject;
  7
  8import java.io.File;
  9import java.util.ArrayList;
 10import java.util.Arrays;
 11import java.util.LinkedHashMap;
 12import java.util.List;
 13import java.util.Map;
 14import java.util.Set;
 15
 16/**
 17 * Service responsible for generating Nx targets from Maven project configuration.
 18 * Handles both phase targets and plugin goal targets.
 19 */
 20public class TargetGenerationService {
 21    
 22    private final Log log;
 23    private final boolean verbose;
 24
 25    public TargetGenerationService(Log log, boolean verbose) {
 26        this.log = log;
 27        this.verbose = verbose;
 28    }
 29
 30    /**
 31     * Generate all targets for a Maven project
 32     * @param project The Maven project to generate targets for
 33     * @param workspaceRoot The workspace root directory
 34     * @param goalDependencies Pre-calculated goal dependencies
 35     * @param phaseDependencies Pre-calculated phase dependencies
 36     * @return Map of target name to target configuration
 37     * @throws IllegalArgumentException if project is null
 38     */
 39    public Map<String, TargetConfiguration> generateTargets(MavenProject project, File workspaceRoot, 
 40                                                           Map<String, List<String>> goalDependencies,
 41                                                           Map<String, List<String>> phaseDependencies) {
 42        if (project == null) {
 43            throw new IllegalArgumentException("Project cannot be null");
 44        }
 45        
 46        Map<String, TargetConfiguration> targets = new LinkedHashMap<>();
 47        
 48        try {
 49            // Generate plugin goal targets first
 50            targets.putAll(generatePluginGoalTargets(project, workspaceRoot, goalDependencies));
 51            
 52            // Generate Maven lifecycle phase targets
 53            targets.putAll(generatePhaseTargets(project, workspaceRoot, targets, phaseDependencies));
 54            
 55        } catch (Exception e) {
 56            if (log != null) {
 57                log.warn("Error generating targets for project " + project.getArtifactId() + ": " + e.getMessage(), e);
 58            }
 59            // Return empty targets rather than failing completely
 60        }
 61        
 62        return targets;
 63    }
 64
 65    /**
 66     * Generate targets for Maven lifecycle phases
 67     */
 68    public Map<String, TargetConfiguration> generatePhaseTargets(MavenProject project, File workspaceRoot, 
 69                                                                 Map<String, TargetConfiguration> allTargets, 
 70                                                                 Map<String, List<String>> phaseDependencies) {
 71        Map<String, TargetConfiguration> phaseTargets = new LinkedHashMap<>();
 72        
 73        String[] phases = {
 74            "clean", "validate", "compile", "test", "package", 
 75            "verify", "install", "deploy", "site"
 76        };
 77        
 78        for (String phase : phases) {
 79            TargetConfiguration target = new TargetConfiguration("nx:noop");
 80            
 81            // Configure as no-op
 82            target.setOptions(new LinkedHashMap<>());
 83            target.setInputs(new ArrayList<>());
 84            target.setOutputs(new ArrayList<>());
 85            
 86            // Phase dependencies - use pre-calculated dependencies
 87            List<String> dependsOn = phaseDependencies.getOrDefault(phase, new ArrayList<>());
 88            
 89            target.setDependsOn(dependsOn);
 90            
 91            // Add metadata
 92            TargetMetadata metadata = new TargetMetadata("phase", "Maven lifecycle phase: " + phase);
 93            metadata.setPhase(phase);
 94            metadata.setPlugin("org.apache.maven:maven-core");
 95            metadata.setTechnologies(Arrays.asList("maven"));
 96            target.setMetadata(metadata);
 97            
 98            phaseTargets.put(phase, target);
 99        }
100        
101        return phaseTargets;
102    }
103
104    /**
105     * Generate targets for plugin goals
106     */
107    public Map<String, TargetConfiguration> generatePluginGoalTargets(MavenProject project, File workspaceRoot, 
108                                                                      Map<String, List<String>> goalDependencies) {
109        Map<String, TargetConfiguration> goalTargets = new LinkedHashMap<>();
110        
111        if (verbose) {
112            log.debug("Generating plugin goal targets for project: " + project.getArtifactId());
113        }
114        
115        final String projectRootToken = "{projectRoot}";
116        final String actualProjectPath;
117        
118        if (workspaceRoot != null) {
119            String relativePath = NxPathUtils.getRelativePath(workspaceRoot, project.getBasedir());
120            actualProjectPath = relativePath.isEmpty() ? "." : relativePath;
121        } else {
122            actualProjectPath = "{projectRoot}";
123        }
124        
125        if (project.getBuildPlugins() != null) {
126            project.getBuildPlugins().forEach(plugin -> {
127                // Process actual executions from effective POM
128                if (plugin.getExecutions() != null) {
129                    plugin.getExecutions().forEach(execution -> {
130                        if (execution.getGoals() != null) {
131                            execution.getGoals().forEach(goal -> {
132                                String targetName = MavenUtils.getTargetName(plugin.getArtifactId(), goal);
133                                
134                                if (!goalTargets.containsKey(targetName)) {
135                                    TargetConfiguration target = createGoalTarget(plugin, goal, execution, projectRootToken, actualProjectPath, goalDependencies.getOrDefault(targetName, new ArrayList<>()));
136                                    goalTargets.put(targetName, target);
137                                }
138                            });
139                        }
140                    });
141                }
142                
143                // Add common goals for well-known plugins
144                addCommonGoalsForPlugin(plugin, goalTargets, projectRootToken, actualProjectPath, goalDependencies);
145            });
146        }
147        
148        return goalTargets;
149    }
150
151    /**
152     * Create a target configuration for a specific Maven goal
153     */
154    public TargetConfiguration createGoalTarget(Plugin plugin, String goal, PluginExecution execution, 
155                                               String projectRootToken, String actualProjectPath, 
156                                               List<String> dependencies) {
157        String pluginKey = plugin.getGroupId() + ":" + plugin.getArtifactId();
158        
159        TargetConfiguration target = new TargetConfiguration("nx:run-commands");
160        
161        Map<String, Object> options = new LinkedHashMap<>();
162        options.put("command", "mvn " + pluginKey + ":" + goal);
163        options.put("cwd", actualProjectPath);
164        target.setOptions(options);
165        
166        // Smart inputs/outputs based on goal
167        List<String> inputs = new ArrayList<>();
168        inputs.add(projectRootToken + "/pom.xml");
169        if (isSourceProcessingGoal(goal)) {
170            inputs.add(projectRootToken + "/src/**/*");
171        }
172        target.setInputs(inputs);
173        
174        List<String> outputs = getGoalOutputs(goal, projectRootToken);
175        target.setOutputs(outputs);
176        
177        // Use pre-calculated dependencies
178        target.setDependsOn(dependencies);
179        
180        // Metadata
181        TargetMetadata metadata = new TargetMetadata("goal", generateGoalDescription(plugin.getArtifactId(), goal));
182        metadata.setPlugin(pluginKey);
183        metadata.setGoal(goal);
184        metadata.setExecutionId(execution.getId());
185        String executionPhase = execution.getPhase();
186        if (executionPhase != null && !executionPhase.isEmpty() && !executionPhase.startsWith("${")) {
187            metadata.setPhase(executionPhase);
188        } else {
189            metadata.setPhase(inferPhaseFromGoal(goal));
190        }
191        metadata.setTechnologies(Arrays.asList("maven"));
192        target.setMetadata(metadata);
193        
194        return target;
195    }
196
197    private void addCommonGoalsForPlugin(Plugin plugin, Map<String, TargetConfiguration> goalTargets, 
198                                        String projectRootToken, String actualProjectPath, 
199                                        Map<String, List<String>> goalDependencies) {
200        String artifactId = plugin.getArtifactId();
201        List<String> commonGoals = new ArrayList<>();
202        
203        if (artifactId.contains("compiler")) {
204            commonGoals.addAll(Arrays.asList("compile", "testCompile"));
205        } else if (artifactId.contains("surefire")) {
206            commonGoals.add("test");
207        } else if (artifactId.contains("quarkus")) {
208            commonGoals.addAll(Arrays.asList("dev", "build"));
209        } else if (artifactId.contains("spring-boot")) {
210            commonGoals.addAll(Arrays.asList("run", "repackage"));
211        }
212        
213        for (String goal : commonGoals) {
214            String targetName = MavenUtils.getTargetName(artifactId, goal);
215            if (!goalTargets.containsKey(targetName)) {
216                TargetConfiguration target = createSimpleGoalTarget(plugin, goal, projectRootToken, actualProjectPath, goalDependencies.getOrDefault(targetName, new ArrayList<>()));
217                goalTargets.put(targetName, target);
218            }
219        }
220    }
221
222    private TargetConfiguration createSimpleGoalTarget(Plugin plugin, String goal, 
223                                                      String projectRootToken, String actualProjectPath,
224                                                      List<String> dependencies) {
225        String pluginKey = plugin.getGroupId() + ":" + plugin.getArtifactId();
226        
227        TargetConfiguration target = new TargetConfiguration("nx:run-commands");
228        
229        Map<String, Object> options = new LinkedHashMap<>();
230        options.put("command", "mvn " + pluginKey + ":" + goal);
231        options.put("cwd", actualProjectPath);
232        target.setOptions(options);
233        
234        List<String> inputs = new ArrayList<>();
235        inputs.add(projectRootToken + "/pom.xml");
236        if (isSourceProcessingGoal(goal)) {
237            inputs.add(projectRootToken + "/src/**/*");
238        }
239        target.setInputs(inputs);
240        
241        List<String> outputs = getGoalOutputs(goal, projectRootToken);
242        target.setOutputs(outputs);
243        
244        // Use pre-calculated dependencies
245        target.setDependsOn(dependencies);
246        
247        TargetMetadata metadata = new TargetMetadata("goal", generateGoalDescription(plugin.getArtifactId(), goal));
248        metadata.setPlugin(pluginKey);
249        metadata.setGoal(goal);
250        metadata.setPhase(inferPhaseFromGoal(goal));
251        metadata.setTechnologies(Arrays.asList("maven"));
252        target.setMetadata(metadata);
253        
254        return target;
255    }
256
257    // Helper methods
258
259    private boolean isSourceProcessingGoal(String goal) {
260        return goal.contains("compile") || goal.contains("test") || goal.contains("build") || 
261               goal.equals("dev") || goal.equals("run");
262    }
263
264    private List<String> getGoalOutputs(String goal, String projectRootToken) {
265        List<String> outputs = new ArrayList<>();
266        if (goal.contains("compile") || goal.contains("build") || goal.contains("jar") || goal.contains("war")) {
267            outputs.add(projectRootToken + "/target/**/*");
268        } else if (goal.contains("test")) {
269            outputs.add(projectRootToken + "/target/surefire-reports/**/*");
270            outputs.add(projectRootToken + "/target/failsafe-reports/**/*");
271        } else if (goal.contains("site") || goal.contains("javadoc")) {
272            outputs.add(projectRootToken + "/target/site/**/*");
273        }
274        return outputs;
275    }
276
277
278    private String generateGoalDescription(String artifactId, String goal) {
279        String pluginName = artifactId.replace("-maven-plugin", "").replace("-plugin", "");
280        
281        switch (goal) {
282            case "compile": return "Compile main sources";
283            case "testCompile": return "Compile test sources";
284            case "test": return "Run tests";
285            case "integration-test": return "Run integration tests";
286            case "dev": return "Start development mode";
287            case "run": return "Run application";
288            case "build": return "Build application";
289            case "jar": return "Create JAR";
290            case "war": return "Create WAR";
291            case "site": return "Generate site documentation";
292            case "javadoc": return "Generate Javadoc";
293            case "enforce": return "Enforce build rules";
294            case "create": return "Create build metadata";
295            default: return pluginName + " " + goal;
296        }
297    }
