300    private String inferPhaseFromGoal(String goal) {
301        if (goal == null || goal.isEmpty()) {
302            return null;
303        }
304        
305        if (goal.equals("compile") || goal.equals("testCompile")) {
306            return "compile";
307        } else if (goal.equals("test") || goal.contains("test")) {
308            return "test";
309        } else if (goal.equals("jar") || goal.equals("war") || goal.equals("build") || goal.equals("repackage")) {
310            return "package";
311        } else if (goal.equals("dev") || goal.equals("run")) {
312            return "compile";
313        } else if (goal.contains("site") || goal.contains("javadoc")) {
314            return "site";
315        } else {
316            return "compile";
