  1import model.TargetConfiguration;
  2import model.TargetMetadata;
  3import org.apache.maven.execution.MavenSession;
  4import org.apache.maven.model.Plugin;
  5import org.apache.maven.model.PluginExecution;
  6import org.apache.maven.plugin.logging.Log;
  7import org.apache.maven.project.MavenProject;
  8
  9import java.util.LinkedHashSet;
 10
 11import java.io.File;
 12import java.util.ArrayList;
 13import java.util.Arrays;
 14import java.util.LinkedHashMap;
 15import java.util.List;
 16import java.util.Map;
 17import java.util.Set;
 18
 19/**
 20 * Service responsible for generating Nx targets from Maven project configuration.
 21 * Handles both phase targets and plugin goal targets.
 22 */
 23public class TargetGenerationService {
 24
 25    private final Log log;
 26    private final boolean verbose;
 27    private final MavenSession session;
 28    private final ExecutionPlanAnalysisService executionPlanAnalysisService;
 29
 30    public TargetGenerationService(Log log, boolean verbose, MavenSession session, ExecutionPlanAnalysisService executionPlanAnalysisService) {
 31        this.log = log;
 32        this.verbose = verbose;
 33        this.session = session;
 34        this.executionPlanAnalysisService = executionPlanAnalysisService;
 35    }
 36
 37    /**
 38     * Generate all targets for a Maven project
 39     * @param project The Maven project to generate targets for
 40     * @param workspaceRoot The workspace root directory
 41     * @param goalDependencies Pre-calculated goal dependencies
 42     * @param phaseDependencies Pre-calculated phase dependencies
 43     * @return Map of target name to target configuration
 44     * @throws IllegalArgumentException if project is null
 45     */
 46    public Map<String, TargetConfiguration> generateTargets(MavenProject project, File workspaceRoot,
 47                                                           Map<String, List<String>> goalDependencies,
 48                                                           Map<String, List<String>> phaseDependencies) {
 49        if (project == null) {
 50            throw new IllegalArgumentException("Project cannot be null");
 51        }
 52
 53        Map<String, TargetConfiguration> targets = new LinkedHashMap<>();
 54
 55        try {
 56            // Generate plugin goal targets first
 57            targets.putAll(generatePluginGoalTargets(project, workspaceRoot, goalDependencies));
 58
 59            // Generate Maven lifecycle phase targets
 60            targets.putAll(generatePhaseTargets(project, workspaceRoot, targets, phaseDependencies));
 61
 62        } catch (Exception e) {
 63            if (log != null) {
 64                log.warn("Error generating targets for project " + project.getArtifactId() + ": " + e.getMessage(), e);
 65            }
 66            // Return empty targets rather than failing completely
 67        }
 68
 69        return targets;
 70    }
 71
 72    /**
 73     * Generate targets for Maven lifecycle phases
 74     */
 75    public Map<String, TargetConfiguration> generatePhaseTargets(MavenProject project, File workspaceRoot,
 76                                                                 Map<String, TargetConfiguration> allTargets,
 77                                                                 Map<String, List<String>> phaseDependencies) {
 78        Map<String, TargetConfiguration> phaseTargets = new LinkedHashMap<>();
 79
 80        // Get all phases from all 3 Maven lifecycles (default, clean, site)
 81        Set<String> applicablePhases = executionPlanAnalysisService.getAllLifecyclePhases();
 82
 83        for (String phase : applicablePhases) {
 84            TargetConfiguration target = new TargetConfiguration("nx:noop");
 85
 86            // Configure as no-op
 87            target.setOptions(new LinkedHashMap<>());
 88            target.setInputs(new ArrayList<>());
 89            target.setOutputs(new ArrayList<>());
 90
 91            // Phase dependencies - use pre-calculated dependencies
 92            List<String> dependsOn = phaseDependencies.getOrDefault(phase, new ArrayList<>());
 93
 94            target.setDependsOn(dependsOn);
 95
 96            // Add metadata
 97            TargetMetadata metadata = new TargetMetadata("phase", "Maven lifecycle phase: " + phase);
 98            metadata.setPhase(phase);
 99            metadata.setPlugin("org.apache.maven:maven-core");
100            metadata.setTechnologies(Arrays.asList("maven"));
101            target.setMetadata(metadata);
102
103            phaseTargets.put(phase, target);
104        }
105
106        return phaseTargets;
107    }
108
109    /**
110     * Generate targets for plugin goals
111     */
112    public Map<String, TargetConfiguration> generatePluginGoalTargets(MavenProject project, File workspaceRoot,
113                                                                      Map<String, List<String>> goalDependencies) {
114        Map<String, TargetConfiguration> goalTargets = new LinkedHashMap<>();
115
116        if (verbose) {
117            log.debug("Generating plugin goal targets for project: " + project.getArtifactId());
118        }
119
120        final String projectRootToken = "{projectRoot}";
121        final String actualProjectPath;
122
123        if (workspaceRoot != null) {
124            String relativePath = NxPathUtils.getRelativePath(workspaceRoot, project.getBasedir());
125            actualProjectPath = relativePath.isEmpty() ? "." : relativePath;
126        } else {
127            actualProjectPath = "{projectRoot}";
128        }
129
130        if (project.getBuildPlugins() != null) {
131            project.getBuildPlugins().forEach(plugin -> {
132                // Process actual executions from effective POM
133                if (plugin.getExecutions() != null) {
134                    plugin.getExecutions().forEach(execution -> {
135                        if (execution.getGoals() != null) {
136                            execution.getGoals().forEach(goal -> {
137                                String targetName = ExecutionPlanAnalysisService.getTargetName(plugin.getArtifactId(), goal);
138
139                                if (!goalTargets.containsKey(targetName)) {
140                                    TargetConfiguration target = createGoalTarget(plugin, goal, execution, projectRootToken, actualProjectPath, goalDependencies.getOrDefault(targetName, new ArrayList<>()), project);
141                                    goalTargets.put(targetName, target);
142                                }
143                            });
144                        }
145                    });
146                }
147
148                // Add common goals for well-known plugins
149                addCommonGoalsForPlugin(plugin, goalTargets, projectRootToken, actualProjectPath, goalDependencies, project);
150            });
151        }
152
153        return goalTargets;
154    }
155
156    /**
157     * Create a target configuration for a specific Maven goal
158     */
159    public TargetConfiguration createGoalTarget(Plugin plugin, String goal, PluginExecution execution,
160                                               String projectRootToken, String actualProjectPath,
161                                               List<String> dependencies, MavenProject project) {
162        String pluginKey = plugin.getGroupId() + ":" + plugin.getArtifactId();
163
164        TargetConfiguration target = new TargetConfiguration("nx:run-commands");
165
166        Map<String, Object> options = new LinkedHashMap<>();
167        options.put("command", "mvn " + pluginKey + ":" + goal);
168        options.put("cwd", actualProjectPath);
169        target.setOptions(options);
170
171        // Smart inputs/outputs based on goal
172        List<String> inputs = new ArrayList<>();
173        inputs.add(projectRootToken + "/pom.xml");
174        if (isSourceProcessingGoal(goal)) {
175            inputs.add(projectRootToken + "/src/**/*");
176        }
177        target.setInputs(inputs);
178
179        List<String> outputs = executionPlanAnalysisService.getGoalOutputs(goal, projectRootToken, project);
180        target.setOutputs(outputs);
181
182        // Use pre-calculated dependencies
183        target.setDependsOn(dependencies);
184
185        // Metadata
186        TargetMetadata metadata = new TargetMetadata("goal", generateGoalDescription(plugin.getArtifactId(), goal));
187        metadata.setPlugin(pluginKey);
188        metadata.setGoal(goal);
189        metadata.setExecutionId(execution.getId());
190        String executionPhase = execution.getPhase();
191        if (executionPhase != null && !executionPhase.isEmpty() && !executionPhase.startsWith("${")) {
192            metadata.setPhase(executionPhase);
193        } else {
194            metadata.setPhase(executionPlanAnalysisService.findPhaseForGoal(project, goal));
195        }
196        metadata.setTechnologies(Arrays.asList("maven"));
197        target.setMetadata(metadata);
198
199        return target;
200    }
201
202    private void addCommonGoalsForPlugin(Plugin plugin, Map<String, TargetConfiguration> goalTargets,
203                                        String projectRootToken, String actualProjectPath,
204                                        Map<String, List<String>> goalDependencies, MavenProject project) {
205        String artifactId = plugin.getArtifactId();
206        List<String> commonGoals = ExecutionPlanAnalysisService.getCommonGoalsForPlugin(artifactId);
207
208        for (String goal : commonGoals) {
209            String targetName = ExecutionPlanAnalysisService.getTargetName(artifactId, goal);
210            if (!goalTargets.containsKey(targetName)) {
211                TargetConfiguration target = createSimpleGoalTarget(plugin, goal, projectRootToken, actualProjectPath, goalDependencies.getOrDefault(targetName, new ArrayList<>()), project);
212                goalTargets.put(targetName, target);
213            }
214        }
215    }
216
217    private TargetConfiguration createSimpleGoalTarget(Plugin plugin, String goal,
218                                                      String projectRootToken, String actualProjectPath,
219                                                      List<String> dependencies, MavenProject project) {
220        String pluginKey = plugin.getGroupId() + ":" + plugin.getArtifactId();
221
222        TargetConfiguration target = new TargetConfiguration("nx:run-commands");
223
224        Map<String, Object> options = new LinkedHashMap<>();
225        options.put("command", "mvn " + pluginKey + ":" + goal);
226        options.put("cwd", actualProjectPath);
227        target.setOptions(options);
228
229        List<String> inputs = new ArrayList<>();
230        inputs.add(projectRootToken + "/pom.xml");
231        if (isSourceProcessingGoal(goal)) {
232            inputs.add(projectRootToken + "/src/**/*");
233        }
234        target.setInputs(inputs);
235
236        List<String> outputs = executionPlanAnalysisService.getGoalOutputs(goal, projectRootToken, project);
237        target.setOutputs(outputs);
238
239        // Use pre-calculated dependencies
240        target.setDependsOn(dependencies);
241
242        TargetMetadata metadata = new TargetMetadata("goal", generateGoalDescription(plugin.getArtifactId(), goal));
243        metadata.setPlugin(pluginKey);
244        metadata.setGoal(goal);
245        metadata.setPhase(executionPlanAnalysisService.findPhaseForGoal(project, goal));
246        metadata.setTechnologies(Arrays.asList("maven"));
247        target.setMetadata(metadata);
248
249        return target;
250    }
251
252    // Helper methods
253
254    private boolean isSourceProcessingGoal(String goal) {
255        return goal.contains("compile") || goal.contains("test") || goal.contains("build") ||
256               goal.equals("dev") || goal.equals("run");
257    }
258
259
260
261
262
263    private String generateGoalDescription(String artifactId, String goal) {
264        String pluginName = ExecutionPlanAnalysisService.normalizePluginName(artifactId);
265
266        return switch (goal) {
267            case "compile" -> "Compile main sources";
268            case "testCompile" -> "Compile test sources";
269            case "test" -> "Run tests";
270            case "integration-test" -> "Run integration tests";
271            case "dev" -> "Start development mode";
272            case "run" -> "Run application";
273            case "build" -> "Build application";
274            case "jar" -> "Create JAR";
275            case "war" -> "Create WAR";
276            case "site" -> "Generate site documentation";
277            case "javadoc" -> "Generate Javadoc";
278            case "enforce" -> "Enforce build rules";
279            case "create" -> "Create build metadata";
280            default -> pluginName + " " + goal;
281        };
282    }
283}
