  1import org.apache.maven.execution.MavenSession;
  2import org.apache.maven.lifecycle.LifecycleExecutor;
  3import org.apache.maven.lifecycle.MavenExecutionPlan;
  4import org.apache.maven.lifecycle.DefaultLifecycles;
  5import org.apache.maven.plugin.MojoExecution;
  6import org.apache.maven.plugin.logging.Log;
  7import org.apache.maven.project.MavenProject;
  8
  9import java.util.*;
 10import java.util.concurrent.ConcurrentHashMap;
 11import java.util.stream.Collectors;
 12
 13/**
 14 * Service that analyzes Maven execution plans once and caches results for efficient querying.
 15 * This avoids recalculating execution plans multiple times for the same project.
 16 */
 17public class ExecutionPlanAnalysisService {
 18    
 19    private final Log log;
 20    private final boolean verbose;
 21    private final LifecycleExecutor lifecycleExecutor;
 22    private final MavenSession session;
 23    private final DefaultLifecycles defaultLifecycles;
 24    
 25    // Cache to store analysis results per project
 26    private final Map<String, ProjectExecutionAnalysis> analysisCache = new ConcurrentHashMap<>();
 27    
 28    public ExecutionPlanAnalysisService(Log log, boolean verbose, LifecycleExecutor lifecycleExecutor, MavenSession session, DefaultLifecycles defaultLifecycles) {
 29        this.log = log;
 30        this.verbose = verbose;
 31        this.lifecycleExecutor = lifecycleExecutor;
 32        this.session = session;
 33        this.defaultLifecycles = defaultLifecycles;
 34    }
 35    
 36    /**
 37     * Get or compute execution analysis for a project
 38     */
 39    public ProjectExecutionAnalysis getAnalysis(MavenProject project) {
 40        String projectKey = MavenUtils.formatProjectKey(project);
 41        return analysisCache.computeIfAbsent(projectKey, key -> analyzeProject(project));
 42    }
 43    
 44    /**
 45     * Find the phase for a specific goal
 46     */
 47    public String findPhaseForGoal(MavenProject project, String goal) {
 48        if (project == null || goal == null || goal.isEmpty()) {
 49            return null;
 50        }
 51        ProjectExecutionAnalysis analysis = getAnalysis(project);
 52        return analysis.getPhaseForGoal(goal);
 53    }
 54    
 55    
 56    /**
 57     * Get all goals for a specific phase
 58     */
 59    public List<String> getGoalsForPhase(MavenProject project, String phase) {
 60        if (project == null || phase == null) {
 61            return new ArrayList<>();
 62        }
 63        ProjectExecutionAnalysis analysis = getAnalysis(project);
 64        return analysis.getGoalsForPhase(phase);
 65    }
 66    
 67    /**
 68     * Get all lifecycle phases from all lifecycles (default, clean, site)
 69     */
 70    public Set<String> getAllLifecyclePhases() {
 71        Set<String> allPhases = new LinkedHashSet<>();
 72        allPhases.addAll(getLifecyclePhases("default"));
 73        allPhases.addAll(getLifecyclePhases("clean"));
 74        allPhases.addAll(getLifecyclePhases("site"));
 75        return allPhases;
 76    }
 77    
 78    /**
 79     * Get default lifecycle phases (validate, compile, test, package, verify, install, deploy)
 80     */
 81    public List<String> getDefaultLifecyclePhases() {
 82        return getLifecyclePhases("default");
 83    }
 84    
 85    /**
 86     * Get clean lifecycle phases (pre-clean, clean, post-clean)
 87     */
 88    public List<String> getCleanLifecyclePhases() {
 89        return getLifecyclePhases("clean");
 90    }
 91    
 92    /**
 93     * Get site lifecycle phases (pre-site, site, post-site, site-deploy)
 94     */
 95    public List<String> getSiteLifecyclePhases() {
 96        return getLifecyclePhases("site");
 97    }
 98    
 99    /**
100     * Get the lifecycle that contains the specified phase
101     */
102    public org.apache.maven.lifecycle.Lifecycle getLifecycleForPhase(String phase) {
103        if (defaultLifecycles == null || phase == null) {
104            return null;
105        }
106        
107        try {
108            return defaultLifecycles.getPhaseToLifecycleMap().get(phase);
109        } catch (Exception e) {
110            if (verbose) {
111                log.warn("Could not get lifecycle for phase '" + phase + "': " + e.getMessage());
112            }
113            return null;
114        }
115    }
116    
117    
118    /**
119     * Get lifecycle phases for a specific lifecycle ID
120     */
121    private List<String> getLifecyclePhases(String lifecycleId) {
122        if (defaultLifecycles == null || lifecycleId == null) {
123            return new ArrayList<>();
124        }
125
126        try {
127            for (org.apache.maven.lifecycle.Lifecycle lifecycle : defaultLifecycles.getLifeCycles()) {
128                if (lifecycleId.equals(lifecycle.getId()) && lifecycle.getPhases() != null) {
129                    return new ArrayList<>(lifecycle.getPhases());
130                }
131            }
132            return new ArrayList<>();
133
134        } catch (Exception e) {
135            if (verbose) {
136                log.warn("Could not access Maven lifecycle '" + lifecycleId + "': " + e.getMessage());
137            }
138            return new ArrayList<>();
139        }
140    }
141    
142    /**
143     * Analyze a project's execution plans and cache the results
144     */
145    private ProjectExecutionAnalysis analyzeProject(MavenProject project) {
146        if (verbose) {
147            log.debug("Analyzing execution plans for project: " + project.getArtifactId());
148        }
149        
150        ProjectExecutionAnalysis analysis = new ProjectExecutionAnalysis();
151        
152        // Use actual lifecycle phases from Maven's lifecycle definitions
153        Set<String> lifecyclePhases = getAllLifecyclePhases();
154        
155        for (String phase : lifecyclePhases) {
156            try {
157                if (lifecycleExecutor != null && session != null) {
158                    MavenExecutionPlan executionPlan = lifecycleExecutor.calculateExecutionPlan(session, phase);
159                    analysis.addExecutionPlan(phase, executionPlan);
160                    
161                    if (verbose) {
