 30/**
 31 * Refactored Maven plugin that analyzes Maven projects for Nx integration.
 32 * This version delegates complex logic to specialized service classes.
 33 */
 34@Mojo(name = "analyze", aggregator = true, requiresDependencyResolution = ResolutionScope.NONE)
 35public class NxAnalyzerMojo extends AbstractMojo {
 36    
 37    @Parameter(defaultValue = "${session}", readonly = true, required = true)
 38    private MavenSession session;
 39    
 40    @Parameter(defaultValue = "${reactorProjects}", readonly = true, required = true)
 41    private List<MavenProject> reactorProjects;
 42    
 43    @Parameter(property = "nx.outputFile")
 44    private String outputFile;
 45    
 46    @Parameter(property = "nx.verbose", defaultValue = "false")
 47    private String verboseStr;
 48    
 49    @Component
 50    private LifecycleExecutor lifecycleExecutor;
 51    
 52    @Component
 53    private DefaultLifecycles defaultLifecycles;
 54    
 55    // Services for delegating complex operations
 56    private ExecutionPlanAnalysisService executionPlanAnalysisService;
 57    private TargetGenerationService targetGenerationService;
 58    private TargetGroupService targetGroupService;
 59    private TargetDependencyService targetDependencyService;
 60    
 61    @Override
 62    public void execute() throws MojoExecutionException, MojoFailureException {
 63        long startTime = System.currentTimeMillis();
 64        
 65        
 66        try {
 67            initializeServices();
 68            
 69            getLog().info("Starting Nx analysis for " + reactorProjects.size() + " projects");
 70            logBasicInfo();
 71            
 72            // Generate analysis results
 73            Map<String, Object> result = performAnalysis();
 74            
 75            // Write output
 76            String outputPath = determineOutputPath();
 77            writeResult(result, outputPath);
 78            
 79            logCompletion(startTime, outputPath);
 80            
 81        } catch (Exception e) {
 82            throw new MojoExecutionException("Analysis failed: " + e.getMessage(), e);
 83        }
 84    }
 85    
 86    private void initializeServices() {
 87        this.executionPlanAnalysisService = new ExecutionPlanAnalysisService(getLog(), isVerbose(), lifecycleExecutor, session, defaultLifecycles);
 88        this.targetGenerationService = new TargetGenerationService(getLog(), isVerbose(), session, executionPlanAnalysisService);
 89        this.targetGroupService = new TargetGroupService(executionPlanAnalysisService);
 90        this.targetDependencyService = new TargetDependencyService(getLog(), isVerbose(), executionPlanAnalysisService);
 91    }
 92    
 93    private void logBasicInfo() {
 94        if (isVerbose()) {
 95            getLog().info("Verbose mode enabled");
 96        }
 97        
 98        getLog().info("Root directory: " + session.getExecutionRootDirectory());
 99        if (!reactorProjects.isEmpty()) {
100            MavenProject first = reactorProjects.get(0);
101            MavenProject last = reactorProjects.get(reactorProjects.size() - 1);
102            getLog().info("First project: " + first.getGroupId() + ":" + first.getArtifactId());
103            getLog().info("Last project: " + last.getGroupId() + ":" + last.getArtifactId());
104        }
105    }
106    
107    private Map<String, Object> performAnalysis() {
108        File workspaceRoot = new File(session.getExecutionRootDirectory());
109        
110        
111        // Generate targets and groups for each project
112        Map<MavenProject, Map<String, TargetConfiguration>> projectTargets = new LinkedHashMap<>();
113        Map<MavenProject, Map<String, TargetGroup>> projectTargetGroups = new LinkedHashMap<>();
114        
115        for (MavenProject project : reactorProjects) {
116            try {
117                // Calculate goal dependencies first
118                Map<String, List<String>> goalDependencies = calculateGoalDependencies(project, workspaceRoot);
119                
120                // Generate targets using pre-calculated goal dependencies (phase dependencies calculated later)
121                Map<String, TargetConfiguration> targets = targetGenerationService.generateTargets(
122                    project, workspaceRoot, goalDependencies, new LinkedHashMap<>());
123                
124                // Now calculate phase dependencies using the generated targets
125                Map<String, List<String>> phaseDependencies = calculatePhaseDependencies(project, targets);
126                
127                // Update phase targets with calculated dependencies
128                updatePhaseTargetsWithDependencies(targets, phaseDependencies);
129                projectTargets.put(project, targets);
130                
131                // Generate target groups
132                Map<String, TargetGroup> targetGroups = targetGroupService.generateTargetGroups(project, targets, session);
133                projectTargetGroups.put(project, targetGroups);
134                
135                if (isVerbose()) {
136                    getLog().info("Processed " + project.getArtifactId() + 
137                                 ": " + targets.size() + " targets, " + targetGroups.size() + " groups");
138                }
139                
140            } catch (Exception e) {
141                getLog().error("Error processing project " + project.getArtifactId() + ": " + e.getMessage(), e);
142                // Continue with empty targets and groups
143                projectTargets.put(project, new LinkedHashMap<>());
144                projectTargetGroups.put(project, new LinkedHashMap<>());
145            }
146        }
147        
148        // Generate Nx-compatible outputs
149        List<CreateNodesV2Entry> createNodesEntries = CreateNodesResultGenerator.generateCreateNodesV2Results(
150            reactorProjects, workspaceRoot, projectTargets, projectTargetGroups);
151        
152        List<Object[]> createNodesResults = new ArrayList<>();
153        for (CreateNodesV2Entry entry : createNodesEntries) {
154            createNodesResults.add(entry.toArray());
155        }
156        
157        List<RawProjectGraphDependency> createDependencies = CreateDependenciesGenerator.generateCreateDependencies(
158            reactorProjects, workspaceRoot, getLog(), isVerbose());
159        
160        if (isVerbose()) {
161            getLog().info("Generated " + createDependencies.size() + " workspace dependencies");
162        }
163        
164        Map<String, Object> result = new LinkedHashMap<>();
165        result.put("createNodesResults", createNodesResults);
166        result.put("createDependencies", createDependencies);
167        
168        return result;
169    }
170    
171    /**
172     * Calculate goal dependencies for a project
173     */
174    private Map<String, List<String>> calculateGoalDependencies(MavenProject project, File workspaceRoot) {
175        Map<String, List<String>> goalDependencies = new LinkedHashMap<>();
176        
177        if (isVerbose()) {
178            getLog().debug("Calculating goal dependencies for " + project.getArtifactId());
179        }
180        
181        // First pass: collect all potential goal targets
182        Set<String> goalTargets = collectGoalTargets(project);
183        
184        // Calculate dependencies for each goal target
185        for (String targetName : goalTargets) {
186            String goal = ExecutionPlanAnalysisService.extractGoalFromTargetName(targetName);
187            
188            // Try to find execution phase from plugin configuration
189            String executionPhase = findExecutionPhase(project, targetName);
190            
191            List<String> dependencies = targetDependencyService.calculateGoalDependencies(
192                project, executionPhase, targetName, reactorProjects);
193            goalDependencies.put(targetName, dependencies);
194        }
195        
196        return goalDependencies;
197    }
198    
199    /**
200     * Calculate phase dependencies for a project using generated targets
201     */
202    private Map<String, List<String>> calculatePhaseDependencies(MavenProject project, Map<String, TargetConfiguration> allTargets) {
203        Map<String, List<String>> phaseDependencies = new LinkedHashMap<>();
204        
205        // Get all lifecycle phases from all Maven lifecycles (default, clean, site)
206        Set<String> allPhases = executionPlanAnalysisService.getAllLifecyclePhases();
207        
208        if (isVerbose()) {
209            getLog().debug("Calculating dependencies for " + allPhases.size() + " lifecycle phases: " + allPhases);
210        }
211        
212        for (String phase : allPhases) {
213            List<String> dependencies = targetDependencyService.calculatePhaseDependencies(
214                phase, allTargets, project, reactorProjects);
215            phaseDependencies.put(phase, dependencies);
216        }
217        
218        return phaseDependencies;
219    }
220    
221    /**
222     * Update phase targets with calculated dependencies
223     */
224    private void updatePhaseTargetsWithDependencies(Map<String, TargetConfiguration> targets, 
225                                                   Map<String, List<String>> phaseDependencies) {
226        for (Map.Entry<String, List<String>> entry : phaseDependencies.entrySet()) {
227            String phase = entry.getKey();
228            List<String> dependencies = entry.getValue();
229            
230            TargetConfiguration phaseTarget = targets.get(phase);
231            if (phaseTarget != null) {
232                phaseTarget.setDependsOn(dependencies);
233            }
234        }
235    }
