237    /**
238     * Collect all potential goal targets from project plugins
239     */
240    private Set<String> collectGoalTargets(MavenProject project) {
241        Set<String> goalTargets = new LinkedHashSet<>();
242        
243        if (isVerbose()) {
244            getLog().debug("Collecting goals for " + project.getArtifactId() + " (" + 
245                          (project.getBuildPlugins() != null ? project.getBuildPlugins().size() : 0) + " plugins)");
246        }
247        
248        if (project.getBuildPlugins() != null) {
249            project.getBuildPlugins().forEach(plugin -> {
250                String artifactId = plugin.getArtifactId();
251                
252                // Add goals from executions
253                if (plugin.getExecutions() != null) {
254                    plugin.getExecutions().forEach(execution -> {
255                        if (execution.getGoals() != null) {
