  1import model.TargetConfiguration;
  2import model.TargetMetadata;
  3import org.apache.maven.execution.MavenSession;
  4import org.apache.maven.model.Plugin;
  5import org.apache.maven.model.PluginExecution;
  6import org.apache.maven.model.Resource;
  7import org.apache.maven.plugin.logging.Log;
  8import org.apache.maven.project.MavenProject;
  9
 10import java.util.LinkedHashSet;
 11
 12import java.io.File;
 13import java.nio.file.Path;
 14import java.nio.file.Paths;
 15import java.util.ArrayList;
 16import java.util.Arrays;
 17import java.util.LinkedHashMap;
 18import java.util.List;
 19import java.util.Map;
 20import java.util.Set;
 21
 22/**
 23 * Service responsible for generating Nx targets from Maven project configuration.
 24 * Handles both phase targets and plugin goal targets.
 25 */
 26public class TargetGenerationService {
 27
 28    private final Log log;
 29    private final boolean verbose;
 30    private final MavenSession session;
 31    private final ExecutionPlanAnalysisService executionPlanAnalysisService;
 32
 33    public TargetGenerationService(Log log, boolean verbose, MavenSession session, ExecutionPlanAnalysisService executionPlanAnalysisService) {
 34        this.log = log;
 35        this.verbose = verbose;
 36        this.session = session;
 37        this.executionPlanAnalysisService = executionPlanAnalysisService;
 38    }
 39
 40    /**
 41     * Generate all targets for a Maven project
 42     * @param project The Maven project to generate targets for
 43     * @param workspaceRoot The workspace root directory
 44     * @param goalDependencies Pre-calculated goal dependencies
 45     * @param phaseDependencies Pre-calculated phase dependencies
 46     * @return Map of target name to target configuration
 47     * @throws IllegalArgumentException if project is null
 48     */
 49    public Map<String, TargetConfiguration> generateTargets(MavenProject project, File workspaceRoot,
 50                                                           Map<String, List<String>> goalDependencies,
 51                                                           Map<String, List<String>> phaseDependencies) {
 52        if (project == null) {
 53            throw new IllegalArgumentException("Project cannot be null");
 54        }
 55
 56        Map<String, TargetConfiguration> targets = new LinkedHashMap<>();
 57
 58        try {
 59            // Generate plugin goal targets first
 60            targets.putAll(generatePluginGoalTargets(project, workspaceRoot, goalDependencies));
 61
 62            // Generate Maven lifecycle phase targets
 63            targets.putAll(generatePhaseTargets(project, workspaceRoot, targets, phaseDependencies));
 64
 65        } catch (Exception e) {
 66            if (log != null) {
 67                log.warn("Error generating targets for project " + project.getArtifactId() + ": " + e.getMessage(), e);
 68            }
 69            // Return empty targets rather than failing completely
 70        }
 71
 72        return targets;
 73    }
 74
 75    /**
 76     * Generate targets for Maven lifecycle phases
 77     */
 78    public Map<String, TargetConfiguration> generatePhaseTargets(MavenProject project, File workspaceRoot,
 79                                                                 Map<String, TargetConfiguration> allTargets,
 80                                                                 Map<String, List<String>> phaseDependencies) {
 81        Map<String, TargetConfiguration> phaseTargets = new LinkedHashMap<>();
 82
 83        // Get all phases from all 3 Maven lifecycles (default, clean, site)
 84        Set<String> applicablePhases = executionPlanAnalysisService.getAllLifecyclePhases();
 85
 86        for (String phase : applicablePhases) {
 87            TargetConfiguration target = new TargetConfiguration("nx:noop");
 88
 89            // Configure as no-op
 90            target.setOptions(new LinkedHashMap<>());
 91            target.setInputs(new ArrayList<>());
 92            target.setOutputs(new ArrayList<>());
 93
 94            // Phase dependencies - use pre-calculated dependencies
 95            List<String> dependsOn = phaseDependencies.getOrDefault(phase, new ArrayList<>());
 96
 97            target.setDependsOn(dependsOn);
 98
 99            // Add metadata
100            TargetMetadata metadata = new TargetMetadata("phase", "Maven lifecycle phase: " + phase);
101            metadata.setPhase(phase);
102            metadata.setPlugin("org.apache.maven:maven-core");
103            metadata.setTechnologies(Arrays.asList("maven"));
104            target.setMetadata(metadata);
105
106            phaseTargets.put(phase, target);
107        }
108
109        return phaseTargets;
110    }
111
112    /**
113     * Generate targets for plugin goals
114     */
115    public Map<String, TargetConfiguration> generatePluginGoalTargets(MavenProject project, File workspaceRoot,
116                                                                      Map<String, List<String>> goalDependencies) {
117        Map<String, TargetConfiguration> goalTargets = new LinkedHashMap<>();
118
119        if (verbose) {
120            log.debug("Generating plugin goal targets for project: " + project.getArtifactId());
121        }
122
123        final String projectRootToken = "{projectRoot}";
124        final String actualProjectPath;
125
126        if (workspaceRoot != null) {
127            String relativePath = NxPathUtils.getRelativePath(workspaceRoot, project.getBasedir());
128            actualProjectPath = relativePath.isEmpty() ? "." : relativePath;
129        } else {
130            actualProjectPath = "{projectRoot}";
131        }
132
133        if (project.getBuildPlugins() != null) {
134            project.getBuildPlugins().forEach(plugin -> {
135                // Process actual executions from effective POM
136                if (plugin.getExecutions() != null) {
137                    plugin.getExecutions().forEach(execution -> {
138                        if (execution.getGoals() != null) {
139                            execution.getGoals().forEach(goal -> {
140                                String targetName = ExecutionPlanAnalysisService.getTargetName(plugin.getArtifactId(), goal);
141
142                                if (!goalTargets.containsKey(targetName)) {
143                                    TargetConfiguration target = createGoalTarget(plugin, goal, execution, projectRootToken, actualProjectPath, goalDependencies.getOrDefault(targetName, new ArrayList<>()), project);
144                                    goalTargets.put(targetName, target);
145                                }
146                            });
147                        }
148                    });
149                }
150
151                // Add common goals for well-known plugins
152                addCommonGoalsForPlugin(plugin, goalTargets, projectRootToken, actualProjectPath, goalDependencies, project);
153            });
154        }
155
156        return goalTargets;
157    }
158
159    /**
160     * Create a target configuration for a specific Maven goal
161     */
162    public TargetConfiguration createGoalTarget(Plugin plugin, String goal, PluginExecution execution,
163                                               String projectRootToken, String actualProjectPath,
164                                               List<String> dependencies, MavenProject project) {
165        String pluginKey = plugin.getGroupId() + ":" + plugin.getArtifactId();
166
167        TargetConfiguration target = new TargetConfiguration("nx:run-commands");
168
169        Map<String, Object> options = new LinkedHashMap<>();
170        options.put("command", "mvn " + pluginKey + ":" + goal);
171        options.put("cwd", actualProjectPath);
172        target.setOptions(options);
173
174        // Smart inputs/outputs based on goal using Maven APIs
175        List<String> inputs = getSmartInputsForGoal(goal, project, projectRootToken);
176        target.setInputs(inputs);
177
178        List<String> outputs = executionPlanAnalysisService.getGoalOutputs(goal, projectRootToken, project);
179        target.setOutputs(outputs);
180
181        // Use pre-calculated dependencies
182        target.setDependsOn(dependencies);
183
184        // Metadata
185        TargetMetadata metadata = new TargetMetadata("goal", generateGoalDescription(plugin.getArtifactId(), goal));
186        metadata.setPlugin(pluginKey);
187        metadata.setGoal(goal);
188        metadata.setExecutionId(execution.getId());
189        String executionPhase = execution.getPhase();
190        if (executionPhase != null && !executionPhase.isEmpty() && !executionPhase.startsWith("${")) {
191            metadata.setPhase(executionPhase);
192        } else {
193            metadata.setPhase(executionPlanAnalysisService.findPhaseForGoal(project, goal));
194        }
195        metadata.setTechnologies(Arrays.asList("maven"));
196        target.setMetadata(metadata);
197
198        return target;
199    }
200
201    private void addCommonGoalsForPlugin(Plugin plugin, Map<String, TargetConfiguration> goalTargets,
202                                        String projectRootToken, String actualProjectPath,
203                                        Map<String, List<String>> goalDependencies, MavenProject project) {
204        String artifactId = plugin.getArtifactId();
205        List<String> commonGoals = ExecutionPlanAnalysisService.getCommonGoalsForPlugin(artifactId);
206
207        for (String goal : commonGoals) {
208            String targetName = ExecutionPlanAnalysisService.getTargetName(artifactId, goal);
209            if (!goalTargets.containsKey(targetName)) {
210                TargetConfiguration target = createSimpleGoalTarget(plugin, goal, projectRootToken, actualProjectPath, goalDependencies.getOrDefault(targetName, new ArrayList<>()), project);
211                goalTargets.put(targetName, target);
212            }
213        }
214    }
215
216    private TargetConfiguration createSimpleGoalTarget(Plugin plugin, String goal,
217                                                      String projectRootToken, String actualProjectPath,
218                                                      List<String> dependencies, MavenProject project) {
219        String pluginKey = plugin.getGroupId() + ":" + plugin.getArtifactId();
220
221        TargetConfiguration target = new TargetConfiguration("nx:run-commands");
222
223        Map<String, Object> options = new LinkedHashMap<>();
224        options.put("command", "mvn " + pluginKey + ":" + goal);
225        options.put("cwd", actualProjectPath);
226        target.setOptions(options);
227
228        List<String> inputs = getSmartInputsForGoal(goal, project, projectRootToken);
229        target.setInputs(inputs);
230
231        List<String> outputs = executionPlanAnalysisService.getGoalOutputs(goal, projectRootToken, project);
232        target.setOutputs(outputs);
233
234        // Use pre-calculated dependencies
235        target.setDependsOn(dependencies);
236
237        TargetMetadata metadata = new TargetMetadata("goal", generateGoalDescription(plugin.getArtifactId(), goal));
238        metadata.setPlugin(pluginKey);
239        metadata.setGoal(goal);
240        metadata.setPhase(executionPlanAnalysisService.findPhaseForGoal(project, goal));
241        metadata.setTechnologies(Arrays.asList("maven"));
242        target.setMetadata(metadata);
243
244        return target;
245    }
246
247    // Helper methods
248
249    /**
250     * Get smart inputs for a Maven goal using actual Maven project configuration
251     * instead of hardcoded patterns. This respects custom source directories,
252     * resource directories, and other Maven configuration.
253     */
254    private List<String> getSmartInputsForGoal(String goal, MavenProject project, String projectRootToken) {
255        List<String> inputs = new ArrayList<>();
256        
257        // Always include POM
258        inputs.add(projectRootToken + "/pom.xml");
259        
260        if (isSourceProcessingGoal(goal)) {
261            // Add actual source directories from Maven configuration
262            for (String sourceRoot : project.getCompileSourceRoots()) {
263                String relativePath = getRelativePathFromProject(sourceRoot, project);
264                if (relativePath != null && !relativePath.isEmpty()) {
265                    inputs.add(projectRootToken + "/" + relativePath + "/**/*");
266                }
267            }
268            
269            // Add test sources for test-related goals
270            if (isTestGoal(goal)) {
271                for (String testSourceRoot : project.getTestCompileSourceRoots()) {
272                    String relativePath = getRelativePathFromProject(testSourceRoot, project);
273                    if (relativePath != null && !relativePath.isEmpty()) {
274                        inputs.add(projectRootToken + "/" + relativePath + "/**/*");
275                    }
276                }
277            }
278            
279            // Add resource directories for goals that need them
280            if (needsResources(goal)) {
281                // Main resources
282                if (project.getBuild() != null && project.getBuild().getResources() != null) {
283                    for (Resource resource : project.getBuild().getResources()) {
284                        if (resource.getDirectory() != null) {
285                            String relativePath = getRelativePathFromProject(resource.getDirectory(), project);
286                            if (relativePath != null && !relativePath.isEmpty()) {
287                                inputs.add(projectRootToken + "/" + relativePath + "/**/*");
288                            }
289                        }
290                    }
291                }
292                
293                // Test resources for test-related goals
294                if (isTestGoal(goal)) {
295                    if (project.getBuild() != null && project.getBuild().getTestResources() != null) {
296                        for (Resource resource : project.getBuild().getTestResources()) {
297                            if (resource.getDirectory() != null) {
298                                String relativePath = getRelativePathFromProject(resource.getDirectory(), project);
299                                if (relativePath != null && !relativePath.isEmpty()) {
300                                    inputs.add(projectRootToken + "/" + relativePath + "/**/*");
301                                }
302                            }
303                        }
304                    }
305                }
306            }
307        }
308        
309        return inputs;
310    }
311
312    private boolean isSourceProcessingGoal(String goal) {
313        return goal.contains("compile") || goal.contains("test") || goal.contains("build") ||
314               goal.equals("dev") || goal.equals("run") || goal.contains("resources");
315    }
316    
317    private boolean isTestGoal(String goal) {
318        return goal.contains("test") || goal.equals("testCompile") || goal.equals("testResources") ||
319               goal.equals("surefire:test") || goal.equals("failsafe:integration-test");
320    }
321    
322    private boolean needsResources(String goal) {
323        return goal.contains("compile") || goal.contains("resources") || 
324               goal.equals("dev") || goal.equals("build") || goal.equals("run") ||
325               goal.contains("test");
326    }
327    
328    /**
329     * Convert an absolute or relative path from Maven configuration to a relative path
330     * from the project base directory.
331     */
332    private String getRelativePathFromProject(String pathString, MavenProject project) {
333        if (pathString == null || pathString.trim().isEmpty()) {
334            return null;
335        }
336        
337        try {
338            Path projectBase = project.getBasedir().toPath().toAbsolutePath();
339            Path targetPath = Paths.get(pathString);
