347                    if (verbose && log != null) {
348                        log.debug("Skipping path outside project: " + pathString);
349                    }
350                    return null;
351                }
352            } else {
353                // Already relative - normalize separators
354                return targetPath.toString().replace('\\', '/');
355            }
356        } catch (Exception e) {
357            if (log != null) {
358                log.warn("Error processing path: " + pathString + ", error: " + e.getMessage());
359            }
360            return null;
361        }
362    }
