import org.apache.maven.execution.MavenSession;
import org.apache.maven.model.Plugin;
import org.apache.maven.plugin.MojoExecution;
import org.apache.maven.plugin.logging.Log;
import org.apache.maven.project.MavenProject;
import org.codehaus.plexus.util.xml.Xpp3Dom;

import java.util.Map;
import java.util.concurrent.ConcurrentHashMap;

/**
 * Service that dynamically analyzes Maven goals using Maven APIs instead of hardcoded patterns.
 * Uses MojoExecution, plugin configuration, and lifecycle phase information to determine 
 * goal behavior and requirements.
 */
public class DynamicGoalAnalysisService {

    private final MavenSession session;
    private final ExecutionPlanAnalysisService executionPlanAnalysis;
    private final Log log;
    private final boolean verbose;
    private final Map<String, GoalBehavior> analysisCache = new ConcurrentHashMap<>();

    public DynamicGoalAnalysisService(MavenSession session, ExecutionPlanAnalysisService executionPlanAnalysis, Log log, boolean verbose) {
        this.session = session;
        this.executionPlanAnalysis = executionPlanAnalysis;
        this.log = log;
        this.verbose = verbose;
    }

    /**
     * Analyze a goal to determine its behavior and requirements.
     */
    public GoalBehavior analyzeGoal(String goal, MavenProject project) {
        String cacheKey = project.getId() + ":" + goal;

        return analysisCache.computeIfAbsent(cacheKey, k -> {
            if (verbose && log != null) {
                log.debug("Analyzing goal behavior for: " + goal);
            }

            GoalBehavior behavior = new GoalBehavior();

            // 1. Find the plugin that provides this goal
            Plugin plugin = findPluginForGoal(goal, project);
            if (plugin != null) {
                GoalBehavior pluginBehavior = analyzePluginConfiguration(plugin, goal);
                behavior = behavior.merge(pluginBehavior);
            }

            // 2. Analyze by lifecycle phase
            String phase = executionPlanAnalysis.findPhaseForGoal(project, goal);
            if (phase != null) {
                GoalBehavior phaseBehavior = analyzeByPhase(phase);
                behavior = behavior.merge(phaseBehavior);
            }

            // 3. Use MojoExecution information if available
            GoalBehavior mojoAnalysis = analyzeMojoExecution(goal, project);
            if (mojoAnalysis.hasAnyBehavior()) {
                behavior = behavior.merge(mojoAnalysis);
            }

            // 4. Minimal fallback if all else fails
            if (!behavior.hasAnyBehavior()) {
                behavior = analyzeMinimalFallback(goal);
            }

            if (verbose && log != null) {
                log.debug("Goal " + goal + " analysis: sources=" + behavior.processesSources() + 
                         ", test=" + behavior.isTestRelated() + ", resources=" + behavior.needsResources());
            }

            return behavior;
        });
    }

    /**
     * Find which plugin provides a specific goal by examining project build plugins.
     */
    private Plugin findPluginForGoal(String goal, MavenProject project) {
        if (project.getBuildPlugins() == null) return null;

        // Check if goal is in format "pluginKey:goal"
        if (goal.contains(":")) {
            String[] parts = goal.split(":");
            if (parts.length >= 2) {
                String artifactId = parts[parts.length - 2]; // Handle groupId:artifactId:goal
                return project.getBuildPlugins().stream()
                    .filter(p -> artifactId.equals(p.getArtifactId()))
                    .findFirst()
                    .orElse(null);
            }
        }

        // For goals without plugin prefix, we'd need more complex resolution
        // This is a limitation that would require PluginManager API access
        return null;
    }

    /**
     * Analyze plugin configuration to determine goal behavior.
     */
    private GoalBehavior analyzePluginConfiguration(Plugin plugin, String goal) {
        GoalBehavior behavior = new GoalBehavior();
        String artifactId = plugin.getArtifactId();

        if ("maven-compiler-plugin".equals(artifactId)) {
            behavior.setProcessesSources(true);
            if ("testCompile".equals(goal)) {
                behavior.setTestRelated(true);
            }

        } else if ("maven-surefire-plugin".equals(artifactId) || 
                   "maven-failsafe-plugin".equals(artifactId)) {
            behavior.setTestRelated(true);
            behavior.setProcessesSources(true); // Tests need compiled sources

        } else if ("maven-resources-plugin".equals(artifactId)) {
            behavior.setNeedsResources(true);
            if (goal.contains("test")) {
                behavior.setTestRelated(true);
            }

        } else if (artifactId.contains("quarkus")) {
            // Quarkus plugins typically need sources and resources
            behavior.setProcessesSources(true);
            behavior.setNeedsResources(true);
            if ("dev".equals(goal) || "test".equals(goal)) {
                behavior.setTestRelated(true);
            }

        } else if ("maven-source-plugin".equals(artifactId) ||
                   "maven-javadoc-plugin".equals(artifactId)) {
            behavior.setProcessesSources(true);

        } else if ("maven-jar-plugin".equals(artifactId) ||
                   "maven-war-plugin".equals(artifactId)) {
            // Packaging plugins work with compiled output, not direct sources
            behavior.setNeedsResources(true);

        } else if ("maven-install-plugin".equals(artifactId) ||
                   "maven-deploy-plugin".equals(artifactId) ||
                   "maven-site-plugin".equals(artifactId)) {
            // These typically don't need sources/resources directly

        } else {
            // Unknown plugin - be conservative and assume it might need sources
            if (goal.contains("compile") || goal.contains("build") || goal.contains("dev")) {
                behavior.setProcessesSources(true);
                behavior.setNeedsResources(true);
            }
        }

        return behavior;
    }

    /**
     * Analyze goal behavior based on Maven lifecycle phase.
     */
    private GoalBehavior analyzeByPhase(String phase) {
        GoalBehavior behavior = new GoalBehavior();

        if (phase == null) return behavior;

        switch (phase) {
            // Source processing phases
            case "generate-sources":
            case "process-sources":
            case "compile":
            case "process-classes":
                behavior.setProcessesSources(true);
                break;

            // Test phases
            case "generate-test-sources":
            case "process-test-sources":
            case "test-compile":
            case "process-test-classes":
            case "test":
            case "integration-test":
                behavior.setTestRelated(true);
                behavior.setProcessesSources(true);
                break;

            // Resource phases
            case "generate-resources":
            case "process-resources":
                behavior.setNeedsResources(true);
                break;

            case "process-test-resources":
                behavior.setNeedsResources(true);
                behavior.setTestRelated(true);
                break;

            // Packaging phases typically don't need direct source access
            case "package":
            case "verify":
            case "install":
            case "deploy":
                break;
        }

        return behavior;
    }

    /**
     * Analyze using MojoExecution information when available.
     */
    private GoalBehavior analyzeMojoExecution(String goal, MavenProject project) {
        GoalBehavior behavior = new GoalBehavior();

        try {
            // This would require access to the actual MojoExecution
            // which is available in ExecutionPlanAnalysisService
            // For now, return empty behavior as this requires more integration
        } catch (Exception e) {
            if (log != null) {
                log.debug("Could not analyze MojoExecution for " + goal + ": " + e.getMessage());
            }
        }

        return behavior;
    }

    /**
     * Minimal fallback analysis using goal name patterns.
     * This is much more conservative than the old hardcoded approach.
     */
    private GoalBehavior analyzeMinimalFallback(String goal) {
        GoalBehavior behavior = new GoalBehavior();

        // Only set flags for very obvious cases
        if (goal.equals("compile") || goal.equals("testCompile")) {
            behavior.setProcessesSources(true);
            if (goal.equals("testCompile")) {
                behavior.setTestRelated(true);
            }
        } else if (goal.equals("test")) {
            behavior.setTestRelated(true);
            behavior.setProcessesSources(true);
        } else if (goal.equals("resources") || goal.equals("testResources")) {
            behavior.setNeedsResources(true);
            if (goal.equals("testResources")) {
                behavior.setTestRelated(true);
            }
        }

        return behavior;
    }
}

