import org.apache.maven.execution.MavenSession;
import org.apache.maven.lifecycle.LifecycleExecutor;
import org.apache.maven.plugin.logging.Log;
import org.apache.maven.project.MavenProject;

import java.util.Map;
import java.util.concurrent.ConcurrentHashMap;

/**
 * Enhanced service that dynamically analyzes Maven goals using Maven's introspection APIs
 * instead of hardcoded patterns. This version integrates with MavenPluginIntrospectionService
 * to provide detailed analysis of plugin behavior and requirements.
 */
public class EnhancedDynamicGoalAnalysisService {

    private final MavenSession session;
    private final ExecutionPlanAnalysisService executionPlanAnalysis;
    private final MavenPluginIntrospectionService introspectionService;
    private final Log log;
    private final boolean verbose;
    private final Map<String, GoalBehavior> analysisCache = new ConcurrentHashMap<>();

    public EnhancedDynamicGoalAnalysisService(MavenSession session, 
                                            ExecutionPlanAnalysisService executionPlanAnalysis,
                                            LifecycleExecutor lifecycleExecutor,
                                            Log log, 
                                            boolean verbose) {
        this.session = session;
        this.executionPlanAnalysis = executionPlanAnalysis;
        this.log = log;
        this.verbose = verbose;

        // Initialize introspection service
        this.introspectionService = new MavenPluginIntrospectionService(session, lifecycleExecutor, log, verbose);
    }

    /**
     * Analyze a goal to determine its behavior using dynamic introspection
     */
    public GoalBehavior analyzeGoal(String goal, MavenProject project) {
        String cacheKey = project.getId() + ":" + goal;

        return analysisCache.computeIfAbsent(cacheKey, k -> {
            if (verbose && log != null) {
                log.debug("Analyzing goal behavior for: " + goal);
            }

            // 1. Primary analysis: Use Maven introspection APIs
            MavenPluginIntrospectionService.GoalIntrospectionResult introspectionResult = 
                introspectionService.analyzeGoal(goal, project);

            GoalBehavior behavior = introspectionResult.toGoalBehavior();

            // 2. Secondary analysis: Enhance with lifecycle phase information
            String phase = executionPlanAnalysis.findPhaseForGoal(project, goal);
            if (phase != null) {
                GoalBehavior phaseBehavior = analyzeByPhase(phase);
                behavior = behavior.merge(phaseBehavior);
            }

            // 3. Tertiary analysis: Apply additional heuristics based on introspection results
            enhanceWithIntrospectionHeuristics(introspectionResult, behavior);

            // 4. Final validation: Ensure behavior makes sense
            validateAndAdjustBehavior(behavior, goal, introspectionResult);

            if (verbose && log != null) {
                log.debug("Goal " + goal + " final analysis: sources=" + behavior.processesSources() + 
                         ", test=" + behavior.isTestRelated() + ", resources=" + behavior.needsResources() +
                         " (based on " + introspectionResult.getFileParameters().size() + " parameters)");
            }

            return behavior;
        });
    }

    /**
     * Analyze goal behavior based on Maven lifecycle phase
     */
    private GoalBehavior analyzeByPhase(String phase) {
        GoalBehavior behavior = new GoalBehavior();

        if (phase == null) return behavior;

        switch (phase) {
            // Source processing phases
            case "generate-sources":
            case "process-sources":
            case "compile":
            case "process-classes":
                behavior.setProcessesSources(true);
                break;

            // Test phases
            case "generate-test-sources":
            case "process-test-sources":
            case "test-compile":
            case "process-test-classes":
            case "test":
            case "integration-test":
                behavior.setTestRelated(true);
                behavior.setProcessesSources(true);
                break;

            // Resource phases
            case "generate-resources":
            case "process-resources":
                behavior.setNeedsResources(true);
                break;

            case "process-test-resources":
                behavior.setNeedsResources(true);
                behavior.setTestRelated(true);
                break;
        }

        return behavior;
    }

    /**
     * Enhance behavior with additional heuristics based on introspection results
     */
    private void enhanceWithIntrospectionHeuristics(
            MavenPluginIntrospectionService.GoalIntrospectionResult introspectionResult,
            GoalBehavior behavior) {

        // If mojo has file parameters, it likely needs some form of input
        if (!introspectionResult.getFileParameters().isEmpty()) {
            // Check if parameters suggest source processing
            boolean hasSourceParams = introspectionResult.getFileParameters().stream()
                .anyMatch(param -> param.getName().toLowerCase().contains("source") ||
                                  param.getType().equals("java.io.File"));

            if (hasSourceParams) {
                behavior.setProcessesSources(true);
            }

            // Check if parameters suggest resource processing
            boolean hasResourceParams = introspectionResult.getFileParameters().stream()
                .anyMatch(param -> param.getName().toLowerCase().contains("resource"));

            if (hasResourceParams) {
                behavior.setNeedsResources(true);
            }
        }

        // If mojo has many input patterns, it likely processes sources
        if (introspectionResult.getInputPatterns().size() > 2) {
            behavior.setProcessesSources(true);
        }

        // If mojo has output patterns, it generates files
        if (!introspectionResult.getOutputPatterns().isEmpty()) {
            // Could be generating sources, resources, or artifacts
            behavior.setProcessesSources(true);
        }

        // If mojo requires dependency resolution, it likely needs compiled classes
        if ("compile".equals(introspectionResult.getRequiresDependencyResolution()) ||
            "test".equals(introspectionResult.getRequiresDependencyResolution())) {
            behavior.setProcessesSources(true);
        }

        // Framework-specific enhancements
        enhanceForFrameworks(introspectionResult, behavior);
    }

    /**
     * Apply framework-specific enhancements
     */
    private void enhanceForFrameworks(
            MavenPluginIntrospectionService.GoalIntrospectionResult introspectionResult,
            GoalBehavior behavior) {

        String pluginArtifactId = introspectionResult.getPluginArtifactId();
        String goal = introspectionResult.getGoal();

        if (pluginArtifactId == null) return;

        // Quarkus-specific enhancements
        if (pluginArtifactId.contains("quarkus")) {
            // Quarkus dev mode needs sources and resources
            if ("dev".equals(goal)) {
                behavior.setProcessesSources(true);
                behavior.setNeedsResources(true);
                behavior.setTestRelated(true); // dev mode can run tests
            }

            // Quarkus build typically needs everything
            if ("build".equals(goal) || "build-uber-jar".equals(goal)) {
                behavior.setProcessesSources(true);
                behavior.setNeedsResources(true);
            }
        }

        // Spring Boot specific enhancements
        if (pluginArtifactId.contains("spring-boot")) {
            if ("run".equals(goal)) {
                behavior.setProcessesSources(true);
                behavior.setNeedsResources(true);
            }

            if ("repackage".equals(goal)) {
                behavior.setNeedsResources(true);
            }
        }

        // Compiler plugin specific enhancements
        if (pluginArtifactId.contains("compiler")) {
            // Always processes sources
            behavior.setProcessesSources(true);

            // Check for annotation processing
            if (!introspectionResult.getConfigurationPaths().isEmpty()) {
                boolean hasAnnotationProcessing = introspectionResult.getConfigurationPaths().keySet().stream()
                    .anyMatch(key -> key.toLowerCase().contains("annotation") || 
                                    key.toLowerCase().contains("processor"));

                if (hasAnnotationProcessing) {
                    // Annotation processing might generate sources
                    behavior.setNeedsResources(true);
                }
            }
        }
    }

    /**
     * Validate and adjust behavior to ensure it makes sense
     */
    private void validateAndAdjustBehavior(GoalBehavior behavior, String goal,
                                         MavenPluginIntrospectionService.GoalIntrospectionResult introspectionResult) {

        // If it's a test goal but not marked as test-related, fix it
        if (goal != null && (goal.contains("test") || goal.contains("Test")) && !behavior.isTestRelated()) {
            behavior.setTestRelated(true);
            if (verbose && log != null) {
                log.debug("Adjusted test-related flag for goal: " + goal);
            }
        }

        // If it's test-related, it probably needs sources
        if (behavior.isTestRelated() && !behavior.processesSources()) {
            behavior.setProcessesSources(true);
            if (verbose && log != null) {
                log.debug("Adjusted processes-sources flag for test goal: " + goal);
            }
        }

        // If it processes sources in compile phase, it might need resources too
        if (behavior.processesSources() && "compile".equals(introspectionResult.getLifecyclePhase())) {
            if (!behavior.needsResources() && hasResourceIndicators(introspectionResult)) {
                behavior.setNeedsResources(true);
                if (verbose && log != null) {
                    log.debug("Adjusted needs-resources flag for compile goal: " + goal);
                }
            }
        }
    }

    /**
     * Check if introspection result has indicators that suggest resource processing
     */
    private boolean hasResourceIndicators(MavenPluginIntrospectionService.GoalIntrospectionResult result) {
        // Check for resource-related parameters
        return result.getFileParameters().stream()
            .anyMatch(param -> param.getName().toLowerCase().contains("resource") ||
                              param.getDescription() != null && 
                              param.getDescription().toLowerCase().contains("resource")) ||
               result.getConfigurationPaths().keySet().stream()
                   .anyMatch(key -> key.toLowerCase().contains("resource"));
    }

    /**
     * Get detailed introspection result for a goal (for debugging/analysis)
     */
    public MavenPluginIntrospectionService.GoalIntrospectionResult getIntrospectionResult(String goal, MavenProject project) {
        return introspectionService.analyzeGoal(goal, project);
    }

    /**
     * Clear caches (useful for testing)
     */
    public void clearCache() {
        analysisCache.clear();
    }

    /**
     * Get cache statistics
     */
    public Map<String, Object> getCacheStats() {
        return Map.of(
            "analysisCache", analysisCache.size(),
            "cachedGoals", analysisCache.keySet()
        );
    }
}

