192    /**
193     * Check if parameter represents a file or directory
194     */
195    private boolean isFileParameter(String name, String type, String description) {
196        if (type == null) return false;
197        
198        // Check type
199        if (type.equals("java.io.File") || type.equals("java.nio.file.Path") || 
200            type.equals("java.lang.String") && (name.contains("Dir") || name.contains("File") || name.contains("Path"))) {
201            return true;
202        }
203        
204        // Check name patterns
205        if (name != null) {
206            String lowerName = name.toLowerCase();
207            if (lowerName.contains("directory") || lowerName.contains("file") || lowerName.contains("path") ||
208                lowerName.contains("output") || lowerName.contains("input") || lowerName.contains("source") ||
209                lowerName.contains("target") || lowerName.contains("destination")) {
210                return true;
211            }
212        }
213        
214        // Check description
215        if (description != null) {
216            String lowerDesc = description.toLowerCase();
217            return lowerDesc.contains("directory") || lowerDesc.contains("file") || lowerDesc.contains("path");
218        }
219        
220        return false;
221    }
