342        // Very conservative fallback
343        if (goal != null) {
344            if (goal.equals("compile") || goal.equals("testCompile")) {
345                result.setProcessesSources(true);
346                if (goal.equals("testCompile")) {
347                    result.setTestRelated(true);
348                }
349            } else if (goal.equals("test")) {
350                result.setTestRelated(true);
351                result.setProcessesSources(true);
352            }
353        }
354        
355        return result;
356    }
