340     */
341    public enum PhaseCategory {
342        SOURCE_PROCESSING,
343        TEST_RELATED,
344        RESOURCE_PROCESSING,
345        GENERATION,
346        PROCESSING,
347        COMPILATION,
348        PACKAGING,
349        VERIFICATION,
350        DEPLOYMENT,
351        VALIDATION,
352        INTEGRATION,
353        CLEANUP,
354        DOCUMENTATION,
355        BUILD,
356        EARLY_PHASE,
357        MIDDLE_PHASE,
358        LATE_PHASE
359    }
360}

