 13/**
 14 * Service that analyzes Maven execution plans upfront and stores all results for efficient querying.
 15 * Pre-computes all Maven analysis to avoid performance bottlenecks during target dependency analysis.
 16 */
 17public class ExecutionPlanAnalysisService {
 18    
 19    private final Log log;
 20    private final boolean verbose;
 21    private final LifecycleExecutor lifecycleExecutor;
 22    private final MavenSession session;
 23    private final DefaultLifecycles defaultLifecycles;
 24    
 25    // Pre-computed analysis results per project
 26    private final Map<String, ProjectExecutionAnalysis> analysisCache = new ConcurrentHashMap<>();
 27    
 28    // Pre-computed lifecycle information (shared across all projects)
 29    private final Set<String> allLifecyclePhases;
 30    private final List<String> defaultLifecyclePhases;
 31    private final List<String> cleanLifecyclePhases;
 32    private final List<String> siteLifecyclePhases;
 33    private final Map<String, org.apache.maven.lifecycle.Lifecycle> phaseToLifecycleMap;
 34    
 35    public ExecutionPlanAnalysisService(Log log, boolean verbose, LifecycleExecutor lifecycleExecutor, MavenSession session, DefaultLifecycles defaultLifecycles) {
 36        this.log = log;
 37        this.verbose = verbose;
 38        this.lifecycleExecutor = lifecycleExecutor;
 39        this.session = session;
 40        this.defaultLifecycles = defaultLifecycles;
 41        
 42        // Pre-compute all lifecycle information once
 43        this.allLifecyclePhases = computeAllLifecyclePhases();
 44        this.defaultLifecyclePhases = computeLifecyclePhases("default");
 45        this.cleanLifecyclePhases = computeLifecyclePhases("clean");
 46        this.siteLifecyclePhases = computeLifecyclePhases("site");
 47        this.phaseToLifecycleMap = computePhaseToLifecycleMap();
 48        
 49        if (verbose) {
 50            log.info("Pre-computed lifecycle information: " + allLifecyclePhases.size() + " total phases");
 51        }
 52    }
 53    
 54    /**
 55     * Pre-analyze all projects in the reactor upfront to avoid performance bottlenecks
 56     */
 57    public void preAnalyzeAllProjects(List<MavenProject> reactorProjects) {
 58        if (reactorProjects == null || reactorProjects.isEmpty()) {
 59            return;
 60        }
 61        
 62        long startTime = System.currentTimeMillis();
 63        if (verbose) {
 64            log.info("Pre-analyzing execution plans for " + reactorProjects.size() + " projects...");
 65        }
 66        
 67        for (MavenProject project : reactorProjects) {
 68            String projectKey = MavenUtils.formatProjectKey(project);
 69            if (!analysisCache.containsKey(projectKey)) {
 70                analysisCache.put(projectKey, analyzeProject(project));
 71            }
 72        }
 73        
 74        long duration = System.currentTimeMillis() - startTime;
 75        if (verbose) {
 76            log.info("Completed pre-analysis of " + reactorProjects.size() + " projects in " + duration + "ms");
 77        }
 78    }
 79    
 80    /**
 81     * Get the LifecycleExecutor for use by other services
 82     */
 83    public LifecycleExecutor getLifecycleExecutor() {
 84        return lifecycleExecutor;
 85    }
 86    
 87    /**
 88     * Get the DefaultLifecycles for use by other services
 89     */
 90    public DefaultLifecycles getDefaultLifecycles() {
 91        return defaultLifecycles;
 92    }
 93    
 94    /**
 95     * Get or compute execution analysis for a project
 96     */
 97    public ProjectExecutionAnalysis getAnalysis(MavenProject project) {
 98        String projectKey = MavenUtils.formatProjectKey(project);
 99        return analysisCache.computeIfAbsent(projectKey, key -> analyzeProject(project));
100    }
101    
102    /**
103     * Find the phase for a specific goal
104     */
105    public String findPhaseForGoal(MavenProject project, String goal) {
106        if (project == null || goal == null || goal.isEmpty()) {
107            return null;
108        }
109        ProjectExecutionAnalysis analysis = getAnalysis(project);
110        return analysis.getPhaseForGoal(goal);
111    }
112    
113    
114    /**
115     * Get all goals for a specific phase
116     */
117    public List<String> getGoalsForPhase(MavenProject project, String phase) {
118        if (project == null || phase == null) {
119            return new ArrayList<>();
120        }
121        ProjectExecutionAnalysis analysis = getAnalysis(project);
122        return analysis.getGoalsForPhase(phase);
123    }
124    
125    /**
126     * Get all lifecycle phases from all lifecycles (default, clean, site)
127     */
128    public Set<String> getAllLifecyclePhases() {
129        return new LinkedHashSet<>(allLifecyclePhases);
130    }
131    
132    /**
133     * Get default lifecycle phases (validate, compile, test, package, verify, install, deploy)
134     */
135    public List<String> getDefaultLifecyclePhases() {
136        return new ArrayList<>(defaultLifecyclePhases);
137    }
138    
139    /**
140     * Get clean lifecycle phases (pre-clean, clean, post-clean)
141     */
142    public List<String> getCleanLifecyclePhases() {
143        return new ArrayList<>(cleanLifecyclePhases);
144    }
145    
146    /**
147     * Get site lifecycle phases (pre-site, site, post-site, site-deploy)
148     */
149    public List<String> getSiteLifecyclePhases() {
150        return new ArrayList<>(siteLifecyclePhases);
151    }
152    
153    /**
154     * Get the lifecycle that contains the specified phase
155     */
156    public org.apache.maven.lifecycle.Lifecycle getLifecycleForPhase(String phase) {
157        if (phase == null) {
158            return null;
159        }
160        return phaseToLifecycleMap.get(phase);
161    }
162    
163    /**
164     * Get all goals that should be completed by the time the specified phase finishes.
165     * This includes goals from all phases up to and including the target phase.
166     */
167    public List<String> getGoalsCompletedByPhase(MavenProject project, String targetPhase) {
168        if (project == null || targetPhase == null) {
169            return new ArrayList<>();
170        }
171        
172        List<String> completedGoals = new ArrayList<>();
173        org.apache.maven.lifecycle.Lifecycle lifecycle = getLifecycleForPhase(targetPhase);
174        
175        if (lifecycle == null) {
176            return completedGoals;
177        }
178        
179        List<String> lifecyclePhases = lifecycle.getPhases();
180        int targetPhaseIndex = lifecyclePhases.indexOf(targetPhase);
181        
182        if (targetPhaseIndex == -1) {
183            return completedGoals;
184        }
185        
186        // Use Set to avoid duplicates
187        Set<String> uniqueGoals = new LinkedHashSet<>();
188        
189        // Get goals from all phases up to and including the target phase
190        for (int i = 0; i <= targetPhaseIndex; i++) {
191            String phase = lifecyclePhases.get(i);
192            List<String> phaseGoals = getGoalsForPhase(project, phase);
193            
194            // Convert to proper plugin:goal format
195            for (String goal : phaseGoals) {
196                if (goal.contains(":")) {
197                    // Goal is already in plugin:goal format
198                    String[] parts = goal.split(":");
199                    if (parts.length >= 3) {
200                        // Format: groupId:artifactId:goal
201                        String groupId = parts[0];
202                        String artifactId = parts[1];
203                        String goalName = parts[2];
204                        uniqueGoals.add(groupId + ":" + artifactId + ":" + goalName);
205                    } else if (parts.length == 2) {
206                        // Format: plugin:goal - convert to full format
207                        String plugin = parts[0];
208                        String goalName = parts[1];
209                        uniqueGoals.add("org.apache.maven.plugins:maven-" + plugin + "-plugin:" + goalName);
210                    }
211                }
212            }
213        }
214        
215        completedGoals.addAll(uniqueGoals);
216        
217        return completedGoals;
218    }
