220    
221    /**
222     * Pre-compute all lifecycle phases from all lifecycles
223     */
224    private Set<String> computeAllLifecyclePhases() {
225        Set<String> allPhases = new LinkedHashSet<>();
226        allPhases.addAll(computeLifecyclePhases("default"));
227        allPhases.addAll(computeLifecyclePhases("clean"));
228        allPhases.addAll(computeLifecyclePhases("site"));
229        return allPhases;
230    }
231    
232    /**
233     * Pre-compute lifecycle phases for a specific lifecycle ID
234     */
235    private List<String> computeLifecyclePhases(String lifecycleId) {
236        if (defaultLifecycles == null || lifecycleId == null) {
237            return new ArrayList<>();
238        }
239
240        try {
241            for (org.apache.maven.lifecycle.Lifecycle lifecycle : defaultLifecycles.getLifeCycles()) {
242                if (lifecycleId.equals(lifecycle.getId()) && lifecycle.getPhases() != null) {
243                    return new ArrayList<>(lifecycle.getPhases());
244                }
245            }
246            return new ArrayList<>();
247
248        } catch (Exception e) {
249            if (verbose) {
250                log.warn("Could not access Maven lifecycle '" + lifecycleId + "': " + e.getMessage());
251            }
252            return new ArrayList<>();
253        }
254    }
255    
256    /**
257     * Pre-compute phase to lifecycle mapping for efficient lookups
258     */
259    private Map<String, org.apache.maven.lifecycle.Lifecycle> computePhaseToLifecycleMap() {
260        Map<String, org.apache.maven.lifecycle.Lifecycle> map = new HashMap<>();
261        
262        if (defaultLifecycles == null) {
263            return map;
264        }
265        
266        try {
267            return defaultLifecycles.getPhaseToLifecycleMap();
268        } catch (Exception e) {
269            if (verbose) {
270                log.warn("Could not build phase to lifecycle map: " + e.getMessage());
271            }
272            return map;
273        }
274    }
