276    /**
277     * Analyze a project's execution plans and cache the results
278     */
279    private ProjectExecutionAnalysis analyzeProject(MavenProject project) {
280        if (verbose) {
281            log.debug("Analyzing execution plans for project: " + project.getArtifactId());
282        }
283        
284        ProjectExecutionAnalysis analysis = new ProjectExecutionAnalysis();
285        
286        // Use pre-computed lifecycle phases for better performance
287        for (String phase : allLifecyclePhases) {
288            try {
289                if (lifecycleExecutor != null && session != null) {
290                    MavenExecutionPlan executionPlan = lifecycleExecutor.calculateExecutionPlan(session, phase);
291                    analysis.addExecutionPlan(phase, executionPlan);
292                    
293                    if (verbose) {
294                        log.debug("Analyzed " + executionPlan.getMojoExecutions().size() + 
295                                 " executions for phase: " + phase);
296                    }
297                } else {
298                    if (verbose) {
299                        log.warn("LifecycleExecutor or session is null, skipping phase: " + phase);
300                    }
301                }
302                
303            } catch (Exception e) {
304                if (verbose) {
305                    log.warn("Could not calculate execution plan for " + phase + ": " + e.getMessage());
306                }
307            }
308        }
309        
310        if (verbose) {
311            log.debug("Completed analysis for " + project.getArtifactId() + 
312                     ": " + analysis.getAllPhases().size() + " phases, " + 
313                     analysis.getAllGoals().size() + " goals");
314        }
315        
316        return analysis;
317    }
