368    /**
369     * Get common goals for well-known plugins
370     */
371    public static List<String> getCommonGoalsForPlugin(String artifactId) {
372        List<String> commonGoals = new ArrayList<>();
373        
374        if (artifactId == null) {
375            return commonGoals;
376        }
377        
378        if (artifactId.contains("compiler")) {
379            commonGoals.addAll(Arrays.asList("compile", "testCompile"));
380        } else if (artifactId.contains("surefire")) {
381            commonGoals.add("test");
382        } else if (artifactId.contains("quarkus")) {
383            commonGoals.addAll(Arrays.asList("dev", "build"));
384        } else if (artifactId.contains("spring-boot")) {
385            commonGoals.addAll(Arrays.asList("run", "repackage"));
386        }
387        
388        return commonGoals;
389    }
