391    /**
392     * Container for analyzed execution plan data for a single project
393     */
394    public static class ProjectExecutionAnalysis {
395        private final Map<String, String> goalToPhaseMap = new HashMap<>();
396        private final Map<String, List<String>> phaseToGoalsMap = new HashMap<>();
397        private final Set<String> allPhases = new LinkedHashSet<>();
398        private final Set<String> allGoals = new LinkedHashSet<>();
399        private final Map<String, ExecutionInfo> goalToExecutionInfo = new HashMap<>();
400        
401        /**
402         * Add execution plan data from a lifecycle endpoint
403         */
404        void addExecutionPlan(String endpoint, MavenExecutionPlan executionPlan) {
405            for (MojoExecution mojoExecution : executionPlan.getMojoExecutions()) {
406                String goal = mojoExecution.getGoal();
407                String phase = mojoExecution.getLifecyclePhase();
408                String pluginArtifactId = mojoExecution.getPlugin().getArtifactId();
409                
410                if (goal != null && phase != null) {
411                    // Store goal-to-phase mapping
412                    goalToPhaseMap.put(goal, phase);
413                    
414                    // Store detailed execution info
415                    ExecutionInfo execInfo = new ExecutionInfo(
416                        goal, phase, pluginArtifactId, 
417                        mojoExecution.getExecutionId(),
418                        mojoExecution.getPlugin().getGroupId() + ":" + pluginArtifactId
419                    );
420                    goalToExecutionInfo.put(goal, execInfo);
421                    
422                    // Handle plugin:goal format too
423                    String targetName = getTargetName(pluginArtifactId, goal);
424                    goalToPhaseMap.put(targetName, phase);
425                    goalToExecutionInfo.put(targetName, execInfo);
426                    
427                    // Track phases and goals
428                    allPhases.add(phase);
429                    allGoals.add(goal);
430                    allGoals.add(targetName);
431                    
432                    // Build phase-to-goals mapping
433                    phaseToGoalsMap.computeIfAbsent(phase, k -> new ArrayList<>()).add(targetName);
434                }
435            }
436        }
437        
438        public String getPhaseForGoal(String goal) {
439            return goalToPhaseMap.get(goal);
440        }
441        
442        public List<String> getGoalsForPhase(String phase) {
443            return phaseToGoalsMap.getOrDefault(phase, new ArrayList<>());
444        }
445        
446        public Set<String> getAllPhases() {
447            return new LinkedHashSet<>(allPhases);
448        }
449        
450        public Set<String> getAllGoals() {
451            return new LinkedHashSet<>(allGoals);
452        }
453        
454        public ExecutionInfo getExecutionInfo(String goal) {
455            return goalToExecutionInfo.get(goal);
456        }
457        
458        public Map<String, List<String>> getPhaseToGoalsMap() {
459            return new HashMap<>(phaseToGoalsMap);
460        }
461    }
