 50            GoalBehavior behavior = new GoalBehavior();
 51            
 52            // 1. Use Maven API-based introspection (primary analysis)
 53            MavenPluginIntrospectionService.GoalIntrospectionResult introspectionResult = 
 54                introspectionService.analyzeGoal(goal, project);
 55            behavior = behavior.merge(introspectionResult.toGoalBehavior());
 56            
 57            // 2. Enhance with Maven API-based lifecycle phase analysis
 58            String phase = executionPlanAnalysis.findPhaseForGoal(project, goal);
