  1import org.apache.maven.execution.MavenSession;
  2import org.apache.maven.lifecycle.LifecycleExecutor;
  3import org.apache.maven.lifecycle.MavenExecutionPlan;
  4import org.apache.maven.plugin.MojoExecution;
  5import org.apache.maven.plugin.PluginParameterExpressionEvaluator;
  6import org.apache.maven.plugin.descriptor.MojoDescriptor;
  7import org.apache.maven.plugin.descriptor.Parameter;
  8import org.apache.maven.plugin.descriptor.PluginDescriptor;
  9import org.apache.maven.plugin.logging.Log;
 10import org.apache.maven.project.MavenProject;
 11import org.codehaus.plexus.util.xml.Xpp3Dom;
 12
 13import java.io.File;
 14import java.util.*;
 15import java.util.concurrent.ConcurrentHashMap;
 16
 17/**
 18 * Service that uses Maven's introspection APIs to dynamically determine plugin and goal behavior
 19 * instead of hardcoded plugin-specific logic. This leverages PluginDescriptor, MojoDescriptor,
 20 * and parameter analysis to understand what files/directories a Mojo actually uses.
 21 */
 22public class MavenPluginIntrospectionService {
 23    
 24    private final MavenSession session;
 25    private final LifecycleExecutor lifecycleExecutor;
 26    private final Log log;
 27    private final boolean verbose;
 28    
 29    // Cache to avoid repeated introspection
 30    private final Map<String, GoalIntrospectionResult> introspectionCache = new ConcurrentHashMap<>();
 31    
 32    public MavenPluginIntrospectionService(MavenSession session, LifecycleExecutor lifecycleExecutor, Log log, boolean verbose) {
 33        this.session = session;
 34        this.lifecycleExecutor = lifecycleExecutor;
 35        this.log = log;
 36        this.verbose = verbose;
 37    }
 38    
 39    /**
 40     * Analyze a goal using Maven's introspection APIs to determine its behavior dynamically.
 41     */
 42    public GoalIntrospectionResult analyzeGoal(String goal, MavenProject project) {
 43        String cacheKey = project.getId() + ":" + goal;
 44        
 45        return introspectionCache.computeIfAbsent(cacheKey, k -> {
 46            if (verbose) {
 47                log.debug("Performing dynamic introspection for goal: " + goal);
 48            }
 49            
 50            GoalIntrospectionResult result = new GoalIntrospectionResult(goal);
 51            
 52            try {
 53                // 1. Find MojoExecution for this goal
 54                MojoExecution mojoExecution = findMojoExecution(goal, project);
 55                if (mojoExecution != null) {
 56                    analyzeMojoExecution(mojoExecution, result);
 57                    
 58                    // 2. Analyze using MojoDescriptor if available
 59                    if (mojoExecution.getMojoDescriptor() != null) {
 60                        analyzeMojoDescriptor(mojoExecution.getMojoDescriptor(), result);
 61                    }
 62                    
 63                    // 3. Analyze plugin configuration
 64                    analyzePluginConfiguration(mojoExecution, result);
 65                } else {
 66                    // No MojoExecution found - use fallback
 67                    if (verbose) {
 68                        log.debug("No MojoExecution found for goal " + goal + ", using fallback analysis");
 69                    }
 70                    result = createFallbackResult(goal);
 71                }
 72                
 73            } catch (Exception e) {
 74                if (verbose) {
 75                    log.warn("Introspection failed for goal " + goal + ": " + e.getMessage());
 76                }
 77                // Fall back to minimal analysis
 78                result = createFallbackResult(goal);
 79            }
 80            
 81            if (verbose) {
 82                log.debug("Introspection result for " + goal + ": " + result.toString());
 83            }
 84            
 85            return result;
 86        });
 87    }
 88    
 89    /**
 90     * Find MojoExecution for a specific goal by examining execution plans
 91     */
 92    private MojoExecution findMojoExecution(String goal, MavenProject project) {
 93        try {
 94            // Try to find the goal in various lifecycle phases
 95            List<String> phasesToCheck = Arrays.asList(
 96                "validate", "compile", "test", "package", "verify", "install", "deploy",
 97                "clean", "site"
 98            );
 99            
100            for (String phase : phasesToCheck) {
101                MavenExecutionPlan executionPlan = lifecycleExecutor.calculateExecutionPlan(session, phase);
102                
103                for (MojoExecution mojoExecution : executionPlan.getMojoExecutions()) {
104                    if (goal.equals(mojoExecution.getGoal()) || 
105                        goal.equals(mojoExecution.getPlugin().getArtifactId() + ":" + mojoExecution.getGoal())) {
106                        return mojoExecution;
107                    }
108                }
109            }
110        } catch (Exception e) {
111            if (verbose) {
112                log.debug("Could not find MojoExecution for " + goal + ": " + e.getMessage());
113            }
114        }
115        
116        return null;
117    }
118    
119    /**
120     * Analyze MojoExecution to extract basic information
121     */
122    private void analyzeMojoExecution(MojoExecution mojoExecution, GoalIntrospectionResult result) {
123        // Get basic execution info
124        result.setPluginArtifactId(mojoExecution.getPlugin().getArtifactId());
125        result.setPluginGroupId(mojoExecution.getPlugin().getGroupId());
126        result.setLifecyclePhase(mojoExecution.getLifecyclePhase());
127        result.setExecutionId(mojoExecution.getExecutionId());
128        
129        // Analyze plugin type patterns
130        String artifactId = mojoExecution.getPlugin().getArtifactId();
131        analyzePluginTypePatterns(artifactId, mojoExecution.getGoal(), result);
132    }
133    
134    /**
135     * Analyze MojoDescriptor to understand mojo parameters and requirements
136     */
137    private void analyzeMojoDescriptor(MojoDescriptor mojoDescriptor, GoalIntrospectionResult result) {
138        if (mojoDescriptor == null) return;
139        
140        // Get mojo description and requirements
141        result.setDescription(mojoDescriptor.getDescription());
142        result.setRequiresDependencyResolution(mojoDescriptor.getDependencyResolutionRequired());
143        result.setRequiresProject(mojoDescriptor.isProjectRequired());
144        
145        // Analyze parameters to understand file/directory requirements
146        List<Parameter> parameters = mojoDescriptor.getParameters();
147        if (parameters != null) {
148            for (Parameter parameter : parameters) {
149                analyzeParameter(parameter, result);
150            }
151        }
152        
153        if (verbose) {
154            log.debug("MojoDescriptor analysis: " + parameters.size() + " parameters, " +
155                     "requires project: " + mojoDescriptor.isProjectRequired() +
156                     ", dependency resolution: " + mojoDescriptor.getDependencyResolutionRequired());
157        }
158    }
159    
160    /**
161     * Analyze individual parameter to understand file/directory requirements
162     */
163    private void analyzeParameter(Parameter parameter, GoalIntrospectionResult result) {
164        String name = parameter.getName();
165        String type = parameter.getType();
166        String description = parameter.getDescription();
167        
168        // Check for file/directory parameters
169        if (isFileParameter(name, type, description)) {
170            result.addFileParameter(name, type, description);
171            
172            // Determine if it's input or output
173            if (isOutputParameter(name, description)) {
174                result.addOutputPattern(name);
175            } else {
176                result.addInputPattern(name);
177            }
178        }
179        
180        // Check for source-related parameters
181        if (isSourceParameter(name, type, description)) {
182            result.setProcessesSources(true);
183        }
184        
185        // Check for test-related parameters
186        if (isTestParameter(name, type, description)) {
187            result.setTestRelated(true);
188        }
189        
190        // Check for resource parameters
191        if (isResourceParameter(name, type, description)) {
192            result.setNeedsResources(true);
193        }
194    }
195    
196    /**
197     * Check if parameter represents a file or directory
198     */
199    private boolean isFileParameter(String name, String type, String description) {
200        if (type == null) return false;
201        
202        // Check type
203        if (type.equals("java.io.File") || type.equals("java.nio.file.Path") || 
204            type.equals("java.lang.String") && (name.contains("Dir") || name.contains("File") || name.contains("Path"))) {
205            return true;
206        }
207        
208        // Check name patterns
209        if (name != null) {
210            String lowerName = name.toLowerCase();
211            if (lowerName.contains("directory") || lowerName.contains("file") || lowerName.contains("path") ||
212                lowerName.contains("output") || lowerName.contains("input") || lowerName.contains("source") ||
213                lowerName.contains("target") || lowerName.contains("destination")) {
214                return true;
215            }
216        }
217        
218        // Check description
219        if (description != null) {
220            String lowerDesc = description.toLowerCase();
221            return lowerDesc.contains("directory") || lowerDesc.contains("file") || lowerDesc.contains("path");
222        }
223        
224        return false;
225    }
226    
227    /**
228     * Check if parameter is an output parameter
229     */
230    private boolean isOutputParameter(String name, String description) {
231        if (name != null) {
232            String lowerName = name.toLowerCase();
233            if (lowerName.contains("output") || lowerName.contains("target") || lowerName.contains("destination") ||
234                lowerName.contains("generated") || lowerName.contains("build")) {
235                return true;
236            }
237        }
238        
239        if (description != null) {
240            String lowerDesc = description.toLowerCase();
241            return lowerDesc.contains("output") || lowerDesc.contains("generate") || lowerDesc.contains("create") ||
242                   lowerDesc.contains("write") || lowerDesc.contains("produce");
243        }
244        
245        return false;
