  5import java.util.*;
  6import java.util.concurrent.ConcurrentHashMap;
  7import java.util.stream.Collectors;
  8
  9/**
 10 * Service that dynamically analyzes Maven lifecycle phases using Maven APIs
 11 * instead of hardcoded phase categorization. Replaces hardcoded switch statements
 12 * with intelligent phase analysis based on Maven's lifecycle metadata.
 13 */
 14public class LifecyclePhaseAnalyzer {
 15    
 16    private final DefaultLifecycles defaultLifecycles;
 17    private final Log log;
 18    private final boolean verbose;
 19    private final Map<String, PhaseAnalysis> analysisCache = new ConcurrentHashMap<>();
 20    
 21    public LifecyclePhaseAnalyzer(DefaultLifecycles defaultLifecycles, Log log, boolean verbose) {
 22        this.defaultLifecycles = defaultLifecycles;
 23        this.log = log;
 24        this.verbose = verbose;
 25    }
 26    
 27    /**
 28     * Analyze a Maven lifecycle phase to determine its behavior and characteristics.
 29     * Uses Maven's DefaultLifecycles API and semantic analysis instead of hardcoded patterns.
 30     */
 31    public PhaseAnalysis analyzePhase(String phase) {
 32        if (phase == null || phase.isEmpty()) {
 33            return new PhaseAnalysis(phase);
