 12/**
 13 * Service responsible for calculating target dependencies in Maven projects.
 14 * Handles phase dependencies, cross-module dependencies, and goal dependencies.
 15 */
 16public class TargetDependencyService {
 17
 18    private final Log log;
 19    private final boolean verbose;
 20    private final ExecutionPlanAnalysisService executionPlanAnalysisService;
 21
 22    public TargetDependencyService(Log log, boolean verbose, ExecutionPlanAnalysisService executionPlanAnalysisService) {
 23        this.log = log;
 24        this.verbose = verbose;
 25        this.executionPlanAnalysisService = executionPlanAnalysisService;
 26    }
 27
 28    /**
 29     * Calculate dependencies for a goal target.
 30     * Goals now depend on other goals, not on phases.
 31     */
 32    public List<Object> calculateGoalDependencies(MavenProject project, String executionPhase,
 33                                                  String targetName, List<MavenProject> actualDependencies) {
 34        List<Object> dependencies = new ArrayList<>();
 35
 36        String effectivePhase = executionPhase;
 37        if (effectivePhase == null || effectivePhase.isEmpty() || effectivePhase.startsWith("${")) {
 38            effectivePhase = executionPlanAnalysisService.findPhaseForGoal(project, ExecutionPlanAnalysisService.extractGoalFromTargetName(targetName));
 39        }
 40
 41        if (effectivePhase != null && !effectivePhase.isEmpty()) {
 42            // Add goal-to-goal dependencies based on Maven lifecycle ordering (same project)
 43            List<String> precedingGoals = getPrecedingGoalsInLifecycle(project, effectivePhase);
 44            for (String goal : precedingGoals) {
 45                dependencies.add(goal); // Simple string dependency for same project
 46            }
 47
 48            // Add cross-module dependencies using object form for better precision
 49            if (!actualDependencies.isEmpty()) {
 50                List<String> dependentProjects = new ArrayList<>();
 51                for (MavenProject depProject : actualDependencies) {
 52                    dependentProjects.add(MavenUtils.formatProjectKey(depProject));
 53                }
 54                
 55                // Create object dependency for cross-module goals in the same phase
 56                TargetDependency crossModuleDep = new TargetDependency(effectivePhase, dependentProjects);
 57                dependencies.add(crossModuleDep);
 58            }
 59        }
 60
 61        return dependencies;
 62    }
 63
 64    /**
 65     * Calculate dependencies for a phase target
 66     */
 67    public List<Object> calculatePhaseDependencies(String phase, Map<String, TargetConfiguration> allTargets,
 68                                                   MavenProject project, List<MavenProject> reactorProjects) {
 69        List<Object> dependsOn = new ArrayList<>();
 70
 71        // Add dependencies on all goals that belong to this phase
 72        List<String> goalsForPhase = getGoalsForPhase(phase, allTargets);
 73        for (String goal : goalsForPhase) {
 74            dependsOn.add(goal); // Simple string dependency for goals in same project
 75        }
 76
 77        return dependsOn;
 78    }
 79
 80    /**
 81     * Get phase dependencies (preceding phases)
 82     */
 83    public List<String> getPhaseDependencies(String phase, MavenProject project) {
 84        List<String> deps = new ArrayList<>();
 85        String precedingPhase = getPrecedingPhase(phase, project);
 86        if (precedingPhase != null) {
 87            deps.add(precedingPhase);
 88        }
 89        return deps;
 90    }
 91
 92    /**
 93     * Get all goals that belong to a specific phase
 94     */
 95    public List<String> getGoalsForPhase(String phase, Map<String, TargetConfiguration> allTargets) {
 96        List<String> goalsForPhase = new ArrayList<>();
 97
 98        for (Map.Entry<String, TargetConfiguration> entry : allTargets.entrySet()) {
 99            String targetName = entry.getKey();
100            TargetConfiguration target = entry.getValue();
101
102            if (target.getMetadata() != null &&
103                "goal".equals(target.getMetadata().getType()) &&
104                phase.equals(target.getMetadata().getPhase())) {
105                goalsForPhase.add(targetName);
106            }
107        }
108
109        return goalsForPhase;
110    }
111
112
113
114
115    /**
116     * Get all goals from preceding phases in the Maven lifecycle.
117     * This ensures goals depend on other goals, not phases.
118     * Optimized to use pre-computed execution plan analysis.
119     */
120    public List<String> getPrecedingGoalsInLifecycle(MavenProject project, String currentPhase) {
121        List<String> precedingGoals = new ArrayList<>();
122
123        if (currentPhase == null || currentPhase.isEmpty()) {
124            return precedingGoals;
125        }
126
127        // Use pre-computed lifecycle information for better performance
128        org.apache.maven.lifecycle.Lifecycle lifecycle = executionPlanAnalysisService.getLifecycleForPhase(currentPhase);
129        if (lifecycle == null || lifecycle.getPhases() == null) {
130            return precedingGoals;
131        }
132
133        List<String> lifecyclePhases = lifecycle.getPhases();
134        int currentPhaseIndex = lifecyclePhases.indexOf(currentPhase);
135
136        if (currentPhaseIndex <= 0) {
137            return precedingGoals; // No preceding phases
138        }
139
140        // Get all goals from all preceding phases using project's plugin configuration
141        for (int i = 0; i < currentPhaseIndex; i++) {
142            String precedingPhase = lifecyclePhases.get(i);
143            List<String> phaseGoals = getCommonGoalsForPhase(precedingPhase, project);
144            precedingGoals.addAll(phaseGoals);
145        }
146
147        if (verbose && !precedingGoals.isEmpty()) {
148            log.debug("Found " + precedingGoals.size() + " preceding goals for phase '" + currentPhase + "': " + precedingGoals);
149        }
150
151        return precedingGoals;
152    }
