154    /**
155     * Get goals for a Maven lifecycle phase by looking up from the project's plugin configuration.
156     * This finds all goals from the project that are bound to the specified phase.
157     */
158    private List<String> getCommonGoalsForPhase(String phase, MavenProject project) {
159        List<String> goals = new ArrayList<>();
160        
161        if (project == null || project.getBuildPlugins() == null) {
162            return goals;
163        }
164        
165        // Go through all plugins and their executions to find goals bound to this phase
166        for (org.apache.maven.model.Plugin plugin : project.getBuildPlugins()) {
167            if (plugin.getExecutions() != null) {
168                for (org.apache.maven.model.PluginExecution execution : plugin.getExecutions()) {
169                    String executionPhase = execution.getPhase();
