 31/**
 32 * Refactored Maven plugin that analyzes Maven projects for Nx integration.
 33 * This version delegates complex logic to specialized service classes.
 34 */
 35@Mojo(name = "analyze", aggregator = true, requiresDependencyResolution = ResolutionScope.NONE)
 36public class NxAnalyzerMojo extends AbstractMojo {
 37    
 38    @Parameter(defaultValue = "${session}", readonly = true, required = true)
 39    private MavenSession session;
 40    
 41    @Parameter(defaultValue = "${reactorProjects}", readonly = true, required = true)
 42    private List<MavenProject> reactorProjects;
 43    
 44    @Parameter(property = "nx.outputFile")
 45    private String outputFile;
 46    
 47    @Parameter(property = "nx.verbose", defaultValue = "false")
 48    private String verboseStr;
 49    
 50    @Component
 51    private LifecycleExecutor lifecycleExecutor;
 52    
 53    @Component
 54    private DefaultLifecycles defaultLifecycles;
 55    
 56    // Services for delegating complex operations
 57    private ExecutionPlanAnalysisService executionPlanAnalysisService;
 58    private TargetGenerationService targetGenerationService;
 59    private TargetGroupService targetGroupService;
 60    private TargetDependencyService targetDependencyService;
 61    
 62    @Override
 63    public void execute() throws MojoExecutionException, MojoFailureException {
 64        long overallStartTime = System.currentTimeMillis();
 65        
 66        getLog().info(" Starting Maven analysis for " + reactorProjects.size() + " projects...");
 67        
 68        try {
 69            // Phase 1: Initialize services
 70            long initStart = System.currentTimeMillis();
 71            initializeServices();
 72            long initDuration = System.currentTimeMillis() - initStart;
 73            getLog().info("  Service initialization completed in " + initDuration + "ms");
 74            
 75            logBasicInfo();
 76            
 77            // Phase 2: Perform Maven analysis
 78            long analysisStart = System.currentTimeMillis();
 79            getLog().info(" Starting Maven workspace analysis...");
 80            Map<String, Object> result = performAnalysis();
 81            long analysisDuration = System.currentTimeMillis() - analysisStart;
 82            getLog().info("  Maven workspace analysis completed in " + analysisDuration + "ms (" + 
 83                         String.format("%.2f", analysisDuration / 1000.0) + "s)");
 84            
 85            // Phase 3: Write output
 86            long writeStart = System.currentTimeMillis();
 87            String outputPath = determineOutputPath();
 88            writeResult(result, outputPath);
 89            long writeDuration = System.currentTimeMillis() - writeStart;
 90            getLog().info("  Output file writing completed in " + writeDuration + "ms");
 91            
 92            logCompletion(overallStartTime, outputPath, analysisDuration);
 93            
 94        } catch (Exception e) {
 95            long failureDuration = System.currentTimeMillis() - overallStartTime;
 96            getLog().error(" Analysis failed after " + failureDuration + "ms: " + e.getMessage());
 97            throw new MojoExecutionException("Analysis failed: " + e.getMessage(), e);
 98        }
 99    }
100    
101    private void initializeServices() {
102        this.executionPlanAnalysisService = new ExecutionPlanAnalysisService(getLog(), isVerbose(), lifecycleExecutor, session, defaultLifecycles);
103        
104        // Pre-analyze all projects upfront to avoid performance bottlenecks during dependency analysis
105        executionPlanAnalysisService.preAnalyzeAllProjects(reactorProjects);
106        
107        this.targetGenerationService = new TargetGenerationService(getLog(), isVerbose(), session, executionPlanAnalysisService);
108        this.targetGroupService = new TargetGroupService(executionPlanAnalysisService);
109        this.targetDependencyService = new TargetDependencyService(getLog(), isVerbose(), executionPlanAnalysisService);
110    }
111    
112    private void logBasicInfo() {
113        if (isVerbose()) {
114            getLog().info("Verbose mode enabled");
115        }
116        
117        getLog().info("Root directory: " + session.getExecutionRootDirectory());
118        if (!reactorProjects.isEmpty()) {
119            MavenProject first = reactorProjects.get(0);
120            MavenProject last = reactorProjects.get(reactorProjects.size() - 1);
121            getLog().info("First project: " + first.getGroupId() + ":" + first.getArtifactId());
122            getLog().info("Last project: " + last.getGroupId() + ":" + last.getArtifactId());
123        }
124    }
125    
126    private Map<String, Object> performAnalysis() {
127        File workspaceRoot = new File(session.getExecutionRootDirectory());
128        
129        // Phase 1: Calculate project dependencies
130        long depCalcStart = System.currentTimeMillis();
131        Map<MavenProject, List<MavenProject>> projectDependencies = calculateProjectDependencies();
132        long depCalcDuration = System.currentTimeMillis() - depCalcStart;
133        getLog().info("  Project dependency calculation completed in " + depCalcDuration + "ms");
134        
135        // Phase 2: Generate targets and groups for each project
136        long targetGenStart = System.currentTimeMillis();
137        Map<MavenProject, Map<String, TargetConfiguration>> projectTargets = new LinkedHashMap<>();
138        Map<MavenProject, Map<String, TargetGroup>> projectTargetGroups = new LinkedHashMap<>();
139        
140        int processedProjects = 0;
141        for (MavenProject project : reactorProjects) {
142            long projectStart = System.currentTimeMillis();
143            try {
144                // Get actual project dependencies for this project (not all reactor projects)
145                List<MavenProject> actualDependencies = projectDependencies.getOrDefault(project, new ArrayList<>());
146                
147                // Calculate goal dependencies using only actual dependencies
148                Map<String, List<Object>> goalDependencies = calculateGoalDependencies(project, actualDependencies);
149                
150                // Generate targets using pre-calculated goal dependencies (phase dependencies calculated later)
151                Map<String, TargetConfiguration> targets = targetGenerationService.generateTargets(
152                    project, workspaceRoot, goalDependencies, new LinkedHashMap<>());
153                
154                // Now calculate phase dependencies using the generated targets
155                Map<String, List<Object>> phaseDependencies = calculatePhaseDependencies(project, targets);
156                
157                // Update phase targets with calculated dependencies
158                updatePhaseTargetsWithDependencies(targets, phaseDependencies);
159                projectTargets.put(project, targets);
160                
161                // Generate target groups
162                Map<String, TargetGroup> targetGroups = targetGroupService.generateTargetGroups(project, targets, session);
163                projectTargetGroups.put(project, targetGroups);
164                
165                processedProjects++;
166                long projectDuration = System.currentTimeMillis() - projectStart;
167                
168                if (isVerbose()) {
169                    getLog().info(" Processed " + project.getArtifactId() + " in " + projectDuration + "ms" +
170                                 ": " + targets.size() + " targets, " + targetGroups.size() + " groups, " +
171                                 actualDependencies.size() + " project dependencies");
172                } else if (processedProjects % 100 == 0) {
173                    // Log progress every 100 projects for large workspaces
174                    long avgTimePerProject = (System.currentTimeMillis() - targetGenStart) / processedProjects;
175                    getLog().info(" Processed " + processedProjects + "/" + reactorProjects.size() + 
176                                 " projects (avg " + avgTimePerProject + "ms per project)");
177                }
178                
179            } catch (Exception e) {
180                getLog().error("Error processing project " + project.getArtifactId() + ": " + e.getMessage(), e);
181                // Continue with empty targets and groups
182                projectTargets.put(project, new LinkedHashMap<>());
183                projectTargetGroups.put(project, new LinkedHashMap<>());
184            }
185        }
186        
187        long targetGenDuration = System.currentTimeMillis() - targetGenStart;
188        getLog().info("  Target generation completed in " + targetGenDuration + "ms for " + 
189                     processedProjects + " projects (avg " + (targetGenDuration / processedProjects) + "ms per project)");
190        
191        // Phase 3: Generate Nx-compatible outputs
192        long outputGenStart = System.currentTimeMillis();
193        List<CreateNodesV2Entry> createNodesEntries = CreateNodesResultGenerator.generateCreateNodesV2Results(
194            reactorProjects, workspaceRoot, projectTargets, projectTargetGroups);
195        
196        List<Object[]> createNodesResults = new ArrayList<>();
197        for (CreateNodesV2Entry entry : createNodesEntries) {
198            createNodesResults.add(entry.toArray());
199        }
200        
201        List<RawProjectGraphDependency> createDependencies = CreateDependenciesGenerator.generateCreateDependencies(
202            reactorProjects, workspaceRoot, getLog(), isVerbose());
203        
204        long outputGenDuration = System.currentTimeMillis() - outputGenStart;
205        getLog().info("  Nx output generation completed in " + outputGenDuration + "ms");
206        
207        if (isVerbose()) {
208            getLog().info("Generated " + createDependencies.size() + " workspace dependencies");
209        }
210        
211        Map<String, Object> result = new LinkedHashMap<>();
212        result.put("createNodesResults", createNodesResults);
213        result.put("createDependencies", createDependencies);
214        
215        return result;
216    }
217    
218    /**
219     * Calculate actual project dependencies for all projects in the reactor.
220     * Returns a map from each project to its list of actual Maven dependency projects.
221     */
222    private Map<MavenProject, List<MavenProject>> calculateProjectDependencies() {
223        Map<MavenProject, List<MavenProject>> projectDependencies = new LinkedHashMap<>();
224        
225        // Build artifact mapping for workspace projects
226        Map<String, MavenProject> artifactToProject = new HashMap<>();
227        for (MavenProject project : reactorProjects) {
228            if (project.getGroupId() != null && project.getArtifactId() != null) {
229                String key = MavenUtils.formatProjectKey(project);
230                artifactToProject.put(key, project);
231            }
232        }
233        
234        // Calculate dependencies for each project
235        for (MavenProject project : reactorProjects) {
236            List<MavenProject> dependencies = new ArrayList<>();
237            
238            if (project.getDependencies() != null) {
239                for (org.apache.maven.model.Dependency dep : project.getDependencies()) {
240                    if (dep.getGroupId() != null && dep.getArtifactId() != null) {
241                        String depKey = dep.getGroupId() + ":" + dep.getArtifactId();
242                        
243                        // Check if this dependency refers to another project in workspace
244                        MavenProject targetProject = artifactToProject.get(depKey);
245                        if (targetProject != null && !targetProject.equals(project)) {
246                            dependencies.add(targetProject);
247                        }
248                    }
249                }
250            }
251            
252            projectDependencies.put(project, dependencies);
253            
254            if (isVerbose() && !dependencies.isEmpty()) {
255                getLog().debug("Project " + project.getArtifactId() + " depends on " + 
256                             dependencies.size() + " workspace projects");
257            }
258        }
259        
260        return projectDependencies;
261    }
262
263    /**
264     * Calculate goal dependencies for a project using only actual project dependencies
265     */
266    private Map<String, List<Object>> calculateGoalDependencies(MavenProject project, List<MavenProject> actualDependencies) {
267        Map<String, List<Object>> goalDependencies = new LinkedHashMap<>();
268        
269        if (isVerbose()) {
270            getLog().debug("Calculating goal dependencies for " + project.getArtifactId());
271        }
272        
273        // First pass: collect all potential goal targets
274        Set<String> goalTargets = collectGoalTargets(project);
275        
276        // Calculate dependencies for each goal target
277        for (String targetName : goalTargets) {
278            String goal = ExecutionPlanAnalysisService.extractGoalFromTargetName(targetName);
279            
280            // Try to find execution phase from plugin configuration
281            String executionPhase = findExecutionPhase(project, targetName);
282            
283            List<Object> dependencies = targetDependencyService.calculateGoalDependencies(
284                project, executionPhase, targetName, actualDependencies);
285            goalDependencies.put(targetName, dependencies);
286        }
287        
288        return goalDependencies;
289    }
290    
291    /**
292     * Calculate phase dependencies for a project using generated targets
293     */
294    private Map<String, List<Object>> calculatePhaseDependencies(MavenProject project, Map<String, TargetConfiguration> allTargets) {
295        Map<String, List<Object>> phaseDependencies = new LinkedHashMap<>();
296        
297        // Get all lifecycle phases from all Maven lifecycles (default, clean, site)
298        Set<String> allPhases = executionPlanAnalysisService.getAllLifecyclePhases();
299        
300        if (isVerbose()) {
301            getLog().debug("Calculating dependencies for " + allPhases.size() + " lifecycle phases: " + allPhases);
302        }
303        
304        for (String phase : allPhases) {
305            List<Object> dependencies = targetDependencyService.calculatePhaseDependencies(
306                phase, allTargets, project, reactorProjects);
307            phaseDependencies.put(phase, dependencies);
308        }
