313    /**
314     * Update phase targets with calculated dependencies
315     */
316    private void updatePhaseTargetsWithDependencies(Map<String, TargetConfiguration> targets, 
317                                                   Map<String, List<Object>> phaseDependencies) {
318        for (Map.Entry<String, List<Object>> entry : phaseDependencies.entrySet()) {
319            String phase = entry.getKey();
