 22/**
 23 * Service responsible for generating Nx targets from Maven project configuration.
 24 * Handles both phase targets and plugin goal targets.
 25 */
 26public class TargetGenerationService {
 27
 28    private final Log log;
 29    private final boolean verbose;
 30    private final MavenSession session;
 31    private final ExecutionPlanAnalysisService executionPlanAnalysisService;
 32    private final DynamicGoalAnalysisService dynamicGoalAnalysis;
 33
 34    public TargetGenerationService(Log log, boolean verbose, MavenSession session, ExecutionPlanAnalysisService executionPlanAnalysisService) {
 35        this.log = log;
 36        this.verbose = verbose;
 37        this.session = session;
 38        this.executionPlanAnalysisService = executionPlanAnalysisService;
 39        this.dynamicGoalAnalysis = new DynamicGoalAnalysisService(session, executionPlanAnalysisService, 
 40                                                                 executionPlanAnalysisService.getLifecycleExecutor(), 
 41                                                                 executionPlanAnalysisService.getDefaultLifecycles(), log, verbose);
 42    }
 43
 44    /**
 45     * Generate all targets for a Maven project
 46     * @param project The Maven project to generate targets for
 47     * @param workspaceRoot The workspace root directory
 48     * @param goalDependencies Pre-calculated goal dependencies
 49     * @param phaseDependencies Pre-calculated phase dependencies
 50     * @return Map of target name to target configuration
 51     * @throws IllegalArgumentException if project is null
 52     */
 53    public Map<String, TargetConfiguration> generateTargets(MavenProject project, File workspaceRoot,
 54                                                           Map<String, List<Object>> goalDependencies,
 55                                                           Map<String, List<Object>> phaseDependencies) {
 56        if (project == null) {
 57            throw new IllegalArgumentException("Project cannot be null");
 58        }
 59
 60        Map<String, TargetConfiguration> targets = new LinkedHashMap<>();
 61
 62        try {
 63            // Generate plugin goal targets first
 64            targets.putAll(generatePluginGoalTargets(project, workspaceRoot, goalDependencies));
 65
 66            // Generate Maven lifecycle phase targets
 67            targets.putAll(generatePhaseTargets(project, workspaceRoot, targets, phaseDependencies));
 68
 69        } catch (Exception e) {
 70            if (log != null) {
 71                log.warn("Error generating targets for project " + project.getArtifactId() + ": " + e.getMessage(), e);
 72            }
 73            // Return empty targets rather than failing completely
 74        }
 75
 76        return targets;
 77    }
 78
 79    /**
 80     * Generate targets for Maven lifecycle phases
 81     * Phases are entry points that depend on individual goal targets
 82     */
 83    public Map<String, TargetConfiguration> generatePhaseTargets(MavenProject project, File workspaceRoot,
 84                                                                 Map<String, TargetConfiguration> allTargets,
 85                                                                 Map<String, List<Object>> phaseDependencies) {
 86        Map<String, TargetConfiguration> phaseTargets = new LinkedHashMap<>();
 87
 88        // Get all phases from all 3 Maven lifecycles (default, clean, site)
 89        Set<String> applicablePhases = executionPlanAnalysisService.getAllLifecyclePhases();
 90
 91        for (String phase : applicablePhases) {
 92            // Phase targets depend on individual goal targets (not batch execution)
 93            List<String> goalsToComplete = executionPlanAnalysisService.getGoalsCompletedByPhase(project, phase);
 94            
 95            TargetConfiguration target;
 96            if (!goalsToComplete.isEmpty()) {
 97                // Phase targets are just entry points - they depend on individual goal targets
 98                target = new TargetConfiguration("nx:noop");
 99                target.setOptions(new LinkedHashMap<>());
100                
101                // Convert goal names to target names and set as dependencies
102                List<Object> goalTargetDependencies = new ArrayList<>();
103                for (String goalName : goalsToComplete) {
104                    // goalName is in format "pluginArtifactId:goalName" 
105                    String targetName = getTargetNameFromGoal(goalName);
106                    if (allTargets.containsKey(targetName)) {
107                        goalTargetDependencies.add(targetName);
108                    } else if (verbose) {
109                        log.debug("Warning: Goal target '" + targetName + "' not found for phase '" + phase + "'");
110                    }
111                }
112                target.setDependsOn(goalTargetDependencies);
113                
114            } else {
115                // No goals for this phase - make it a no-op
116                target = new TargetConfiguration("nx:noop");
117                target.setOptions(new LinkedHashMap<>());
118                target.setDependsOn(new ArrayList<>());
119            }
120
121            // Configure inputs/outputs (minimal since phase is just orchestration)
122            target.setInputs(new ArrayList<>());
123            target.setOutputs(new ArrayList<>());
124
125            // Add metadata
126            String description = "Maven lifecycle phase: " + phase + " (depends on " + target.getDependsOn().size() + " goals)";
127            TargetMetadata metadata = new TargetMetadata("phase", description);
128            metadata.setPhase(phase);
129            metadata.setPlugin("org.apache.maven:maven-core");
130            metadata.setTechnologies(Arrays.asList("maven"));
131            target.setMetadata(metadata);
132
133            phaseTargets.put(phase, target);
134            
135            if (verbose) {
136                log.debug("Generated phase target '" + phase + "' depending on goals: " + target.getDependsOn());
137            }
138        }
139
140        return phaseTargets;
141    }
142    
143    /**
144     * Convert a full goal name (plugin:goal) to target name (plugin:goal format)
145     */
146    private String getTargetNameFromGoal(String goalName) {
147        // goalName is already in format "artifactId:goal" or "groupId:artifactId:goal"
148        // Extract just the artifactId:goal part for target name
149        String[] parts = goalName.split(":");
150        if (parts.length >= 2) {
151            String artifactId = parts[parts.length - 2]; // Second to last part is artifactId
152            String goal = parts[parts.length - 1];       // Last part is goal
153            return ExecutionPlanAnalysisService.getTargetName(artifactId, goal);
154        }
155        return goalName; // Fallback to original name
156    }
