158    /**
159     * Generate targets for plugin goals
160     */
161    public Map<String, TargetConfiguration> generatePluginGoalTargets(MavenProject project, File workspaceRoot,
162                                                                      Map<String, List<Object>> goalDependencies) {
163        Map<String, TargetConfiguration> goalTargets = new LinkedHashMap<>();
164
165        if (verbose) {
166            log.debug("Generating plugin goal targets for project: " + project.getArtifactId());
167        }
168
169        final String projectRootToken = "{projectRoot}";
170        final String actualProjectPath;
171
172        if (workspaceRoot != null) {
173            String relativePath = NxPathUtils.getRelativePath(workspaceRoot, project.getBasedir());
174            actualProjectPath = relativePath.isEmpty() ? "." : relativePath;
175        } else {
176            actualProjectPath = "{projectRoot}";
177        }
178
179        if (project.getBuildPlugins() != null) {
180            project.getBuildPlugins().forEach(plugin -> {
181                // Process actual executions from effective POM
182                if (plugin.getExecutions() != null) {
183                    plugin.getExecutions().forEach(execution -> {
184                        if (execution.getGoals() != null) {
185                            execution.getGoals().forEach(goal -> {
186                                String targetName = ExecutionPlanAnalysisService.getTargetName(plugin.getArtifactId(), goal);
187
188                                if (!goalTargets.containsKey(targetName)) {
189                                    TargetConfiguration target = createGoalTarget(plugin, goal, execution, projectRootToken, actualProjectPath, goalDependencies.getOrDefault(targetName, new ArrayList<Object>()), project);
190                                    goalTargets.put(targetName, target);
191                                }
192                            });
193                        }
194                    });
195                }
196
197                // Add common goals for well-known plugins
198                addCommonGoalsForPlugin(plugin, goalTargets, projectRootToken, actualProjectPath, goalDependencies, project);
199            });
200        }
201
202        return goalTargets;
203    }
204
205    /**
206     * Create a target configuration for a specific Maven goal
207     */
208    public TargetConfiguration createGoalTarget(Plugin plugin, String goal, PluginExecution execution,
209                                               String projectRootToken, String actualProjectPath,
210                                               List<Object> dependencies, MavenProject project) {
211        String pluginKey = plugin.getGroupId() + ":" + plugin.getArtifactId();
212
213
214        TargetConfiguration target = new TargetConfiguration("@nx-quarkus/maven-plugin:maven-batch");
215        
216        if (verbose) {
217            log.info("DEBUG: Creating goal target with TypeScript executor: " + pluginKey + ":" + goal);
218        }
219
220        Map<String, Object> options = new LinkedHashMap<>();
221        // Use TypeScript batch executor for single goal execution with session context
222        List<String> goals = new ArrayList<>();
223        goals.add(pluginKey + ":" + goal);
224        options.put("goals", goals);
225        options.put("projectRoot", actualProjectPath);
226        options.put("verbose", verbose);
227        options.put("mavenPluginPath", "maven-plugin");
228        options.put("failOnError", true);
229        target.setOptions(options);
230
231        // Smart inputs/outputs based on goal using Maven APIs
232        List<String> inputs = getSmartInputsForGoal(goal, project, projectRootToken);
233        target.setInputs(inputs);
234
235        List<String> outputs = executionPlanAnalysisService.getGoalOutputs(goal, projectRootToken, project);
236        target.setOutputs(outputs);
237
238        // Use pre-calculated dependencies
239        target.setDependsOn(dependencies);
240
241        // Metadata
242        TargetMetadata metadata = new TargetMetadata("goal", generateGoalDescription(plugin.getArtifactId(), goal));
243        metadata.setPlugin(pluginKey);
244        metadata.setGoal(goal);
245        metadata.setExecutionId(execution.getId());
246        String executionPhase = execution.getPhase();
247        if (executionPhase != null && !executionPhase.isEmpty() && !executionPhase.startsWith("${")) {
248            metadata.setPhase(executionPhase);
249        } else {
250            metadata.setPhase(executionPlanAnalysisService.findPhaseForGoal(project, goal));
251        }
252        metadata.setTechnologies(Arrays.asList("maven"));
253        target.setMetadata(metadata);
254
255        return target;
256    }
