258    private void addCommonGoalsForPlugin(Plugin plugin, Map<String, TargetConfiguration> goalTargets,
259                                        String projectRootToken, String actualProjectPath,
260                                        Map<String, List<Object>> goalDependencies, MavenProject project) {
261        String artifactId = plugin.getArtifactId();
262        List<String> commonGoals = ExecutionPlanAnalysisService.getCommonGoalsForPlugin(artifactId);
263
264        for (String goal : commonGoals) {
265            String targetName = ExecutionPlanAnalysisService.getTargetName(artifactId, goal);
266            if (!goalTargets.containsKey(targetName)) {
267                TargetConfiguration target = createSimpleGoalTarget(plugin, goal, projectRootToken, actualProjectPath, goalDependencies.getOrDefault(targetName, new ArrayList<Object>()), project);
268                goalTargets.put(targetName, target);
269            }
270        }
271    }
