313
314    // Helper methods
315
316    /**
317     * Get smart inputs for a Maven goal using dynamic Maven API analysis
318     * instead of hardcoded patterns. This uses actual Maven project configuration,
319     * plugin analysis, and lifecycle phase information.
320     */
321    private List<String> getSmartInputsForGoal(String goal, MavenProject project, String projectRootToken) {
322        List<String> inputs = new ArrayList<>();
323        
324        // Always include POM
325        inputs.add(projectRootToken + "/pom.xml");
326        
327        // Get dynamic analysis of goal behavior
328        GoalBehavior behavior = dynamicGoalAnalysis.analyzeGoal(goal, project);
329        
330        if (behavior.processesSources()) {
331            // Use actual source directories from Maven configuration
332            List<String> sourcePaths = behavior.getSourcePaths();
333            if (sourcePaths.isEmpty()) {
334                // Fallback to project defaults
335                sourcePaths = project.getCompileSourceRoots();
336            }
337            
338            for (String sourcePath : sourcePaths) {
339                String relativePath = getRelativePathFromProject(sourcePath, project);
340                if (relativePath != null && !relativePath.isEmpty()) {
341                    inputs.add(projectRootToken + "/" + relativePath + "/**/*");
342                }
343            }
344            
345            // Add test sources for test-related goals
346            if (behavior.isTestRelated()) {
347                for (String testSourceRoot : project.getTestCompileSourceRoots()) {
348                    String relativePath = getRelativePathFromProject(testSourceRoot, project);
349                    if (relativePath != null && !relativePath.isEmpty()) {
350                        inputs.add(projectRootToken + "/" + relativePath + "/**/*");
351                    }
352                }
353            }
354        }
355        
356        if (behavior.needsResources()) {
357            // Use actual resource directories from Maven configuration
358            List<String> resourcePaths = behavior.getResourcePaths();
359            if (resourcePaths.isEmpty()) {
360                // Fallback to project defaults
361                if (project.getBuild() != null && project.getBuild().getResources() != null) {
362                    for (Resource resource : project.getBuild().getResources()) {
363                        if (resource.getDirectory() != null) {
364                            resourcePaths.add(resource.getDirectory());
365                        }
366                    }
367                }
368            }
369            
370            for (String resourcePath : resourcePaths) {
371                String relativePath = getRelativePathFromProject(resourcePath, project);
372                if (relativePath != null && !relativePath.isEmpty()) {
373                    inputs.add(projectRootToken + "/" + relativePath + "/**/*");
374                }
375            }
376            
377            // Test resources for test-related goals
378            if (behavior.isTestRelated()) {
379                if (project.getBuild() != null && project.getBuild().getTestResources() != null) {
380                    for (Resource resource : project.getBuild().getTestResources()) {
381                        if (resource.getDirectory() != null) {
382                            String relativePath = getRelativePathFromProject(resource.getDirectory(), project);
383                            if (relativePath != null && !relativePath.isEmpty()) {
384                                inputs.add(projectRootToken + "/" + relativePath + "/**/*");
385                            }
386                        }
387                    }
388                }
389            }
390        }
391        
392        return inputs;
393    }
394
395    // Removed hardcoded goal classification methods:
396    // - isSourceProcessingGoal() - replaced by dynamicGoalAnalysis.analyzeGoal()
397    // - isTestGoal() - replaced by GoalBehavior.isTestRelated() 
398    // - needsResources() - replaced by GoalBehavior.needsResources()
399    // 
400    // These methods used hardcoded string patterns and have been replaced with
401    // dynamic Maven API-based analysis that uses actual plugin configuration,
402    // lifecycle phases, and MojoExecution information.
403    
404    /**
405     * Convert an absolute or relative path from Maven configuration to a relative path
406     * from the project base directory.
407     */
408    private String getRelativePathFromProject(String pathString, MavenProject project) {
409        if (pathString == null || pathString.trim().isEmpty()) {
410            return null;
411        }
412        
413        try {
414            Path projectBase = project.getBasedir().toPath().toAbsolutePath();
415            Path targetPath = Paths.get(pathString);
416            
417            if (targetPath.isAbsolute()) {
418                // Convert absolute path to relative
419                if (targetPath.startsWith(projectBase)) {
420                    return projectBase.relativize(targetPath).toString().replace('\\', '/');
421                } else {
422                    // Path is outside project - skip it
423                    if (verbose && log != null) {
424                        log.debug("Skipping path outside project: " + pathString);
425                    }
