  1import { describe, it, expect, beforeAll, afterAll } from 'vitest';
  2import { execSync } from 'child_process';
  3import { readFileSync, existsSync, rmSync } from 'fs';
  4
  5const TIMEOUT = 120000; // 2 minutes
  6
  7describe('Maven Plugin E2E Smoke Tests', () => {
  8  // Generate unique test ID for this test run
  9  const testId = Math.random().toString(36).substring(7);
 10  const sharedGraphFile = `/tmp/nx-e2e-${testId}-shared-graph.json`;
 11  let projectGraph: any;
 12
 13  beforeAll(async () => {
 14    // Maven compilation and Nx reset are handled by global setup
 15    // Just generate the shared graph file once for all tests
 16    console.log(' Generating shared project graph...');
 17    const graphGenStart = Date.now();
 18    
 19    execSync(`npx nx graph --file ${sharedGraphFile} --verbose`, { stdio: 'inherit' });
 20    
 21    const graphGenDuration = Date.now() - graphGenStart;
 22    const graphContent = readFileSync(sharedGraphFile, 'utf8');
 23    projectGraph = JSON.parse(graphContent);
 24    
 25    console.log(` Shared graph generated with ${Object.keys(projectGraph.graph.nodes).length} nodes`);
 26    console.log(`  Maven analysis completed in ${graphGenDuration}ms (${(graphGenDuration / 1000).toFixed(2)}s)`);
 27  }, TIMEOUT);
 28
 29  afterAll(() => {
 30    // Cleanup this test run's temporary files
 31    try {
 32      rmSync(`/tmp/nx-e2e-${testId}-*`, { recursive: true, force: true });
 33    } catch (error) {
 34      // Ignore cleanup errors
 35    }
 36  });
 37
 38  describe('Basic Plugin Functionality', () => {
 39    it('should successfully run nx show projects', () => {
 40      const output = execSync('npx nx show projects', { encoding: 'utf8' });
 41      expect(output).toBeTruthy();
 42      expect(output.split('\n').filter(line => line.trim()).length).toBeGreaterThan(0);
 43    }, TIMEOUT);
 44
 45    it('should handle nx install maven-plugin command', () => {
 46      // This command might fail, but we test that it doesn't crash
 47      try {
 48        const output = execSync('npx nx install maven-plugin', { encoding: 'utf8', stdio: 'pipe' });
 49        console.log(' nx install maven-plugin succeeded');
 50      } catch (error: any) {
 51        console.log(' nx install maven-plugin failed (expected):', error.message);
 52        // We expect this might fail, so we just ensure it doesn't crash completely
 53        expect(error.status).toBeDefined();
 54      }
 55    }, TIMEOUT);
 56  });
 57
 58  describe('Project Graph Generation', () => {
 59    it('should have valid project graph structure', () => {
 60      expect(projectGraph).toHaveProperty('graph');
 61      expect(projectGraph.graph).toHaveProperty('nodes');
 62      expect(projectGraph.graph).toHaveProperty('dependencies');
 63      expect(Object.keys(projectGraph.graph.nodes).length).toBeGreaterThan(0);
 64    });
 65
 66    it('should have consistent graph structure across multiple generations', () => {
 67      // Generate graph 2 more times to verify consistency
 68      const additionalGraphs = [];
 69
 70      for (let i = 0; i < 2; i++) {
 71        const graphFile = `/tmp/nx-e2e-${testId}-consistency-${i}.json`;
 72        execSync(`npx nx graph --file ${graphFile}`, { stdio: 'pipe' });
 73
 74        const graphContent = readFileSync(graphFile, 'utf8');
 75        additionalGraphs.push(JSON.parse(graphContent));
 76      }
 77
 78      // All graphs should have the same number of nodes as the shared graph
 79      const expectedNodeCount = Object.keys(projectGraph.graph.nodes).length;
 80      expect(expectedNodeCount).toBeGreaterThan(0);
 81
 82      additionalGraphs.forEach((graph, index) => {
 83        expect(Object.keys(graph.graph.nodes).length).toBe(expectedNodeCount);
 84      });
 85    }, TIMEOUT);
 86  });
 87
 88  describe('Snapshot Testing', () => {
 89
 90    it('should snapshot quarkus-core project configuration', () => {
 91      // Find quarkus-core project
 92      const coreProject = projectGraph.graph.nodes['io.quarkus:quarkus-core'];
 93
 94      expect(coreProject).toMatchSnapshot();
 95    }, TIMEOUT);
 96
 97    it('should snapshot quarkus-core project dependencies', () => {
 98      // Find quarkus-core project
 99      const coreDependencies = projectGraph.graph.dependencies['io.quarkus:quarkus-core'];
100
101      expect(coreDependencies).toMatchSnapshot();
102    }, TIMEOUT);
103
104    it('should snapshot key Maven projects structure', () => {
105      // Find a few key Maven projects to snapshot
106      const keyProjects = [
107        'quarkus-parent',
108        'quarkus-core',
109        'arc-parent',
110        'arc'
111      ];
112
113      const foundProjects = keyProjects.map(projectKey => {
114        const projectEntry = Object.entries(projectGraph.graph.nodes).find(([name, node]: [string, any]) =>
115          name.includes(projectKey) || node.data?.name?.includes(projectKey)
116        );
117
118        if (projectEntry) {
119          const [projectName, projectData] = projectEntry;
120          return {
121            key: projectKey,
122            name: projectData.data?.name || projectName,
123            type: projectData.type,
124            hasTargets: !!projectData.data?.targets,
125            targetCount: projectData.data?.targets ? Object.keys(projectData.data.targets).length : 0,
126            hasDependencies: !!(projectGraph.graph.dependencies[projectName]?.length > 0)
127          };
128        }
129        return null;
130      }).filter(Boolean);
131
132      expect(foundProjects.length).toBeGreaterThan(0);
133      expect(foundProjects).toMatchSnapshot();
134    }, TIMEOUT);
135
136    it('should snapshot overall project statistics', () => {
137      const stats = {
138        totalProjects: Object.keys(projectGraph.graph.nodes).length,
139        projectsWithTargets: Object.values(projectGraph.graph.nodes).filter((node: any) =>
140          node.data?.targets && Object.keys(node.data.targets).length > 0
141        ).length,
142        projectsWithDependencies: Object.keys(projectGraph.graph.dependencies).filter(key =>
143          projectGraph.graph.dependencies[key]?.length > 0
144        ).length,
145        hasValidGraph: !!projectGraph.graph,
146        hasNodes: !!projectGraph.graph.nodes,
147        hasDependencies: !!projectGraph.graph.dependencies
148      };
149
150      expect(stats).toMatchSnapshot();
151    }, TIMEOUT);
152  });
153
154  describe('Advanced Plugin Features', () => {
155    it('should support verbose project listing', () => {
156      const output = execSync('npx nx show projects --verbose', { encoding: 'utf8' });
157      expect(output).toBeTruthy();
158      expect(output.length).toBeGreaterThan(0);
159    }, TIMEOUT);
160
161    it('should handle project details queries', () => {
162      // Get a project name first
163      const projectsOutput = execSync('npx nx show projects', { encoding: 'utf8' });
164      const projects = projectsOutput.split('\n').filter(line => line.trim());
165
166      if (projects.length > 0) {
167        const firstProject = projects[0].trim();
168
169        try {
170          const detailsOutput = execSync(`npx nx show project ${firstProject}`, {
171            encoding: 'utf8',
172            stdio: 'pipe'
173          });
174          expect(detailsOutput).toBeTruthy();
175          console.log(` Successfully queried details for project: ${firstProject}`);
176        } catch (error: any) {
177          console.log(` Project details query failed for ${firstProject}:`, error.message);
178          // This might be expected if the command format is different
179        }
180      }
181    }, TIMEOUT);
182
183    it('should not crash with invalid Maven executable', () => {
184      // Test resilience by temporarily breaking the Maven path
185      const originalPath = process.env.PATH;
186
187      try {
188        // Remove Maven from PATH temporarily
189        process.env.PATH = '/usr/bin:/bin';
190
191        // The plugin should handle this gracefully
192        const output = execSync('npx nx show projects 2>&1 || echo "graceful-failure"', {
193          encoding: 'utf8',
194          stdio: 'pipe'
195        });
196
197        // Should either work or fail gracefully
198        expect(output).toBeTruthy();
199        console.log(' Plugin handles Maven unavailability gracefully');
200      } finally {
201        // Restore original PATH
202        process.env.PATH = originalPath;
203      }
204    }, TIMEOUT);
205  });
206
207  describe('Performance and Reliability', () => {
208    it('should complete operations within reasonable time', () => {
209      const start = Date.now();
210
211      execSync('npx nx show projects', { stdio: 'pipe' });
212
213      const duration = Date.now() - start;
214      expect(duration).toBeLessThan(7500); // Should complete within 30 seconds
215
216      console.log(` nx show projects completed in ${duration}ms`);
217    });
218  });
