// Use a custom thread pool to avoid resource exhaustion issues
int numThreads = Math.min(Runtime.getRuntime().availableProcessors(), reactorProjects.size());
ForkJoinPool customThreadPool = new ForkJoinPool(numThreads);

if (verbose) {
    log.info(" PARALLEL pre-analyzing execution plans for " + reactorProjects.size() + " projects using " + 
             numThreads + " threads...");
}

try {
    // Use custom thread pool for concurrent execution plan analysis
    customThreadPool.submit(() -> reactorProjects.parallelStream().forEach(project -> {
        String projectKey = MavenUtils.formatProjectKey(project);
        if (!analysisCache.containsKey(projectKey)) {
            analysisCache.put(projectKey, analyzeProject(project));
        }
    })).get(); // Wait for completion
} catch (Exception e) {
    log.error("Parallel pre-analysis failed: " + e.getMessage(), e);
    throw new RuntimeException("Failed to pre-analyze projects in parallel", e);
} finally {
    // Always clean up the thread pool to prevent resource leaks
    customThreadPool.shutdown();
    try {
        if (!customThreadPool.awaitTermination(10, java.util.concurrent.TimeUnit.SECONDS)) {
            log.warn("Pre-analysis thread pool did not terminate gracefully, forcing shutdown");
            customThreadPool.shutdownNow();
        }
    } catch (InterruptedException e) {
        log.warn("Pre-analysis thread pool shutdown interrupted");
        customThreadPool.shutdownNow();
        Thread.currentThread().interrupt();
    }
}

long duration = System.currentTimeMillis() - startTime;
if (verbose) {
    log.info(" PARALLEL pre-analysis completed for " + reactorProjects.size() + " projects in " + duration + "ms" +
             " (avg " + (duration / reactorProjects.size()) + "ms per project)");
}

