 17/**
 18 * Service responsible for calculating target dependencies in Maven projects.
 19 * Handles phase dependencies, cross-module dependencies, and goal dependencies.
 20 */
 21public class TargetDependencyService {
 22    
 23    private final Log log;
 24    private final boolean verbose;
 25    private final MavenSession session;
 26
 27    public TargetDependencyService(Log log, boolean verbose, MavenSession session) {
 28        this.log = log;
 29        this.verbose = verbose;
 30        this.session = session;
 31    }
 32
 33    /**
 34     * Calculate dependencies for a goal target
 35     */
 36    public List<String> calculateGoalDependencies(MavenProject project, String executionPhase, 
 37                                                  String targetName, List<MavenProject> reactorProjects) {
 38        List<String> dependsOn = new ArrayList<>();
 39        
 40        
 41        String effectivePhase = executionPhase;
 42        if (effectivePhase == null || effectivePhase.isEmpty() || effectivePhase.startsWith("${")) {
 43            effectivePhase = inferPhaseFromGoal(MavenUtils.extractGoalFromTargetName(targetName));
 44        }
 45        
 46        // Fallback: map common goals to known phases if inference fails
 47        if (effectivePhase == null || effectivePhase.isEmpty()) {
 48            String goal = MavenUtils.extractGoalFromTargetName(targetName);
 49            effectivePhase = mapGoalToPhase(goal);
 50        }
 51        
 52        if (effectivePhase != null && !effectivePhase.isEmpty()) {
 53            // Add dependency on preceding phase (but don't fail if this doesn't work)
 54            try {
 55                String precedingPhase = getPrecedingPhase(effectivePhase);
 56                if (precedingPhase != null && !precedingPhase.isEmpty()) {
 57                    dependsOn.add(precedingPhase);
 58                }
 59            } catch (Exception e) {
 60                // Continue even if preceding phase detection fails
 61            }
 62            
 63            // ALWAYS add cross-module dependencies using Nx ^ syntax
 64            // Goals depend on their phase across all dependent projects
 65            dependsOn.add("^" + effectivePhase);
 66        }
 67        
 68        
 69        return dependsOn;
 70    }
 71
 72    /**
 73     * Calculate dependencies for a phase target
 74     */
 75    public List<String> calculatePhaseDependencies(String phase, Map<String, TargetConfiguration> allTargets, 
 76                                                   MavenProject project, List<MavenProject> reactorProjects) {
 77        List<String> dependsOn = new ArrayList<>();
 78        
 79        // Add dependency on preceding phase
 80        List<String> phaseDependencies = getPhaseDependencies(phase);
 81        dependsOn.addAll(phaseDependencies);
 82        
 83        // Add dependencies on all goals that belong to this phase
 84        List<String> goalsForPhase = getGoalsForPhase(phase, allTargets);
 85        dependsOn.addAll(goalsForPhase);
 86        
 87        // Add cross-module dependencies using Nx ^ syntax
 88        dependsOn.add("^" + phase);
 89        
 90        return dependsOn;
 91    }
 92
 93    /**
 94     * Get phase dependencies (preceding phases)
 95     */
 96    public List<String> getPhaseDependencies(String phase) {
 97        List<String> deps = new ArrayList<>();
 98        String precedingPhase = getPrecedingPhase(phase);
 99        if (precedingPhase != null) {
100            deps.add(precedingPhase);
101        }
102        return deps;
103    }
104
105    /**
106     * Get all goals that belong to a specific phase
107     */
108    public List<String> getGoalsForPhase(String phase, Map<String, TargetConfiguration> allTargets) {
109        List<String> goalsForPhase = new ArrayList<>();
110        
111        for (Map.Entry<String, TargetConfiguration> entry : allTargets.entrySet()) {
112            String targetName = entry.getKey();
113            TargetConfiguration target = entry.getValue();
114            
115            if (target.getMetadata() != null && 
116                "goal".equals(target.getMetadata().getType()) &&
117                phase.equals(target.getMetadata().getPhase())) {
118                goalsForPhase.add(targetName);
119            }
120        }
121        
122        return goalsForPhase;
123    }
124
125
126
127    /**
128     * Get Maven execution plan using the lifecycle executor
129     */
130    private MavenExecutionPlan getExecutionPlan() {
131        try {
132            LifecycleExecutor lifecycleExecutor = session.getContainer().lookup(LifecycleExecutor.class);
133            List<String> goals = session.getGoals();
134            return lifecycleExecutor.calculateExecutionPlan(session, goals.toArray(new String[0]));
135        } catch (Exception e) {
136            if (verbose) {
137                log.warn("Could not access Maven execution plan: " + e.getMessage(), e);
138            }
139            return null;
140        }
141    }
142
143    /**
144     * Get the preceding phase in the Maven lifecycle
145     */
146    public String getPrecedingPhase(String phase) {
147        if (phase == null || phase.isEmpty()) {
148            return null;
149        }
150        
151        MavenExecutionPlan executionPlan = getExecutionPlan();
152        if (executionPlan == null) {
153            return null;
154        }
155        
156        List<String> allPhases = new ArrayList<>();
157        Set<String> seenPhases = new LinkedHashSet<>();
158        
159        for (MojoExecution mojoExecution : executionPlan.getMojoExecutions()) {
160            String executionPhase = mojoExecution.getLifecyclePhase();
161            if (executionPhase != null && !executionPhase.isEmpty()) {
162                seenPhases.add(executionPhase);
163            }
164        }
165        allPhases.addAll(seenPhases);
166        
167        int currentPhaseIndex = allPhases.indexOf(phase);
168        if (currentPhaseIndex > 0) {
169            return allPhases.get(currentPhaseIndex - 1);
170        }
171        
172        return null;
173    }
174
175    /**
176     * Simple mapping of common goals to their typical phases
177     */
178    private String mapGoalToPhase(String goal) {
179        if (goal == null) return null;
180        
181        switch (goal) {
182            case "clean": return "clean";
183            case "compile": return "compile";
184            case "testCompile": return "test-compile";
185            case "test": return "test";
186            case "package": return "package";
187            case "jar": return "package";
188            case "war": return "package";
189            case "install": return "install";
190            case "deploy": return "deploy";
191            case "site": return "site";
192            default: return null;
193        }
194    }
195
196    /**
197     * Infer the Maven phase from a goal name by examining plugin configurations
198     */
199    public String inferPhaseFromGoal(String goal) {
200        if (goal == null || goal.isEmpty()) {
201            return null;
202        }
203        
204        MavenExecutionPlan executionPlan = getExecutionPlan();
205        if (executionPlan == null) {
206            return null;
207        }
208        
209        for (MojoExecution mojoExecution : executionPlan.getMojoExecutions()) {
210            String executionGoal = mojoExecution.getGoal();
211            if (goal.equals(executionGoal) || goal.endsWith(":" + executionGoal)) {
212                return mojoExecution.getLifecyclePhase();
213            }
214        }
215        
216        return null;
217    }
218
219    
220    /**
221     * Get Nx project path from Maven project (relative path from workspace root)
222     */
223    private String getProjectPath(MavenProject project) {
224        if (session != null) {
225            File workspaceRoot = new File(session.getExecutionRootDirectory());
226            String relativePath = NxPathUtils.getRelativePath(workspaceRoot, project.getBasedir());
227            return relativePath.isEmpty() ? "." : relativePath;
228        } else {
229            // Fallback to project base directory name
230            return project.getBasedir().getName();
231        }
232    }
