import org.codehaus.plexus.PlexusContainer;
import org.junit.Before;
import org.junit.Test;
import org.mockito.Mock;
import org.mockito.MockitoAnnotations;

import java.io.File;
import java.util.*;

import static org.junit.Assert.*;
import static org.mockito.ArgumentMatchers.any;
import static org.mockito.Mockito.*;

/**
 * Unit tests for TargetDependencyService
 */
public class TargetDependencyServiceTest {

    @Mock
    private Log mockLog;

    @Mock
    private MavenSession mockSession;

    @Mock
    private MavenProject mockProject;

    @Mock
    private LifecycleExecutor mockLifecycleExecutor;

    @Mock
    private PlexusContainer mockContainer;

    @Mock
    private MavenExecutionPlan mockExecutionPlan;

    private TargetDependencyService service;
    private TargetDependencyService verboseService;

    @Before
    public void setUp() throws Exception {
        MockitoAnnotations.openMocks(this);

        // Setup basic mocks
        when(mockSession.getContainer()).thenReturn(mockContainer);
        when(mockContainer.lookup(LifecycleExecutor.class)).thenReturn(mockLifecycleExecutor);

        // Create services
        service = new TargetDependencyService(mockLog, false, mockSession);
        verboseService = new TargetDependencyService(mockLog, true, mockSession);
    }

    /**
     * Test calculateGoalDependencies with valid execution phase
     */
    @Test
    public void testCalculateGoalDependencies_WithValidPhase() {
        // Setup
        String executionPhase = "compile";
        String targetName = "compiler:compile";
        List<MavenProject> reactorProjects = Arrays.asList(mockProject);

        setupMockExecutionPlan(Arrays.asList("validate", "compile", "test"));

        // Execute
        List<String> dependencies = service.calculateGoalDependencies(
            mockProject, executionPhase, targetName, reactorProjects);

        // Verify
        assertNotNull(dependencies);
        assertEquals(2, dependencies.size());
        assertTrue(dependencies.contains("validate")); // preceding phase
        assertTrue(dependencies.contains("^compile")); // cross-module dependency
    }

    /**
     * Test calculateGoalDependencies with null execution phase
     */
    @Test
    public void testCalculateGoalDependencies_WithNullPhase() {
        // Setup
        String targetName = "compiler:compile";
        List<MavenProject> reactorProjects = Arrays.asList(mockProject);

        setupMockExecutionPlan(Arrays.asList("validate", "compile", "test"));

        // Execute
        List<String> dependencies = service.calculateGoalDependencies(
            mockProject, null, targetName, reactorProjects);

        // Verify - should fall back to goal mapping
        assertNotNull(dependencies);
        assertEquals(2, dependencies.size());
        assertTrue(dependencies.contains("validate")); // preceding phase
        assertTrue(dependencies.contains("^compile")); // cross-module dependency
    }

    /**
     * Test calculateGoalDependencies with empty execution phase
     */
    @Test
    public void testCalculateGoalDependencies_WithEmptyPhase() {
        // Setup
        String targetName = "compiler:compile";
        List<MavenProject> reactorProjects = Arrays.asList(mockProject);

        setupMockExecutionPlan(Arrays.asList("validate", "compile", "test"));

        // Execute
