        when(mockContainer.lookup(LifecycleExecutor.class)).thenThrow(new RuntimeException("Lookup failed"));

        // Execute
        String precedingPhase = service.getPrecedingPhase("test");

        // Verify
        assertNull(precedingPhase);
    }

    /**
     * Test inferPhaseFromGoal
     */
    @Test
    public void testInferPhaseFromGoal() {
        // Setup
        List<MojoExecution> mojoExecutions = Arrays.asList(
            createMojoExecution("compile", "compile"),
            createMojoExecution("testCompile", "test-compile"),
            createMojoExecution("test", "test")
        );
        when(mockExecutionPlan.getMojoExecutions()).thenReturn(mojoExecutions);
        setupMockExecutionPlan();

        // Execute & Verify
        assertEquals("compile", service.inferPhaseFromGoal("compile"));
        assertEquals("test", service.inferPhaseFromGoal("test"));
        assertEquals("compile", service.inferPhaseFromGoal("maven-compiler-plugin:compile"));
    }

    /**
     * Test inferPhaseFromGoal with null goal
     */
    @Test
    public void testInferPhaseFromGoal_NullGoal() {
        // Execute
        String phase = service.inferPhaseFromGoal(null);

