        // Verify
        assertNull(phase);
    }

    /**
     * Test inferPhaseFromGoal when execution plan is null
     */
    @Test
    public void testInferPhaseFromGoal_NullExecutionPlan() throws Exception {
        // Setup
        when(mockContainer.lookup(LifecycleExecutor.class)).thenThrow(new RuntimeException("Lookup failed"));

        // Execute
        String phase = service.inferPhaseFromGoal("compile");

        // Verify
        assertNull(phase);
    }

    /**
     * Test verbose logging behavior
     */
    @Test
    public void testVerboseLogging() throws Exception {
        // Setup
        when(mockContainer.lookup(LifecycleExecutor.class)).thenThrow(new RuntimeException("Test exception"));

        // Execute
        verboseService.getPrecedingPhase("test");

        // Verify verbose logging was called
        verify(mockLog, times(1)).warn(contains("Could not access Maven execution plan"), any(Exception.class));
    }

    /**
     * Test non-verbose logging behavior
     */
    @Test
    public void testNonVerboseLogging() throws Exception {
        // Setup
        when(mockContainer.lookup(LifecycleExecutor.class)).thenThrow(new RuntimeException("Test exception"));

        // Execute
        service.getPrecedingPhase("test");

        // Verify no verbose logging
        verify(mockLog, never()).warn(anyString(), any(Exception.class));
    }

    // Helper methods

    /**
     * Create a map of test targets for testing
     */
    private Map<String, TargetConfiguration> createTestTargetsMap() {
        Map<String, TargetConfiguration> targets = new HashMap<>();

        // Add a goal target
        TargetConfiguration goalTarget = new TargetConfiguration("maven:run");
        TargetMetadata goalMetadata = new TargetMetadata("goal", "Test compile goal");
        goalMetadata.setPhase("test");
        goalTarget.setMetadata(goalMetadata);
        targets.put("compiler:testCompile", goalTarget);

        // Add a phase target
        TargetConfiguration phaseTarget = new TargetConfiguration("maven:phase");
        TargetMetadata phaseMetadata = new TargetMetadata("phase", "Test phase");
        phaseTarget.setMetadata(phaseMetadata);
        targets.put("test", phaseTarget);

        return targets;
    }

    /**
     * Setup mock execution plan with phases
     */
    private void setupMockExecutionPlan(List<String> phases) {
        try {
            when(mockSession.getGoals()).thenReturn(Arrays.asList("clean", "install"));
            when(mockLifecycleExecutor.calculateExecutionPlan(eq(mockSession), any(String[].class)))
                .thenReturn(mockExecutionPlan);

            List<MojoExecution> mojoExecutions = new ArrayList<>();
            for (String phase : phases) {
                mojoExecutions.add(createMojoExecution("goal", phase));
            }
            when(mockExecutionPlan.getMojoExecutions()).thenReturn(mojoExecutions);
        } catch (Exception e) {
            throw new RuntimeException("Failed to setup mock execution plan", e);
        }
    }

    /**
     * Setup mock execution plan without specific phases
     */
    private void setupMockExecutionPlan() {
        try {
            when(mockSession.getGoals()).thenReturn(Arrays.asList("clean", "install"));
            when(mockLifecycleExecutor.calculateExecutionPlan(eq(mockSession), any(String[].class)))
                .thenReturn(mockExecutionPlan);
        } catch (Exception e) {
            throw new RuntimeException("Failed to setup mock execution plan", e);
        }
    }

    /**
     * Create a mock MojoExecution
     */
    private MojoExecution createMojoExecution(String goal, String phase) {
        MojoDescriptor mojoDescriptor = new MojoDescriptor();
        mojoDescriptor.setGoal(goal);

        PluginDescriptor pluginDescriptor = new PluginDescriptor();
        mojoDescriptor.setPluginDescriptor(pluginDescriptor);

        MojoExecution mojoExecution = new MojoExecution(mojoDescriptor);
        mojoExecution.setLifecyclePhase(phase);

        return mojoExecution;
    }
}

