            List<MojoExecution> mojoExecutions = new ArrayList<>();
            for (String phase : phases) {
                mojoExecutions.add(createMojoExecution("goal", phase));
            }
            when(mockExecutionPlan.getMojoExecutions()).thenReturn(mojoExecutions);
        } catch (Exception e) {
            throw new RuntimeException("Failed to setup mock execution plan", e);
        }
