        TargetConfiguration goalTarget = new TargetConfiguration("maven:run");
        TargetMetadata goalMetadata = new TargetMetadata("goal", "Test compile goal");
        goalMetadata.setPhase("test");
        goalTarget.setMetadata(goalMetadata);
        targets.put("compiler:testCompile", goalTarget);

        // Add a goal target for compile phase
