        compileTarget.setMetadata(compileMetadata);
        targets.put("compiler:compile", compileTarget);

        // Add a phase target
        TargetConfiguration phaseTarget = new TargetConfiguration("maven:phase");
        TargetMetadata phaseMetadata = new TargetMetadata("phase", "Test phase");
        phaseTarget.setMetadata(phaseMetadata);
        targets.put("test", phaseTarget);

        return targets;
    }
