 77â†’        return dependsOn;
 78â†’    }
 79â†’
 80â†’    /**
 81â†’     * Get phase dependencies (preceding phases)
 82â†’     */
 83â†’    public List<String> getPhaseDependencies(String phase, MavenProject project) {
 84â†’        List<String> deps = new ArrayList<>();
 85â†’        String precedingPhase = getPrecedingPhase(phase, project);
 86â†’        if (precedingPhase != null) {
 87â†’            deps.add(precedingPhase);
 88â†’        }
 89â†’        return deps;
 90â†’    }
 91â†’
 92â†’    /**
 93â†’     * Get all goals that belong to a specific phase
 94â†’     */
 95â†’    public List<String> getGoalsForPhase(String phase, Map<String, TargetConfiguration> allTargets) {
 96â†’        List<String> goalsForPhase = new ArrayList<>();
 97â†’
 98â†’        for (Map.Entry<String, TargetConfiguration> entry : allTargets.entrySet()) {
 99â†’            String targetName = entry.getKey();
100â†’            TargetConfiguration target = entry.getValue();
101â†’
102â†’            if (target.getMetadata() != null &&
103â†’                "goal".equals(target.getMetadata().getType()) &&
104â†’                phase.equals(target.getMetadata().getPhase())) {
105â†’                goalsForPhase.add(targetName);
106â†’            }
107â†’        }
108â†’
109â†’        return goalsForPhase;
110â†’    }
111â†’
112â†’
113â†’
114â†’
115â†’    /**
116â†’     * Get all goals from preceding phases in the Maven lifecycle.
117â†’     * This ensures goals depend on other goals, not phases.
118â†’     * Optimized to use pre-computed execution plan analysis.
119â†’     */
120â†’    public List<String> getPrecedingGoalsInLifecycle(MavenProject project, String currentPhase) {
121â†’        List<String> precedingGoals = new ArrayList<>();
122â†’
123â†’        if (currentPhase == null || currentPhase.isEmpty()) {
124â†’            return precedingGoals;
125â†’        }
126â†’
127â†’        // Use pre-computed lifecycle information for better performance
128â†’        org.apache.maven.lifecycle.Lifecycle lifecycle = executionPlanAnalysisService.getLifecycleForPhase(currentPhase);
129â†’        if (lifecycle == null || lifecycle.getPhases() == null) {
130â†’            return precedingGoals;
131â†’        }
132â†’
133â†’        List<String> lifecyclePhases = lifecycle.getPhases();
134â†’        int currentPhaseIndex = lifecyclePhases.indexOf(currentPhase);
135â†’
136â†’        if (currentPhaseIndex <= 0) {
137â†’            return precedingGoals; // No preceding phases
138â†’        }
139â†’
140â†’        // Get all goals from all preceding phases using project's plugin configuration
141â†’        for (int i = 0; i < currentPhaseIndex; i++) {
142â†’            String precedingPhase = lifecyclePhases.get(i);
143â†’            List<String> phaseGoals = getCommonGoalsForPhase(precedingPhase, project);
144â†’            precedingGoals.addAll(phaseGoals);
145â†’        }
146â†’
147â†’        if (verbose && !precedingGoals.isEmpty()) {
148â†’            log.debug("Found " + precedingGoals.size() + " preceding goals for phase '" + currentPhase + "': " + precedingGoals);
149â†’        }
