  1import org.apache.maven.execution.MavenSession;
  2import org.apache.maven.lifecycle.LifecycleExecutor;
  3import org.apache.maven.lifecycle.MavenExecutionPlan;
  4import org.apache.maven.plugin.MojoExecution;
  5import org.apache.maven.plugin.PluginParameterExpressionEvaluator;
  6import org.apache.maven.plugin.descriptor.MojoDescriptor;
  7import org.apache.maven.plugin.descriptor.Parameter;
  8import org.apache.maven.plugin.descriptor.PluginDescriptor;
  9import org.apache.maven.plugin.logging.Log;
 10import org.apache.maven.project.MavenProject;
 11import org.codehaus.plexus.util.xml.Xpp3Dom;
 12
 13import java.io.File;
 14import java.util.*;
 15import java.util.concurrent.ConcurrentHashMap;
 16
 17/**
 18 * Service that uses Maven's introspection APIs to dynamically determine plugin and goal behavior
 19 * instead of hardcoded plugin-specific logic. This leverages PluginDescriptor, MojoDescriptor,
 20 * and parameter analysis to understand what files/directories a Mojo actually uses.
 21 */
 22public class MavenPluginIntrospectionService {
 23    
 24    private final MavenSession session;
 25    private final LifecycleExecutor lifecycleExecutor;
 26    private final Log log;
 27    private final boolean verbose;
 28    
 29    // Cache to avoid repeated introspection
 30    private final Map<String, GoalIntrospectionResult> introspectionCache = new ConcurrentHashMap<>();
 31    
 32    public MavenPluginIntrospectionService(MavenSession session, LifecycleExecutor lifecycleExecutor, Log log, boolean verbose) {
 33        this.session = session;
 34        this.lifecycleExecutor = lifecycleExecutor;
 35        this.log = log;
 36        this.verbose = verbose;
 37    }
 38    
 39    /**
 40     * Analyze a goal using Maven's introspection APIs to determine its behavior dynamically.
 41     */
 42    public GoalIntrospectionResult analyzeGoal(String goal, MavenProject project) {
 43        String cacheKey = project.getId() + ":" + goal;
 44        
 45        return introspectionCache.computeIfAbsent(cacheKey, k -> {
 46            if (verbose) {
 47                log.debug("Performing dynamic introspection for goal: " + goal);
 48            }
 49            
 50            GoalIntrospectionResult result = new GoalIntrospectionResult(goal);
 51            
 52            try {
 53                // 1. Find MojoExecution for this goal
 54                MojoExecution mojoExecution = findMojoExecution(goal, project);
 55                if (mojoExecution != null) {
 56                    analyzeMojoExecution(mojoExecution, result);
 57                }
 58                
 59                // 2. Analyze using MojoDescriptor if available
 60                if (mojoExecution != null && mojoExecution.getMojoDescriptor() != null) {
 61                    analyzeMojoDescriptor(mojoExecution.getMojoDescriptor(), result);
 62                }
 63                
 64                // 3. Analyze plugin configuration
 65                if (mojoExecution != null) {
 66                    analyzePluginConfiguration(mojoExecution, result);
 67                }
 68                
 69            } catch (Exception e) {
 70                if (verbose) {
 71                    log.warn("Introspection failed for goal " + goal + ": " + e.getMessage());
 72                }
 73                // Fall back to minimal analysis
 74                result = createFallbackResult(goal);
 75            }
 76            
 77            if (verbose) {
 78                log.debug("Introspection result for " + goal + ": " + result.toString());
 79            }
 80            
 81            return result;
 82        });
 83    }
 84    
 85    /**
 86     * Find MojoExecution for a specific goal by examining execution plans
 87     */
 88    private MojoExecution findMojoExecution(String goal, MavenProject project) {
 89        try {
 90            // Try to find the goal in various lifecycle phases
 91            List<String> phasesToCheck = Arrays.asList(
 92                "validate", "compile", "test", "package", "verify", "install", "deploy",
 93                "clean", "site"
 94            );
 95            
 96            for (String phase : phasesToCheck) {
 97                MavenExecutionPlan executionPlan = lifecycleExecutor.calculateExecutionPlan(session, phase);
 98                
 99                for (MojoExecution mojoExecution : executionPlan.getMojoExecutions()) {
100                    if (goal.equals(mojoExecution.getGoal()) || 
101                        goal.equals(mojoExecution.getPlugin().getArtifactId() + ":" + mojoExecution.getGoal())) {
102                        return mojoExecution;
103                    }
104                }
105            }
106        } catch (Exception e) {
107            if (verbose) {
108                log.debug("Could not find MojoExecution for " + goal + ": " + e.getMessage());
109            }
110        }
111        
112        return null;
113    }
114    
115    /**
116     * Analyze MojoExecution to extract basic information
117     */
118    private void analyzeMojoExecution(MojoExecution mojoExecution, GoalIntrospectionResult result) {
119        // Get basic execution info
120        result.setPluginArtifactId(mojoExecution.getPlugin().getArtifactId());
121        result.setPluginGroupId(mojoExecution.getPlugin().getGroupId());
122        result.setLifecyclePhase(mojoExecution.getLifecyclePhase());
123        result.setExecutionId(mojoExecution.getExecutionId());
124        
125        // Analyze plugin type patterns
126        String artifactId = mojoExecution.getPlugin().getArtifactId();
127        analyzePluginTypePatterns(artifactId, mojoExecution.getGoal(), result);
128    }
129    
130    /**
131     * Analyze MojoDescriptor to understand mojo parameters and requirements
132     */
133    private void analyzeMojoDescriptor(MojoDescriptor mojoDescriptor, GoalIntrospectionResult result) {
134        if (mojoDescriptor == null) return;
135        
136        // Get mojo description and requirements
137        result.setDescription(mojoDescriptor.getDescription());
138        result.setRequiresDependencyResolution(mojoDescriptor.getDependencyResolutionRequired());
139        result.setRequiresProject(mojoDescriptor.isProjectRequired());
140        
141        // Analyze parameters to understand file/directory requirements
142        List<Parameter> parameters = mojoDescriptor.getParameters();
143        if (parameters != null) {
144            for (Parameter parameter : parameters) {
145                analyzeParameter(parameter, result);
146            }
147        }
148        
149        if (verbose) {
150            log.debug("MojoDescriptor analysis: " + parameters.size() + " parameters, " +
151                     "requires project: " + mojoDescriptor.isProjectRequired() +
152                     ", dependency resolution: " + mojoDescriptor.getDependencyResolutionRequired());
153        }
154    }
155    
156    /**
157     * Analyze individual parameter to understand file/directory requirements
158     */
159    private void analyzeParameter(Parameter parameter, GoalIntrospectionResult result) {
160        String name = parameter.getName();
161        String type = parameter.getType();
162        String description = parameter.getDescription();
163        
164        // Check for file/directory parameters
165        if (isFileParameter(name, type, description)) {
166            result.addFileParameter(name, type, description);
167            
168            // Determine if it's input or output
169            if (isOutputParameter(name, description)) {
170                result.addOutputPattern(name);
171            } else {
172                result.addInputPattern(name);
173            }
174        }
175        
176        // Check for source-related parameters
177        if (isSourceParameter(name, type, description)) {
178            result.setProcessesSources(true);
179        }
180        
181        // Check for test-related parameters
182        if (isTestParameter(name, type, description)) {
183            result.setTestRelated(true);
184        }
185        
186        // Check for resource parameters
187        if (isResourceParameter(name, type, description)) {
188            result.setNeedsResources(true);
189        }
190    }
191    
192    /**
193     * Check if parameter represents a file or directory
194     */
195    private boolean isFileParameter(String name, String type, String description) {
196        if (type == null) return false;
197        
198        // Check type
199        if (type.equals("java.io.File") || type.equals("java.nio.file.Path") || 
200            type.equals("java.lang.String") && (name.contains("Dir") || name.contains("File") || name.contains("Path"))) {
201            return true;
202        }
203        
204        // Check name patterns
205        if (name != null) {
206            String lowerName = name.toLowerCase();
207            if (lowerName.contains("directory") || lowerName.contains("file") || lowerName.contains("path") ||
208                lowerName.contains("output") || lowerName.contains("input") || lowerName.contains("source") ||
209                lowerName.contains("target") || lowerName.contains("destination")) {
210                return true;
211            }
212        }
213        
214        // Check description
215        if (description != null) {
216            String lowerDesc = description.toLowerCase();
217            return lowerDesc.contains("directory") || lowerDesc.contains("file") || lowerDesc.contains("path");
218        }
219        
220        return false;
221    }
222    
223    /**
224     * Check if parameter is an output parameter
225     */
226    private boolean isOutputParameter(String name, String description) {
227        if (name != null) {
228            String lowerName = name.toLowerCase();
229            if (lowerName.contains("output") || lowerName.contains("target") || lowerName.contains("destination") ||
230                lowerName.contains("generated") || lowerName.contains("build")) {
231                return true;
232            }
233        }
234        
235        if (description != null) {
236            String lowerDesc = description.toLowerCase();
237            return lowerDesc.contains("output") || lowerDesc.contains("generate") || lowerDesc.contains("create") ||
238                   lowerDesc.contains("write") || lowerDesc.contains("produce");
239        }
240        
241        return false;
242    }
243    
244    /**
245     * Check if parameter is source-related
246     */
247    private boolean isSourceParameter(String name, String type, String description) {
248        if (name != null && name.toLowerCase().contains("source")) return true;
249        if (description != null && description.toLowerCase().contains("source")) return true;
250        return false;
251    }
252    
253    /**
254     * Check if parameter is test-related
255     */
256    private boolean isTestParameter(String name, String type, String description) {
257        if (name != null && name.toLowerCase().contains("test")) return true;
258        if (description != null && description.toLowerCase().contains("test")) return true;
259        return false;
260    }
261    
262    /**
263     * Check if parameter is resource-related
264     */
265    private boolean isResourceParameter(String name, String type, String description) {
266        if (name != null && name.toLowerCase().contains("resource")) return true;
267        if (description != null && description.toLowerCase().contains("resource")) return true;
268        return false;
269    }
270    
271    /**
272     * Analyze plugin configuration XML to understand file/directory usage
273     */
274    private void analyzePluginConfiguration(MojoExecution mojoExecution, GoalIntrospectionResult result) {
275        Xpp3Dom configuration = mojoExecution.getConfiguration();
276        if (configuration != null) {
277            analyzeConfigurationElement(configuration, result);
278        }
279    }
280    
281    /**
282     * Recursively analyze configuration XML elements
283     */
284    private void analyzeConfigurationElement(Xpp3Dom element, GoalIntrospectionResult result) {
285        if (element == null) return;
286        
287        String name = element.getName();
288        String value = element.getValue();
289        
290        // Look for file/directory configurations
291        if (value != null && (name.toLowerCase().contains("dir") || name.toLowerCase().contains("file") ||
292                             name.toLowerCase().contains("path") || name.toLowerCase().contains("output"))) {
293            result.addConfigurationPath(name, value);
294        }
295        
296        // Recursively check child elements
297        for (Xpp3Dom child : element.getChildren()) {
298            analyzeConfigurationElement(child, result);
299        }
300    }
301    
302    /**
303     * Analyze plugin type patterns (enhanced version of the old hardcoded logic)
304     */
305    private void analyzePluginTypePatterns(String artifactId, String goal, GoalIntrospectionResult result) {
306        if (artifactId == null) return;
307        
308        // Use pattern matching but make it more flexible
309        if (artifactId.contains("compiler")) {
310            result.setProcessesSources(true);
311            if (goal != null && goal.contains("test")) {
312                result.setTestRelated(true);
313            }
314        } else if (artifactId.contains("surefire") || artifactId.contains("failsafe")) {
315            result.setTestRelated(true);
316            result.setProcessesSources(true);
317        } else if (artifactId.contains("resources")) {
318            result.setNeedsResources(true);
319            if (goal != null && goal.contains("test")) {
320                result.setTestRelated(true);
321            }
322        } else if (artifactId.contains("source") || artifactId.contains("javadoc")) {
323            result.setProcessesSources(true);
324        }
325        
326        // Framework-specific patterns
327        if (artifactId.contains("quarkus") || artifactId.contains("spring-boot")) {
328            result.setProcessesSources(true);
329            result.setNeedsResources(true);
330            if (goal != null && (goal.equals("dev") || goal.equals("test"))) {
331                result.setTestRelated(true);
332            }
333        }
334    }
335    
336    /**
337     * Create fallback result when introspection fails
338     */
339    private GoalIntrospectionResult createFallbackResult(String goal) {
340        GoalIntrospectionResult result = new GoalIntrospectionResult(goal);
341        
342        // Very conservative fallback
343        if (goal != null) {
344            if (goal.equals("compile") || goal.equals("testCompile")) {
345                result.setProcessesSources(true);
346                if (goal.equals("testCompile")) {
347                    result.setTestRelated(true);
348                }
349            } else if (goal.equals("test")) {
350                result.setTestRelated(true);
351                result.setProcessesSources(true);
352            }
353        }
354        
355        return result;
356    }
357    
358    /**
359     * Result of goal introspection containing all discovered information
360     */
361    public static class GoalIntrospectionResult {
362        private final String goal;
363        private String pluginGroupId;
364        private String pluginArtifactId;
365        private String lifecyclePhase;
366        private String executionId;
367        private String description;
368        private String requiresDependencyResolution;
369        private boolean requiresProject;
370        
371        // Behavior flags
372        private boolean processesSources = false;
373        private boolean testRelated = false;
374        private boolean needsResources = false;
375        
376        // Parameter and configuration analysis
377        private final List<ParameterInfo> fileParameters = new ArrayList<>();
378        private final Set<String> inputPatterns = new LinkedHashSet<>();
379        private final Set<String> outputPatterns = new LinkedHashSet<>();
380        private final Map<String, String> configurationPaths = new HashMap<>();
381        
382        public GoalIntrospectionResult(String goal) {
383            this.goal = goal;
384        }
385        
386        // Getters and setters
387        public String getGoal() { return goal; }
388        public String getPluginGroupId() { return pluginGroupId; }
389        public void setPluginGroupId(String pluginGroupId) { this.pluginGroupId = pluginGroupId; }
390        public String getPluginArtifactId() { return pluginArtifactId; }
391        public void setPluginArtifactId(String pluginArtifactId) { this.pluginArtifactId = pluginArtifactId; }
392        public String getLifecyclePhase() { return lifecyclePhase; }
393        public void setLifecyclePhase(String lifecyclePhase) { this.lifecyclePhase = lifecyclePhase; }
394        public String getExecutionId() { return executionId; }
395        public void setExecutionId(String executionId) { this.executionId = executionId; }
396        public String getDescription() { return description; }
397        public void setDescription(String description) { this.description = description; }
398        public String getRequiresDependencyResolution() { return requiresDependencyResolution; }
399        public void setRequiresDependencyResolution(String requiresDependencyResolution) { 
400            this.requiresDependencyResolution = requiresDependencyResolution; 
401        }
402        public boolean isRequiresProject() { return requiresProject; }
403        public void setRequiresProject(boolean requiresProject) { this.requiresProject = requiresProject; }
404        
405        public boolean processesSources() { return processesSources; }
406        public void setProcessesSources(boolean processesSources) { this.processesSources = processesSources; }
407        public boolean isTestRelated() { return testRelated; }
408        public void setTestRelated(boolean testRelated) { this.testRelated = testRelated; }
409        public boolean needsResources() { return needsResources; }
410        public void setNeedsResources(boolean needsResources) { this.needsResources = needsResources; }
411        
412        public void addFileParameter(String name, String type, String description) {
413            fileParameters.add(new ParameterInfo(name, type, description));
414        }
415        
416        public void addInputPattern(String pattern) { inputPatterns.add(pattern); }
417        public void addOutputPattern(String pattern) { outputPatterns.add(pattern); }
418        public void addConfigurationPath(String name, String path) { configurationPaths.put(name, path); }
419        
420        public List<ParameterInfo> getFileParameters() { return new ArrayList<>(fileParameters); }
421        public Set<String> getInputPatterns() { return new LinkedHashSet<>(inputPatterns); }
422        public Set<String> getOutputPatterns() { return new LinkedHashSet<>(outputPatterns); }
423        public Map<String, String> getConfigurationPaths() { return new HashMap<>(configurationPaths); }
424        
425        /**
426         * Convert to GoalBehavior for compatibility with existing code
427         */
428        public GoalBehavior toGoalBehavior() {
429            GoalBehavior behavior = new GoalBehavior();
430            behavior.setProcessesSources(processesSources);
431            behavior.setTestRelated(testRelated);
432            behavior.setNeedsResources(needsResources);
433            return behavior;
434        }
435        
436        @Override
437        public String toString() {
438            return "GoalIntrospectionResult{" +
439                   "goal='" + goal + '\'' +
440                   ", plugin=" + pluginGroupId + ":" + pluginArtifactId +
441                   ", phase=" + lifecyclePhase +
442                   ", sources=" + processesSources +
443                   ", test=" + testRelated +
444                   ", resources=" + needsResources +
445                   ", fileParams=" + fileParameters.size() +
446                   '}';
447        }
448    }
449    
450    /**
451     * Information about a parameter
452     */
453    public static class ParameterInfo {
454        private final String name;
455        private final String type;
456        private final String description;
457        
458        public ParameterInfo(String name, String type, String description) {
459            this.name = name;
460            this.type = type;
461            this.description = description;
462        }
463        
464        public String getName() { return name; }
465        public String getType() { return type; }
466        public String getDescription() { return description; }
467        
468        @Override
469        public String toString() {
470            return name + "(" + type + ")";
471        }
472    }
473}

