  2import org.apache.maven.lifecycle.LifecycleExecutor;
  3import org.apache.maven.lifecycle.DefaultLifecycles;
  4import org.apache.maven.plugin.AbstractMojo;
  5import org.apache.maven.plugin.MojoExecutionException;
  6import org.apache.maven.plugin.MojoFailureException;
  7import org.apache.maven.plugins.annotations.Component;
  8import org.apache.maven.plugins.annotations.Mojo;
  9import org.apache.maven.plugins.annotations.Parameter;
 10import org.apache.maven.plugins.annotations.ResolutionScope;
 11import org.apache.maven.project.MavenProject;
 12
 13import com.google.gson.Gson;
 14import com.google.gson.GsonBuilder;
 15
 16import model.CreateNodesV2Entry;
 17import model.RawProjectGraphDependency;
 18import model.TargetConfiguration;
 19import model.TargetGroup;
 20import java.io.File;
 21import java.io.FileWriter;
 22import java.io.IOException;
 23import java.util.ArrayList;
 24import java.util.HashMap;
 25import java.util.LinkedHashMap;
 26import java.util.LinkedHashSet;
 27import java.util.List;
 28import java.util.Map;
 29import java.util.Set;
 30
 31/**
 32 * Refactored Maven plugin that analyzes Maven projects for Nx integration.
 33 * This version delegates complex logic to specialized service classes.
 34 */
 35@Mojo(name = "analyze", aggregator = true, requiresDependencyResolution = ResolutionScope.NONE)
 36public class NxAnalyzerMojo extends AbstractMojo {
 37    
 38    @Parameter(defaultValue = "${session}", readonly = true, required = true)
 39    private MavenSession session;
 40    
 41    @Parameter(defaultValue = "${reactorProjects}", readonly = true, required = true)
 42    private List<MavenProject> reactorProjects;
 43    
 44    @Parameter(property = "nx.outputFile")
 45    private String outputFile;
 46    
 47    @Parameter(property = "nx.verbose", defaultValue = "false")
 48    private String verboseStr;
 49    
 50    @Component
 51    private LifecycleExecutor lifecycleExecutor;
 52    
 53    @Component
 54    private DefaultLifecycles defaultLifecycles;
 55    
 56    // Services for delegating complex operations
 57    private ExecutionPlanAnalysisService executionPlanAnalysisService;
 58    private TargetGenerationService targetGenerationService;
 59    private TargetGroupService targetGroupService;
 60    private TargetDependencyService targetDependencyService;
 61    
 62    @Override
 63    public void execute() throws MojoExecutionException, MojoFailureException {
 64        long startTime = System.currentTimeMillis();
 65        
 66        
 67        try {
 68            initializeServices();
 69            
 70            getLog().info("Starting Nx analysis for " + reactorProjects.size() + " projects");
 71            logBasicInfo();
 72            
 73            // Generate analysis results
 74            Map<String, Object> result = performAnalysis();
 75            
 76            // Write output
 77            String outputPath = determineOutputPath();
 78            writeResult(result, outputPath);
 79            
 80            logCompletion(startTime, outputPath);
 81            
 82        } catch (Exception e) {
 83            throw new MojoExecutionException("Analysis failed: " + e.getMessage(), e);
 84        }
 85    }
 86    
 87    private void initializeServices() {
 88        this.executionPlanAnalysisService = new ExecutionPlanAnalysisService(getLog(), isVerbose(), lifecycleExecutor, session, defaultLifecycles);
 89        this.targetGenerationService = new TargetGenerationService(getLog(), isVerbose(), session, executionPlanAnalysisService);
 90        this.targetGroupService = new TargetGroupService(executionPlanAnalysisService);
 91        this.targetDependencyService = new TargetDependencyService(getLog(), isVerbose(), executionPlanAnalysisService);
 92    }
 93    
 94    private void logBasicInfo() {
 95        if (isVerbose()) {
 96            getLog().info("Verbose mode enabled");
 97        }
 98        
 99        getLog().info("Root directory: " + session.getExecutionRootDirectory());
100        if (!reactorProjects.isEmpty()) {
101            MavenProject first = reactorProjects.get(0);
102            MavenProject last = reactorProjects.get(reactorProjects.size() - 1);
103            getLog().info("First project: " + first.getGroupId() + ":" + first.getArtifactId());
104            getLog().info("Last project: " + last.getGroupId() + ":" + last.getArtifactId());
105        }
106    }
107    
108    private Map<String, Object> performAnalysis() {
109        File workspaceRoot = new File(session.getExecutionRootDirectory());
110        
111        
112        // First, calculate actual project dependencies for all projects
113        Map<MavenProject, List<MavenProject>> projectDependencies = calculateProjectDependencies();
114        
115        // Generate targets and groups for each project
116        Map<MavenProject, Map<String, TargetConfiguration>> projectTargets = new LinkedHashMap<>();
117        Map<MavenProject, Map<String, TargetGroup>> projectTargetGroups = new LinkedHashMap<>();
118        
119        for (MavenProject project : reactorProjects) {
120            try {
121                // Get actual project dependencies for this project (not all reactor projects)
122                List<MavenProject> actualDependencies = projectDependencies.getOrDefault(project, new ArrayList<>());
123                
124                // Calculate goal dependencies using only actual dependencies
125                Map<String, List<String>> goalDependencies = calculateGoalDependencies(project, actualDependencies);
126                
127                // Generate targets using pre-calculated goal dependencies (phase dependencies calculated later)
128                Map<String, TargetConfiguration> targets = targetGenerationService.generateTargets(
129                    project, workspaceRoot, goalDependencies, new LinkedHashMap<>());
130                
131                // Now calculate phase dependencies using the generated targets
132                Map<String, List<String>> phaseDependencies = calculatePhaseDependencies(project, targets);
133                
134                // Update phase targets with calculated dependencies
135                updatePhaseTargetsWithDependencies(targets, phaseDependencies);
136                projectTargets.put(project, targets);
137                
138                // Generate target groups
139                Map<String, TargetGroup> targetGroups = targetGroupService.generateTargetGroups(project, targets, session);
140                projectTargetGroups.put(project, targetGroups);
141                
142                if (isVerbose()) {
143                    getLog().info("Processed " + project.getArtifactId() + 
144                                 ": " + targets.size() + " targets, " + targetGroups.size() + " groups, " +
145                                 actualDependencies.size() + " project dependencies");
146                }
147                
148            } catch (Exception e) {
149                getLog().error("Error processing project " + project.getArtifactId() + ": " + e.getMessage(), e);
150                // Continue with empty targets and groups
151                projectTargets.put(project, new LinkedHashMap<>());
152                projectTargetGroups.put(project, new LinkedHashMap<>());
153            }
154        }
155        
156        // Generate Nx-compatible outputs
157        List<CreateNodesV2Entry> createNodesEntries = CreateNodesResultGenerator.generateCreateNodesV2Results(
158            reactorProjects, workspaceRoot, projectTargets, projectTargetGroups);
159        
160        List<Object[]> createNodesResults = new ArrayList<>();
161        for (CreateNodesV2Entry entry : createNodesEntries) {
162            createNodesResults.add(entry.toArray());
163        }
164        
165        List<RawProjectGraphDependency> createDependencies = CreateDependenciesGenerator.generateCreateDependencies(
166            reactorProjects, workspaceRoot, getLog(), isVerbose());
167        
168        if (isVerbose()) {
169            getLog().info("Generated " + createDependencies.size() + " workspace dependencies");
170        }
171        
172        Map<String, Object> result = new LinkedHashMap<>();
173        result.put("createNodesResults", createNodesResults);
174        result.put("createDependencies", createDependencies);
175        
176        return result;
177    }
178    
179    /**
180     * Calculate actual project dependencies for all projects in the reactor.
181     * Returns a map from each project to its list of actual Maven dependency projects.
182     */
183    private Map<MavenProject, List<MavenProject>> calculateProjectDependencies() {
184        Map<MavenProject, List<MavenProject>> projectDependencies = new LinkedHashMap<>();
185        
186        // Build artifact mapping for workspace projects
