        log.info("ğŸ“ Output written to: $outputPath")
        log.info("ğŸ—ï¸  Projects analyzed: ${reactorProjects.size}")

        // Performance insights
        if (reactorProjects.isNotEmpty()) {
            val avgTimePerProject = analysisDuration / reactorProjects.size.toDouble()
            log.info("â±ï¸  Average time per project: ${String.format("%.1f", avgTimePerProject)}ms")

            when {
                avgTimePerProject > 1000 -> {
                    log.warn("âš ï¸  High average time per project detected - consider optimizing large projects")
                }
                avgTimePerProject < 10 -> {
                    log.info("ğŸš€ Excellent performance - very fast analysis per project")
                }
            }
        }
