/**
 * Data class representing the behavior and requirements of a Maven goal.
 * Used to replace hardcoded goal classification logic with dynamic analysis.
 */
data class GoalBehavior(
    var processesSources: Boolean = false,
    var testRelated: Boolean = false,
    var needsResources: Boolean = false,
    val sourcePaths: MutableList<String> = mutableListOf(),
    val resourcePaths: MutableList<String> = mutableListOf(),
    val outputPaths: MutableList<String> = mutableListOf()
) {

    fun processesSources(): Boolean = processesSources

    fun isTestRelated(): Boolean = testRelated

    fun needsResources(): Boolean = needsResources

    fun getSourcePaths(): List<String> = sourcePaths.toList()

    fun setSourcePaths(paths: List<String>) {
        sourcePaths.clear()
        sourcePaths.addAll(paths)
    }

    fun getResourcePaths(): List<String> = resourcePaths.toList()

    fun setResourcePaths(paths: List<String>) {
        resourcePaths.clear()
        resourcePaths.addAll(paths)
    }

    fun getOutputPaths(): List<String> = outputPaths.toList()

    fun setOutputPaths(paths: List<String>) {
        outputPaths.clear()
        outputPaths.addAll(paths)
    }

    /**
     * Merge this behavior with another, taking the union of all capabilities.
     */
    fun merge(other: GoalBehavior): GoalBehavior {
        return GoalBehavior(
            processesSources = this.processesSources || other.processesSources,
            testRelated = this.testRelated || other.testRelated,
            needsResources = this.needsResources || other.needsResources
        ).apply {
            sourcePaths.addAll(this@GoalBehavior.sourcePaths)
            sourcePaths.addAll(other.sourcePaths)

            resourcePaths.addAll(this@GoalBehavior.resourcePaths)
            resourcePaths.addAll(other.resourcePaths)

            outputPaths.addAll(this@GoalBehavior.outputPaths)
            outputPaths.addAll(other.outputPaths)
        }
    }

    /**
     * Check if this behavior has any defined capabilities.
     */
    fun hasAnyBehavior(): Boolean {
        return processesSources || testRelated || needsResources || 
               sourcePaths.isNotEmpty() || resourcePaths.isNotEmpty() || outputPaths.isNotEmpty()
    }
}

