/**
 * Represents an Nx target configuration
 */
data class TargetConfiguration(
    var executor: String? = null,
    var options: MutableMap<String, Any> = LinkedHashMap(),
    var inputs: MutableList<String> = ArrayList(),
    var outputs: MutableList<String> = ArrayList(),
    var dependsOn: MutableList<Any> = ArrayList(),
    var metadata: TargetMetadata? = null
) {
    constructor(executor: String) : this(executor = executor)
}

package model

/**
 * Represents a target dependency in object form for Nx.
 * This allows for more precise control over dependencies than simple strings.
 * 
 * Based on Nx documentation:
 * - dependencies: Run this target on all dependencies first
 * - projects: Run target on specific projects first
 * - target: target name
 * - params: "forward" or "ignore", defaults to "ignore"
 */
data class TargetDependency(
    var dependencies: Boolean? = null,
    var projects: List<String>? = null,
    var target: String? = null,
    var params: String = "ignore"
) {
    constructor(target: String) : this(target = target)

    constructor(target: String, dependencies: Boolean) : this(
        target = target,
        dependencies = dependencies
    )

    constructor(target: String, projects: List<String>) : this(
        target = target,
        projects = projects
    )
}

package model

/**
 * Represents a group of targets organized by Maven lifecycle phase
 */
data class TargetGroup(
    var phase: String? = null,
    var description: String? = null,
    var targets: MutableList<String> = ArrayList(),
    var order: Int = 0
) {
    constructor(phase: String, description: String, order: Int) : this(
        phase = phase,
        description = description,
        order = order
    )

    fun addTarget(targetName: String) {
        if (!targets.contains(targetName)) {
            targets.add(targetName)
        }
    }
}

