    ): CreateNodesResult {
        val projectName = NxPathUtils.getProjectName(project)
        val projectRoot = NxPathUtils.getRelativePath(workspaceRoot, project.basedir).let { root ->
            if (root.isEmpty()) "." else root
        }

        // Create ProjectConfiguration
        val projectConfig = ProjectConfiguration(
            name = "${project.groupId}:${project.artifactId}",
            root = projectRoot,
            projectType = determineProjectType(project)
        )

        // Add targets (update paths to use workspace-relative paths)
        targets?.takeIf { it.isNotEmpty() }?.let { targetMap ->
            val updatedTargets = updateTargetPaths(targetMap, workspaceRoot, project.basedir)
            projectConfig.targets.putAll(updatedTargets)
        }

        // Add metadata
        val metadata = ProjectMetadata(
            groupId = project.groupId,
            artifactId = project.artifactId,
            version = project.version,
            packaging = project.packaging
        )

        // Add target groups to metadata (convert from Map<String, TargetGroup> to Map<String, List<String>>)
        targetGroups?.takeIf { it.isNotEmpty() }?.let { groups ->
            val convertedTargetGroups = groups.mapValues { (_, targetGroup) ->
                targetGroup.targets.toList()
            }
            metadata.targetGroups.putAll(convertedTargetGroups)
        }

        projectConfig.metadata?.let { projectConfig.copy(metadata = metadata) }
            ?: projectConfig.copy(metadata = metadata)

        // Create CreateNodesResult
        val result = CreateNodesResult()
        result.addProject(projectRoot, projectConfig)

        return result
    }

    /**
     * Update target paths to use workspace-relative paths instead of {projectRoot} tokens
     */
    private fun updateTargetPaths(
        targets: Map<String, TargetConfiguration>,
        workspaceRoot: File,
        projectDir: File
    ): Map<String, TargetConfiguration> {
        val projectRootToken = NxPathUtils.getRelativePath(workspaceRoot, projectDir).let { root ->
            if (root.isEmpty()) "." else root
        }

        return targets.mapValues { (_, target) ->
            // Update cwd in options
            val options = target.options.toMutableMap()
            if (options["cwd"] == "{projectRoot}") {
                options["cwd"] = projectRootToken
            }

            target.copy(
                options = options,
                inputs = target.inputs.toMutableList(),
                outputs = target.outputs.toMutableList(),
                dependsOn = target.dependsOn.toMutableList()
            )
        }
    }

    /**
     * Determine project type based on Maven project configuration
     * @param project The Maven project to analyze
     * @return "application" or "library" (never null)
     */
    private fun determineProjectType(project: MavenProject): String {
        val packaging = project.packaging

        return when (packaging) {
            null -> "library"
            "pom" -> "library"  // POM packaging usually indicates an aggregator/parent project
            "war", "ear" -> "application"  // WAR and EAR packaging indicates web applications
            else -> "library"  // Default to library for all other packaging types (jar, etc.)
        }
    }
}

