import org.apache.maven.execution.MavenSession
import org.apache.maven.lifecycle.DefaultLifecycles
import org.apache.maven.lifecycle.LifecycleExecutor
import org.apache.maven.lifecycle.MavenExecutionPlan
import org.apache.maven.plugin.MojoExecution
import org.apache.maven.plugin.logging.Log
import org.apache.maven.project.MavenProject
import java.util.*
import java.util.concurrent.ConcurrentHashMap

/**
 * Service that analyzes Maven execution plans upfront and stores all results for efficient querying.
 * Pre-computes all Maven analysis to avoid performance bottlenecks during target dependency analysis.
 */
class ExecutionPlanAnalysisService(
    private val log: Log,
    private val verbose: Boolean,
    val lifecycleExecutor: LifecycleExecutor,
    private val session: MavenSession,
    val defaultLifecycles: DefaultLifecycles
) {

    // Pre-computed analysis results per project
    private val analysisCache = ConcurrentHashMap<String, ProjectExecutionAnalysis>()

    // Pre-computed lifecycle information (shared across all projects)
    private val allLifecyclePhases: Set<String>
    private val defaultLifecyclePhases: List<String>
    private val cleanLifecyclePhases: List<String>
    private val siteLifecyclePhases: List<String>
    private val phaseToLifecycleMap: Map<String, org.apache.maven.lifecycle.Lifecycle>

    init {
        // Pre-compute all lifecycle information once
        allLifecyclePhases = computeAllLifecyclePhases()
        defaultLifecyclePhases = computeLifecyclePhases("default")
        cleanLifecyclePhases = computeLifecyclePhases("clean")
        siteLifecyclePhases = computeLifecyclePhases("site")
        phaseToLifecycleMap = computePhaseToLifecycleMap()

        if (verbose) {
            log.info("Pre-computed lifecycle information: ${allLifecyclePhases.size} total phases")
        }
    }

    /**
     * Pre-analyze all projects to populate the cache
     */
    fun preAnalyzeAllProjects(projects: List<MavenProject>) {
        val startTime = System.currentTimeMillis()

        projects.forEach { project ->
            try {
                getAnalysis(project)
            } catch (e: Exception) {
                if (verbose) {
                    log.warn("Failed to pre-analyze project ${project.artifactId}: ${e.message}")
                }
            }
        }

        val duration = System.currentTimeMillis() - startTime
        if (verbose) {
            log.info("Pre-analyzed ${projects.size} projects in ${duration}ms")
        }
    }

    /**
     * Get analysis for a project (cached)
     */
    fun getAnalysis(project: MavenProject): ProjectExecutionAnalysis {
        val key = "${project.groupId}:${project.artifactId}"
        return analysisCache.computeIfAbsent(key) { analyzeProject(project) }
    }

    /**
     * Find the phase for a specific goal
     */
    fun findPhaseForGoal(project: MavenProject?, goal: String?): String? {
        if (project == null || goal.isNullOrEmpty()) {
            return null
        }
        val analysis = getAnalysis(project)
        return analysis.getPhaseForGoal(goal)
    }

    /**
     * Get all goals for a specific phase
     */
    fun getGoalsForPhase(project: MavenProject?, phase: String?): List<String> {
        if (project == null || phase == null) {
            return emptyList()
        }
        val analysis = getAnalysis(project)
        return analysis.getGoalsForPhase(phase)
    }

    /**
     * Get all lifecycle phases from all lifecycles (default, clean, site)
     */
    fun getAllLifecyclePhases(): Set<String> = LinkedHashSet(allLifecyclePhases)

    /**
     * Get default lifecycle phases (validate, compile, test, package, verify, install, deploy)
     */
    fun getDefaultLifecyclePhases(): List<String> = ArrayList(defaultLifecyclePhases)

    /**
     * Get clean lifecycle phases (pre-clean, clean, post-clean)
     */
    fun getCleanLifecyclePhases(): List<String> = ArrayList(cleanLifecyclePhases)

    /**
     * Get site lifecycle phases (pre-site, site, post-site, site-deploy)
     */
    fun getSiteLifecyclePhases(): List<String> = ArrayList(siteLifecyclePhases)

    /**
     * Get goals completed by a specific phase
     */
    fun getGoalsCompletedByPhase(project: MavenProject, phase: String): List<String> {
        val analysis = getAnalysis(project)
        return analysis.getGoalsCompletedByPhase(phase)
    }

    private fun analyzeProject(project: MavenProject): ProjectExecutionAnalysis {
        val analysis = ProjectExecutionAnalysis(project)

        try {
            // Analyze each lifecycle
            allLifecyclePhases.forEach { phase ->
                analyzePhaseExecutionPlan(project, phase, analysis)
            }
        } catch (e: Exception) {
            if (verbose) {
                log.warn("Error analyzing project ${project.artifactId}: ${e.message}")
            }
        }

        return analysis
    }

    private fun analyzePhaseExecutionPlan(project: MavenProject, phase: String, analysis: ProjectExecutionAnalysis) {
        try {
            val executionPlan = lifecycleExecutor.calculateExecutionPlan(session, phase)

            executionPlan.mojoExecutions.forEach { mojoExecution ->
                val goalName = "${mojoExecution.plugin.artifactId}:${mojoExecution.goal}"
                analysis.addGoalToPhase(phase, goalName)
                analysis.addPhaseForGoal(goalName, phase)
            }
        } catch (e: Exception) {
            if (verbose) {
                log.debug("Could not analyze execution plan for phase $phase in project ${project.artifactId}: ${e.message}")
            }
        }
    }

    private fun computeAllLifecyclePhases(): Set<String> {
        val phases = linkedSetOf<String>()

        try {
            defaultLifecycles.lifeCycles.forEach { lifecycle ->
                lifecycle.phases?.let { phases.addAll(it) }
            }
        } catch (e: Exception) {
            if (verbose) {
                log.warn("Could not compute all lifecycle phases: ${e.message}")
            }
        }

        return phases
    }

    private fun computeLifecyclePhases(lifecycleId: String): List<String> {
        return try {
            defaultLifecycles.lifeCycles
                .find { it.id == lifecycleId }
                ?.phases
                ?: emptyList()
        } catch (e: Exception) {
            if (verbose) {
                log.warn("Could not compute phases for lifecycle $lifecycleId: ${e.message}")
            }
            emptyList()
        }
    }

    private fun computePhaseToLifecycleMap(): Map<String, org.apache.maven.lifecycle.Lifecycle> {
        return try {
            defaultLifecycles.phaseToLifecycleMap ?: emptyMap()
        } catch (e: Exception) {
            if (verbose) {
                log.warn("Could not compute phase to lifecycle map: ${e.message}")
            }
            emptyMap()
        }
    }

    companion object {
        /**
         * Generate target name from plugin artifact ID and goal
         */
        fun getTargetName(artifactId: String, goal: String): String = "$artifactId:$goal"
    }

    /**
     * Project-specific execution analysis results
     */
    class ProjectExecutionAnalysis(private val project: MavenProject) {
        private val phaseToGoals = linkedMapOf<String, MutableList<String>>()
        private val goalToPhase = linkedMapOf<String, String>()

        fun addGoalToPhase(phase: String, goal: String) {
            phaseToGoals.computeIfAbsent(phase) { mutableListOf() }.add(goal)
        }

        fun addPhaseForGoal(goal: String, phase: String) {
            goalToPhase[goal] = phase
        }

        fun getGoalsForPhase(phase: String): List<String> = 
            phaseToGoals[phase]?.toList() ?: emptyList()

        fun getPhaseForGoal(goal: String): String? = goalToPhase[goal]

        fun getGoalsCompletedByPhase(phase: String): List<String> {
            // Return goals that would be executed up to and including this phase
            return getGoalsForPhase(phase)
        }
    }
}

