        return analysis.getGoalsCompletedByPhase(phase)
    }

    private fun analyzeProject(project: MavenProject): ProjectExecutionAnalysis {
        val analysis = ProjectExecutionAnalysis(project)

        try {
            // Analyze each lifecycle
            allLifecyclePhases.forEach { phase ->
                analyzePhaseExecutionPlan(project, phase, analysis)
            }
        } catch (e: Exception) {
            if (verbose) {
                log.warn("Error analyzing project ${project.artifactId}: ${e.message}")
            }
        }

        return analysis
    }

    private fun analyzePhaseExecutionPlan(project: MavenProject, phase: String, analysis: ProjectExecutionAnalysis) {
        try {
            val executionPlan = lifecycleExecutor.calculateExecutionPlan(session, phase)

            executionPlan.mojoExecutions.forEach { mojoExecution ->
                val goalName = "${mojoExecution.plugin.artifactId}:${mojoExecution.goal}"
                analysis.addGoalToPhase(phase, goalName)
                analysis.addPhaseForGoal(goalName, phase)
            }
        } catch (e: Exception) {
            if (verbose) {
                log.debug("Could not analyze execution plan for phase $phase in project ${project.artifactId}: ${e.message}")
            }
        }
    }

    private fun computeAllLifecyclePhases(): Set<String> {
        val phases = linkedSetOf<String>()

        try {
            defaultLifecycles.lifeCycles.forEach { lifecycle ->
                lifecycle.phases?.let { phases.addAll(it) }
            }
        } catch (e: Exception) {
            if (verbose) {
                log.warn("Could not compute all lifecycle phases: ${e.message}")
            }
        }

        return phases
    }

    private fun computeLifecyclePhases(lifecycleId: String): List<String> {
        return try {
            defaultLifecycles.lifeCycles
                .find { it.id == lifecycleId }
                ?.phases
                ?: emptyList()
        } catch (e: Exception) {
            if (verbose) {
                log.warn("Could not compute phases for lifecycle $lifecycleId: ${e.message}")
            }
            emptyList()
        }
    }

    private fun computePhaseToLifecycleMap(): Map<String, org.apache.maven.lifecycle.Lifecycle> {
        return try {
            defaultLifecycles.phaseToLifecycleMap ?: emptyMap()
        } catch (e: Exception) {
            if (verbose) {
                log.warn("Could not compute phase to lifecycle map: ${e.message}")
            }
            emptyMap()
        }
    }

    companion object {
        /**
         * Generate target name from plugin artifact ID and goal
         */
        fun getTargetName(artifactId: String, goal: String): String = "$artifactId:$goal"
    }

    /**
     * Project-specific execution analysis results
     */
    class ProjectExecutionAnalysis(private val project: MavenProject) {
        private val phaseToGoals = linkedMapOf<String, MutableList<String>>()
        private val goalToPhase = linkedMapOf<String, String>()

        fun addGoalToPhase(phase: String, goal: String) {
            phaseToGoals.computeIfAbsent(phase) { mutableListOf() }.add(goal)
        }

        fun addPhaseForGoal(goal: String, phase: String) {
            goalToPhase[goal] = phase
        }

        fun getGoalsForPhase(phase: String): List<String> = 
            phaseToGoals[phase]?.toList() ?: emptyList()

        fun getPhaseForGoal(goal: String): String? = goalToPhase[goal]

        fun getGoalsCompletedByPhase(phase: String): List<String> {
            // Return goals that would be executed up to and including this phase
            return getGoalsForPhase(phase)
        }
    }
}

