import com.google.gson.GsonBuilder
import org.apache.maven.shared.invoker.*
import java.io.File
import kotlin.system.exitProcess

/**
 * Nx Maven Batch Executor - Executes multiple Maven goals in a single session
 * while maintaining proper artifact context and providing detailed per-goal results.
 */
object NxMavenBatchExecutor {

    private val gson = GsonBuilder().setPrettyPrinting().create()

    @JvmStatic
    fun main(args: Array<String>) {
        if (args.size < 3) {
            System.err.println("Usage: java NxMavenBatchExecutor <goals> <workspaceRoot> <projects> [verbose]")
            System.err.println("Example: java NxMavenBatchExecutor \"maven-jar-plugin:jar,maven-install-plugin:install\" \"/workspace\" \".,module1,module2\" true")
            exitProcess(1)
        }

        val goalsList = args[0]
        val workspaceRoot = args[1]
        val projectsList = args[2]
        val verbose = args.size > 3 && args[3].toBoolean()

        try {
            val goals = goalsList.split(",")
            val projects = projectsList.split(",")
            val result = executeBatch(goals, workspaceRoot, projects, verbose)

            // Output JSON result for Nx to parse
            println(gson.toJson(result))

            // Exit with appropriate code
            exitProcess(if (result.isOverallSuccess) 0 else 1)

        } catch (e: Exception) {
            val errorResult = BatchExecutionResult().apply {
                isOverallSuccess = false
                errorMessage = "Batch executor failed: ${e.message}"
            }

            System.err.println(gson.toJson(errorResult))
            exitProcess(1)
        }
    }

    /**
     * Execute multiple Maven goals across multiple projects in a single session
     */
    private fun executeBatch(
        goals: List<String>,
        workspaceRoot: String,
        projects: List<String>,
        verbose: Boolean
    ): BatchExecutionResult {
        val result = BatchExecutionResult()
        val startTime = System.currentTimeMillis()

        if (verbose) {
            System.err.println("ğŸš€ Starting batch execution of ${goals.size} goals across ${projects.size} projects")
        }

        try {
            val invoker = DefaultInvoker()
            val workspaceDir = File(workspaceRoot)

            // Execute each goal across all projects
            for (goal in goals) {
                if (verbose) {
                    System.err.println("ğŸ“¦ Executing goal: $goal")
                }

                for (project in projects) {
                    val projectDir = if (project == ".") workspaceDir else File(workspaceDir, project)

                    if (verbose) {
                        System.err.println("  ğŸ“ In project: ${projectDir.absolutePath}")
                    }

                    val goalResult = executeGoalInProject(invoker, goal, projectDir, verbose)
                    result.goalResults.add(goalResult)

                    if (!goalResult.isSuccess) {
                        result.isOverallSuccess = false
                    }
                }
            }

        } catch (e: Exception) {
            result.isOverallSuccess = false
            result.errorMessage = "Execution failed: ${e.message}"
        }

        val duration = System.currentTimeMillis() - startTime
        result.executionTimeMs = duration

        if (verbose) {
            System.err.println("âœ… Batch execution completed in ${duration}ms")
        }

        return result
    }

    private fun executeGoalInProject(
        invoker: Invoker,
        goal: String,
        projectDir: File,
        verbose: Boolean
    ): GoalExecutionResult {
        val goalResult = GoalExecutionResult().apply {
            this.goal = goal
            this.project = projectDir.absolutePath
        }

        try {
            val request = DefaultInvocationRequest().apply {
                pomFile = File(projectDir, "pom.xml")
                goals = listOf(goal)
                isBatchMode = true
                isOffline = false
            }

            val result = invoker.execute(request)
            goalResult.isSuccess = result.exitCode == 0
            goalResult.exitCode = result.exitCode

            if (result.executionException != null) {
                goalResult.errorMessage = result.executionException.message
            }

        } catch (e: Exception) {
            goalResult.isSuccess = false
            goalResult.errorMessage = "Failed to execute goal: ${e.message}"
        }

        return goalResult
    }

    data class BatchExecutionResult(
        var isOverallSuccess: Boolean = true,
        var executionTimeMs: Long = 0L,
        var errorMessage: String? = null,
        val goalResults: MutableList<GoalExecutionResult> = mutableListOf()
    )

    data class GoalExecutionResult(
        var goal: String = "",
        var project: String = "",
        var isSuccess: Boolean = true,
        var exitCode: Int = 0,
        var errorMessage: String? = null
    )
}

